@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         Stylish: Main window with buttons bigger.
// @author       Roblesolido
// @description	 This changes the main window with buttons bigger.
// ==/UserStyle==*/


/* For Stylish 1.0 */
richlistbox#styles richlistitem image {display:none !important;}
richlistbox#styles richlistitem > vbox > deck > hbox:first-child
{
margin:10px 0 0 16px !important;
}
richlistbox#styles richlistitem > vbox > hbox > vbox:first-child
{
background-image: url("data:image/png;base64,")!important;
background-repeat:no-repeat !important;
background-position:0px 0px !important;
padding-left:16px!important;
}
richlistbox#styles #groupEnabledTrue > richlistitem > vbox > hbox > vbox:first-child
{
background-position:-16px 0px !important;
}

/*end*/
@-moz-document url-prefix(chrome://stylish/content/manage-standalone.xul){
#stylish-manage-standalone
{
padding-bottom: 1em !important;
}

#stylish-manage-standalone,
#stylish-manage-standalone > hbox
{
-moz-box-direction: reverse !important;
}

#stylish-manage-standalone > hbox {
-moz-box-pack:end !important;
padding-left: 5px !important;
border-bottom: 2px groove threedface !important;
background-color: -moz-field !important;
}

#stylish-manage-standalone > hbox > button {
-moz-appearance: none !important;
-moz-box-orient: vertical !important;
-moz-user-focus: ignore !important;
margin: 0 1px !important;
padding: 7px 5px 7px 5px !important;
border: none !important;
background-color: transparent !important;
list-style-image: url(data:image/png;base64,) !important;
}
#stylish-manage-standalone > hbox > spacer {display: none !important;}

#new-style { -moz-image-region: rect(0px, 32px, 32px, 0px) !important; }
#new-style[disabled="true"]{ -moz-image-region: rect(32px, 32px, 64px, 0px) !important; }
#copy-style-info { -moz-image-region: rect(0px, 64px,  32px, 32px) !important; }
#copy-style-info[disabled="true"]{ -moz-image-region: rect(32px, 64px,  64px, 32px) !important; }
#s-delete { -moz-image-region: rect(0px, 96px,  32px, 64px) !important; }
#s-delete[disabled="true"]{ -moz-image-region: rect(32px, 96px,  64px, 64px) !important; }
#update-all {-moz-image-region: rect(0px, 128px, 32px, 96px) !important; }
#update-all[disabled="true"]{ -moz-image-region: rect(32px, 128px, 64px, 96px) !important; }
#s-help { -moz-image-region: rect(0px, 160px, 32px, 128px) !important; }
#s-help[disabled="true"]{-moz-image-region: rect(32px, 160px, 64px, 128px) !important; }

#stylish-manage-standalone > hbox > button:not([disabled="true"]):hover { background-color: #E0E8F6 !important;}
#stylish-manage-standalone > hbox > button:not([disabled="true"]):active { background-color: #C1D2EE !important;}

}
/* --- */
