css:
@-moz-document url-prefix("https://forums.craigslist.org") {


.threadline a { color: black; text-decoration: none; font-weight:normal!important; }

/*where it shows "bos"/"pdx" etc*/
.areaselector { display:inline-block; padding-top:7px; }

/*areaselector making first thing caps*/
a.areaselector + a { text-transform: uppercase;}

/*Handle*/
.hnd { font-weight:normal;}

/*Where the post content is*/
#R { background-color:white!important; }

/*only time FONT is used -- the date*/
font, .hnd { color:#888;}
}
