css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("dict.cc") {
  
  /**
   * Main-Colors used:
   * DC3522, D9CB9E, 374140, 2A2C2B, 2A2C2B
   * Color-theme called "SeaWolf" on color.adobe.com
   * Sorry for the abuse of these wonderful colors :P
   */
   
  div#maincontent {
    color: #CCC;
  }
    
  .hl1, .hl3, h1, h2 {
    color: #D9CB9E !important;;
  }
  
  input[type="text"], select {
    background-color: #2A2C2B !important;
    color: #BBB !important;
  }
  
  abbr, kbd {
    color: #7F9694;
  }
  
  div#langbar {
    background: #1E1E20 !important;
    color: #DC3522;
    border-color: #666;
  }
  
  div#langbar a {
    color: #DDD;
  }
  
  input.inp1 {
    background: #DC3522;
  }
  
  input.inp2 {
    background: #374140;
  }
    
  /* For iframe Login & Signup */
  body {
    background-color: #1E1E20 !important;
    color: #CCC;
  }
  
  body, #maincontent, #maincontent div {
    background-color: #1E1E20;
  }
  
  .noli666, .noli666 > div  {
    color: #D9CB9E !important;
  }
  
  .noli666 a, a {
    color: #CCC !important;
  }

  /* Bescht attribute-id ever! */
  div#maincontent div#entertexthere {
    color: #D9CB9E !important;
  }
  
  /* Main-Page dictionaries table */
  div#maincontent > table > tbody > tr > td {
    background-color: #374140 !important;
  }
  
  /* Main Page Ads/Game/Comments */
  
  div#maincontent > div > div > h3, div#maincontent > div > div > h3 a.hometabi {
    border-color: #DC3522 !important;
  }
  
  div#maincontent > div > div > h3 a.hometaba {
    background-color: #DC3522 !important;;
  }
  
  div#maincontent > div > div > h3 a.hometaba:hover,
  div#maincontent > div > div > h3 a.hometabi:hover {
    background-color: #FF5144;
  }
  
  div#maincontent > div > div > h3 + div {
    background-color: #374140 !important;
    border-color: #DC3522 !important;
  }
  
  div#maincontent > div > div > h3 + div > div:first-child {
    background-color: #2A2C2B !important;
  }
  
    div#maincontent > div > div > h3 + div > div a.gameanswer b {
    color: #DC3522 !important;
  }
  
  div#maincontent > div > div > h3 + div > div a.gameanswer {
    background-color: #2A2C2B;
  }
  
  div#maincontent > div > div > h3 + div > div a.gameanswer:hover {
    background-color: #505452;
  }
    
  /* Translation-Detail page */
  #maincontent > div:not(.orangebar):not(.subnav),
  #maincontent > div:not(.orangebar):not(.subnav) > .noline {
    background-color: #2A2C2B !important;
  }
  
  /* Detail header box */
  div#maincontent > div > div.noline > table.nopad > tbody > tr > td > div.noline {
    background-color: #374140 !important;
  }
  
  /* Detail header category text (e.g. "NOUN" ...) */
  div#maincontent > div > div.noline > table.nopad > tbody > tr > td > div.noline > table.nopad > tbody > tr > td > b {
    color: #D9CB9E !important;
  }
  
  /* Top Contributors */
  
  div#maincontent > div > table.subnav {
    background: #2A2C2B !important;
  }
  
  /* Dont color in the margins between the entries, which are tablerows themselfes *grrrrr* */
  div#maincontent > div > table.nopad > tbody > tr > td:not(:only-child) {
    background-color: #374140 !important;
  }
  
  div#maincontent > form > table > tbody > tr > td > div,
  div#maincontent > form > table > tbody > tr > td > div > b > i > a,
  div#maincontent > form > table > tbody > tr > td > h1.mainheadline {
    color: #DC3522 !important;
  }
  
  td.td6 {
    color: #D9CB9E;
    border-color: #DC3522;
  }
  
  div.orangebar {
    background: #374140 !important;
  }
  
  div.orangebar a {
    color: #DDD !important;
  }
  
  /* Separators */
  div.orangebar > img, 
  div.orangebar > div > img {
    position: relative;
    top: -1px;
    width: 0;
    border-left: 1px solid #D9CB9E;
    margin: 0 5px;
  }
  
  div.subnav {
    background: #374140 !important;
    color: #D9CB9E !important;
  }
  
  div.subnav a {
    color: #DDD !important;
  }
  
  td.bluebar {
    background: #374140 !important;
  }
  
  div#maincontent > table td.td2 {
    background: #444 !important;
  }
  
  div#maincontent > table td.noline {
    background: #555 !important;
    color: #DDD;
  }
  
  div#maincontent > table td.noline > table td > b {
    color: #DDD !important;
  }
  
  div#maincontent > table td.noline a {
    color: #DDD;
  }
  
  div#maincontent > table .td7, 
  div#maincontent > table .td7nl, 
  div#maincontent > table .td7cml, 
  div#maincontent > table .td7cmr {
    background: #2A2C2B !important;
    border-bottom-color: #1E1E20;
  }
  
  div#maincontent > table .td7 a, 
  div#maincontent > table .td7nl a, 
  div#maincontent > table .td7cml a, 
  div#maincontent > table .td7cmr a {
    color: #BBB;
  }
  
  div#maincontent > table .td7cml img, 
  div#maincontent > table .td7cmr img {
    opacity: 0.5;
  }
  
  /* Context-Menu for translations */
  
  div#overDiv > div, div#overDiv > div > div.nolibg {
    background-color: #2A2C2B !important;
    border: solid 1px #DC3522 !important;
  }
  
  div#overDiv > div > div.nolibg > hr {
    color: #DC3522;
  }
  
  /* No translations found page */
  div#maincontent > div:nth-child(7), div#maincontent > div:nth-child(7) table {
    background: #374140 !important;
  }
  
  dfn {
    background: #374140;
  }
  
  div#maincontentpart2 {
    background: #1E1E20;
  }
  
  div#maincontentpart2 td,
  div#maincontentpart2 td a {
    background: #2A2C2B;
    color: #CCC;
  }
  
  div#maincontentpart2 div.noline {
    background: #374140 !important;
  }
  
  div#maincontentpart2 div {
    background: #2A2C2B !important;
    color: #BBB;
  }

  div#maincontentpart2 div.noline a {
    color: #CCC;
  }
  
  
  /* News & Stuff */

  
  div.news {
    background-color: #374140 !important;
    border-left: 4px solid #DC3522;
  }
  
  div.news > em {
    color: #DC3522;
  }
  
}
