css:
@-moz-document domain("smogon.com") {
  html, body, #header {
    background: #FFF;
    color: #000;
  }
  
  html {
    background: #FFF;
  }
  
  #content .pageContent {
    background-color: #EFEFEF;
  }
  
  #logo .logoText {
    color: #000;
  }
  
  /* Top Navigation Bar */
  
  .navTabs {
    font-size: 11px;
    background-color: #F0CD41;
    padding: 0px 25px;
    border-bottom: 1px solid #5B4F20;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    height: 25px;
  }
  
  .navTabs .navLink {
    color: #5B4F20 !important;
  }
  
   .navTabs .navLink:hover, navLink:hover {
     background-color: #E2BD27 !important;
   }
  
  .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: 700;
    background-color: #E2BD27;
    padding-top: 2px;
    margin-top: -2px;
    border-width: 1px;
    border-style: solid solid none;
    border-color: #5B4F20;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
  }
  
  .navTabs .navTab.selected .tabLinks {
    background-color: #E2BD27;
  }
  
  .navTabs .navTab.selected .tabLinks a {
    color: #5B4F20 !important;
  }
  
  .navTabs .navTab.selected .tabLinks a:focus, .navTabs .navTab.selected .tabLinks a:hover {
    background: #BC9D20;
  }
  
  /* Top Navigation Bar ~end */
  
  .nodeList .categoryStrip {
    color: #6D3F03;
    background-color: #E2BD27;
    border: 1px solid #5B4F20;
  }
  
  .nodeList .categoryStrip .nodeTitle a {
    color: #5B4F20;
  } 
  
  #header_island, #about, #search_help {
   background: url("http://i.imgur.com/Woe118C.png?2");
  }
  
  #header_island {
    border: 1px solid #5B4F20 !important;
    border-radius: 12px; 
  }
  
  #header_island a {
    color: #5B4F20;
  }
  
  input[name="q"] {
    background: #FFFCD5;
    border: 1px solid #5B4F20;
    color: #000;
  }
  
  input[value="Search"] {
    border: 1px solid #5B4F20;
     background-color: #E2BD27;
  }
  
  a:link, a:visited {
    color: #3A5978;
  }
  
  a:link:hover, a:visited:hover {
    background: transparent;
    color: #647F94;
  }
  
  #QuickSearch, .formPopup .primaryControls {
    background: #EFEFEF !important;
  }
  
  .footerLegal .pageContent a, concealed {
    color: #454545 !important;
  } 
  
  .PanelScroller .panel {
    background: #C4CDD6;
  }
  
  .node .nodeLastPost, .secondaryContent {
    background: #C4CDD6 !important;
    border: 1px solid #889BAD !important;
  }
  
  .pairsJustified dt, .sidebar .avatarList .userTitle {
    color: #666666;
  }
  
  .sidebar .section .secondaryContent h3, .sidebar .section .secondaryContent h3 a {
    color: #3A5978;
    text-shadow: 1px 1px #889BAD;
  }
  
  .sidebar .section .secondaryContent .footnote {
    color: #3A5978;
  }
  
  .PanelScroller .scrollContainer {
     border: 1px solid #889BAD;
  }
  
  .sectionMain {
    border: 1px solid #889BAD;
  }
  
  .footer .pageContent {
    background: url("http://i.imgur.com/mT1nlR5.png?1");
    border: 1px solid #5B4F20;
    color: #5B4F20;
  }
  
  .footer a, .footer a:visited, .footer a:hover, .footer a:visited:hover {
    color: #5B4F20;
  }
  
  .breadcrumb {
    border: 1px solid #889BAD;
    background: #C4CDD6;
  }
  
  .breadcrumb .crust a.crumb {
    background: #C4CDD6;
  }
  
  .breadcrumb .crust .arrow span {
    border-width: 12px 1px 12px 12px;
    border-style: solid none solid solid;
    border-color: transparent #000 transparent #C4CDD6;
    -moz-border-right-colors: #889BAD;
    display: block;
    position: absolute;
    left: -13px;
    top: -12px;
    z-index: 51;
    white-space: nowrap;
    overflow: hidden;
    text-indent: 9999px;
    width: 0px;
    height: 0px;
  }
  
  .tabs {
    background: #EFEFEF;
    border-bottom: 1px solid #889BAD;
  }
  
  .tabs li a, .tabs.noLinks li {
    border-color: #889BAD;
    background-color: #C4CDD6;
  }
  
  .tabs li.active a, .tabs.noLinks li.active {
    background-color: #EFEFEF;
    padding-bottom: 1px;
    border-bottom: 1px none #000;
  }
  
  .Menu .sectionFooter {
    background-color: rgba(106, 129, 152, 0.9);
    border: none;
  }
  
  .Menu {
    font-size: 11px;
    border-width: 5px 1px 1px;
    border-style: solid;
    border-color: #889BAD;
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.5);
    min-width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
  }
  
  .Popup.PopupContainerControl.PopupOpen {
    background: #889BAD;
  }
  
  .Menu .secondaryContent {
    background-color: #EFEFEF;
  }
  
  .statusEditorCounter {
    color: #D0FFD8;
    text-shadow: 0px 0px 1px #17AB2F;
  }
  
  .button.primary {
    background: #C4CDD6;
    border: 1px solid #889BAD;
  }
  
  .button.primary:hover {
    background: #889BAD;
    border: 1px solid #3A5978;
  }
  
  .subHeading {
    background: url("http://i.imgur.com/mT1nlR5.png?1");
    border: 1px solid #5B4F20;
    color: #5B4F20;
    border-right: none;
  }
  
  .withSearch .navTabs .navTab.selected .blockLinksList {
    background: transparent !important;
    border: none !important;
  } 
  
  .discussionList .sectionHeaders {
    background: url("http://i.imgur.com/mT1nlR5.png?1");
    border: 1px solid #5B4F20;
    color: #5B4F20;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
  }
  
  #DiscussionListOptionsHandle a {
    color: #3A5978;
  }
  
  .discussionListItem .posterAvatar {
    border-left: 1px solid #889BAD;
    border-right: 1px solid #889BAD;
    background: #C4CDD6;
  }
  
  .discussionListItem .stats {
    border-left: 1px solid #889BAD;
    border-right: 1px solid #889BAD;
    background: #C4CDD6;
  }
  
  .pairsJustified dt {
    color: #5D768F;
  }
  
  .sectionFooter {
    background: #C4CDD6;
    border: 1px solid #889BAD;
    color: #3A5978;
    border-top: none;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
  }
  
  .discussionListItem {
    background-color: #FCFCFF;
    border-bottom: 1px solid #889BAD;
    border-right: 1px solid #889BAD;
  }
  
  .messageUserBlock .arrow span {
    position: absolute;
    top: -10px;
    left: -11px;
    display: block;
    width: 0px;
    height: 0px;
    line-height: 0;
    border-width: 10px medium 10px 10px;
    border-style: solid none solid solid;
    border-color: transparent -moz-use-text-color transparent #C4CDD6;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    border-image: none;
    -moz-border-left-colors: #C4CDD6;
  }
  
  .messageUserBlock div.avatarHolder, .messageUserBlock .extraUserInfo {
    background: #C4CDD6;
    border: none;
  }
  
  .messageUserBlock {
    background: #9098B2;
  }
  
  .messageUserBlock a.username {
    color: #FFF;
    text-shadow: 1px 1px #848BA2;
  }
  
  .messageList .message {
    border-top: 1px solid #6A8198;
  }
  
  .conversation_view .messageList {
    border-color: #6A8198 #6A8198 -moz-use-text-color -moz-use-text-color;
  }
  
  .bbCodeQuote .attribution {
    color: #141414;
    background: url("http://i.imgur.com/mT1nlR5.png?1") !important;
    border-bottom: 1px solid #5B4F20;
  }
  
  .bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-style: italic;
    font-size: 9pt;
    background-color: #FFF4E5;
    background-repeat: repeat-x;
    background-position: center top;
    padding: 10px;
    border-radius: 4px;
  }
  
  .bbCodeQuote {
    border-color: #5B4F20;
  }
  
  .redactor_box {
    border-color: #6A8198;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
  }

  .redactor_toolbar {
    border-color: #6A8198;
    background: #C4CDD6;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
  }

  .redactor_toolbar li.redactor_btn_group ul {
    background: #B9C0C7;
    box-shadow: none;
    border: 1px solid #9098B2;
  }
  
  textarea.textCtrl, .textCtrl {
    border: 1px solid #3A5978;
  }
  
  textarea.textCtrl:focus, .textCtrl:focus {
    background: #C4CDD6;
    border: 1px solid #3A5978;
  }
  
  .xenForm fieldset {
    border-top: 1px solid #889BAD;
  }
  
  .primaryContent {
    border-bottom: 1px solid #889BAD;
  }
  
  .xenForm .sectionHeader {
    color: #3A5978;
  }
  
  .formPopup {
    background-color: #EFEFEF;
  }
  
  .formPopup .controlsWrapper {
    background: #C4CDD6; 
  }
  
  .PageNav a, .itemPageNav a {
    color: #5B4F20 !important;
    text-decoration: none;
    background-color: #ECD370;
    border-width: 1px;
    border-style: solid;
    border-color: #5B4F20 !important;
    border-radius: 3px;
    text-align: center;
    width: 19px;
  }
  
  .PageNav a:hover, .itemPageNav a:hover {
    color: #5B4F20;
    background-color: #E2BD27;
    border-color: #5B4F20;
  }
  
  .PageNav a.currentPage {
    color: #3A5978;
    background-color: #C4CDD6 !important;
    border-color: #3A5978;
    position: relative;
  }
  
  .discussionList .sectionHeaders a:hover {
    background: #E9CD5D !important;
  }
  
  @-moz-document url-prefix("http://www.smogon.com/forums/account/signature"), url-prefix("http://www.smogon.com/forums/account/") {
  
    .heading, .secondaryContent {
      background: #889BAD;
      border: 1px solid #3A5978;
      border-right: none;
      color: #F0F7FC !important;
    }

    .secondaryContent {
      border-top: none;
    }

    .secondaryContent:hover {
      color: #3A5978 !important;
      border-right: none;
      border-left: none;
    }

    .primaryContent {
      border-bottom: 1px solid #6A8198;
    }

    .PanelScroller .navControls a.current {
      background-color: #9098B2;
      border-color: #9098B2;
    }

    .PanelScroller .navControls a {
      background: #C4CDD6;
      border-color: #9098B2;
    }

    .redactor_box {
      border-color: #6A8198;
    }

    .redactor_toolbar {
      border-color: #6A8198;
      background: #C4CDD6;
    }

    .redactor_toolbar li.redactor_btn_group ul {
      background: #B9C0C7;
      box-shadow: none;
      border: 1px solid #9098B2;
    }
  
  }
    
  @-moz-document url-prefix("http://www.smogon.com/dex/") {
    html {
      background: #889BAD;
    }
    
    body {
      text-align: center;
      font-family: "Trebuchet MS";
      background: #C4CDD6;
      border-radius: 5px;
    }
    
    p, h3, h4, ul {
      text-align: center;
      font-family: "Trebuchet MS";
    }
    
    ul {
      border-radius: 5px;
      border: 1px solid #889BAD;
      width: 40%;
      margin-right: auto;
      margin-left: auto;
    }
    
    ul:hover {
      background: #B2B8BF;
    }
    
    h3, h4 {
      text-decoration: underline;
    }
  }  
}
