css:
@-moz-document domain("nrk.no") {
/*
################################
# Moerk - Dark theme 
# Version 0.8
# for NRK.no
# ------------------------------
#
# Made by
# Christer Fjoelstad
#
# fjolstad.no
# twitter - @cfjoelstad
# christer@fjolstad.no
################################
*/

body {
background-color:#2F2F2F;
}

.nrk-masthead {
background-color:#2f2f2f;
}
.widget figcaption {
  color: #9B9B9B;
}
.text-body a, .text-body a:link, .text-body a:visited {
  border-bottom: 1px solid #4C4C4C;
}
body>header .widget-title {
  color: #9f9f9f;
}
.drfront h3 a {
color:#FFF !important;
}

.stream.lean .stream-header {
  background: #4A4A4A;
  padding: 10px 15px;
}
.p3-front-player-plugin .front-p3-player {
  background-color: #65c1b8;
}
.newsroom-embedded > .col > .stream.widget {
  padding-top: 0px; 
}
.stream-lean-exit span {
  color: #FFFFFF;
}
.stream-lean-footer {
  color: #9f9f9f;
  background-color: #4a4a4a;
  border:0px;
}
.drfront .df-container-skin-default,.drfront .df-container-skin-Usynlig-container {
background-color: #2F2F2F;
border:0px;
}

.df-container-skin-default h3 a {
color:#fff!important;
}
.group.floor {
  background: #474747;
  margin-bottom: 0px !important;
}
.text-body {
color:#B8B8B8;
}

.skin-fill {
fill:#fff;
}

figure.audio .video-title {
color:#8E8E8E!important;
}

.menu-main .menu-title a {
color:#fff!important;
}

.plug-preamble {
color:#b8b8b8;
}

.drfront .img-brd-1 .df-img-container-inner {
border:none!important;
}

.drfront .bg-brd {
border-top:none;
padding-top:0px;
}
.drfront{
color:#B8B8B8;
}
.group.floor-top {
  background: #353535;
}
.title,.article-lead,.text-body h2,.menu-main .menu-item a,.widget-title {
color:#fff;
}
a.skin-text-hover {
  color: #fff;
}
.author .role {
  color: #D3D3D3;
}
/* version 0.5*/
.bulletin-text > .bulletin-title {
  color: #fff !important;
}
.bulletin-text > .bulletin-time > a, .bulletin-text > .bulletin-time > a:link, .bulletin-text > .bulletin-time > a:visited {
  color: #9D9D9D;
}
.bulletin-text > p, .bulletin-text > p > a, .bulletin-text > .bulletin-title {
  color: #B8B8B8;
}
.bulletin-attachment.plug .title, .bulletin-attachment.video .title {
  color: #E8E8E8;
}
.quote-text p {
  color: #FFF;
}
figure.audio p.video-description{
  color: #797979 !important;
}
figure.audio .video-title {
  color: #fff!important;
}
.compilation-authors-list {
  margin-top: 20px;
}
.compilation-authors-list .author .fn {
  color: #FFFFFF;
}
.drfront .bg-backdrop {
  color: #f3f3f3;
  background-color: rgb(21, 21, 21);
}
.drfront .df-container-skin-Svart-container {
  background-color: rgb(21, 21, 21);
}
/* version 0.6*/
#front-9 .ytring-quote blockquote small, #front-16 .ytring-quote blockquote small {
  color: #FFF;
}
.tipbox {
  border: 1px solid #626262;
  background: #474747;
}
/* 0.6.1 */
.fact {
  background-color: #474747;
  border: 1px solid #626262;
}
/* 0.6.2 */
.drfront .df-container-skin-default-grey-bg {
    border-bottom: 0px;
    background: rgba(255, 255, 255, 0.15);
}
.recipe .text-body h2 {
    color: #9B9B9B;
}
.article-preamble {
    color: #9B9B9B;
}
.recipe-list {
    color: #9B9B9B;
}
.recipe-meta-title {
    color: #9B9B9B;
}
.article-feature-body-wrapper > p, .article-feature-body-wrapper > ol, .article-feature-body-wrapper > ul {
    color: #9B9B9B;
}
.article-feature > header:after, .article-feature > header {
    background-color: #2F2F2F;
}
.article-feature > .col.fc {
    background: rgb(47, 47, 47);
}
.article-feature-body-wrapper > h2 {
    color: #9B9B9B;
}
.article-feature-body-wrapper > p > strong {
    color: #fff;
}
.article-feature-group > .col, .article-feature-universe > .col {
    background: #2F2F2F;
    border-top: 1px #ccc solid;
}
/* 0.6.3 */
.menu-main__title a {
    color: #FFFFFF;
}
.menu-main a {
    color: rgb(159, 159, 159);
}
/* 0.6.3 */
.drfront .df-container-skin-FotballEM_2016 .bg-container-heading {
    background: #0267B7;
    color: #2f2f2f;
}
.drfront .df-container-skin-FotballEM_2016 {
    background: #2f2f2f;
}

/* 0.7 - new NRK frontpage */
.nrk-color-base {
    color: #f3f3f3;
    background-color: #2f2f2f;
}
.kur-house {
    background-color: #2f2f2f;
}
.kur-floor--apartment.kur-floor--mix {
    background: #4c4c4c;
    color: #ffffff;
}
.newsfeed {
    background-color: #171717 !important;
}
.newsfeed-control {
    background-color: #3c3c3c !important;
}
.newsfeed__title {
    border-right: 1px solid #5d5d5d;
}
.kur-floor--apartment .kur-room__content {
    background: #4c4c4c;
}
.kur-floor--apartment .nrk-color-base {
    background: #4c4c4c;
}
.kur-room--xl.kur-room--is-wide .kur-room__content {
    box-shadow: 0px 8px 25px 5px rgba(0,0,0,.25);
}
.kur-floor--apartment .kur-floor__title {
    background: #4c4c4c;
}
/* 0.7.1 */
.kur-room--light {
    color: #fff;
    background-color: #2f2f2f;
}
/* 0.8 */
.nrk-masthead__promo {
    color: #fff;
}
.article-dateline {
    color: #f3f3f3;
}
.plug-feature .plug-teaser {
    background: #515151;
}
.plug-feature .plug-metadata-footer {
    color: #fff;
}
.article-feature__footer .nrkno-share .nrk-button {
    color: #fff;
}
.valg-valgomat-search {
    color: #fff;
}
.valg-valgomat-besvarelse {
    background-color: #7b7b7b;
}
.valg-valgomat-besvarelse label {
    background: #7b7b7b;
}
.compilation-title {
    color: #f3f3f3;
}
.compilation-summary {
    background-color: #4b4b4b;
}
.compilation-summary-title {
    color: #26292a;
}
.kur-floor--apartment .kur-floor__title + div .kur-room--light {
    border: none;
}
.kur-floor--apartment .kur-room {
    border: none
}
}
