/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

::-webkit-scrollbar {
width: 7px;
height: 7px;
background-color: transparent;
scrollbar-arrow-color: #dddddd;
}

::-webkit-scrollbar-thumb {
min-height: 5px;
min-width: 5px;
-webkit-border-radius: 20px; 
border: 1px solid #aaaaaa;
background-color: #dddddd;
}

::-webkit-scrollbar-thumb:hover {
min-height: 5px;
min-width: 5px;
-webkit-border-radius: 20px;
border: 1px solid #888888;
background-color: #bbbbbb;
}

::-webkit-scrollbar-thumb:active {
-webkit-border-radius: 20px;
border: 1px solid #888;
background-color: #bbbbbb;
}

/* UP ARROW */
::-webkit-scrollbar-button:vertical:decrement {
background-image: url(http://i62.tinypic.com/hv8ihf.jpg);
height: 6px !important;
}

/* DOWN ARROW */
::-webkit-scrollbar-button:vertical:increment {
background-image: url(http://i62.tinypic.com/11uyet2.jpg);
height: 6px !important;
}

/* LEFT ARROW */
::-webkit-scrollbar-button:horizontal:decrement {
background-image: url(http://i60.tinypic.com/fc60lh.jpg);
width: 6px !important;
}

/* RIGHT ARROW */
::-webkit-scrollbar-button:horizontal:increment {
background-image: url(http://i57.tinypic.com/20z9u1h.jpg);
width: 6px !important;
}
