#floatingtoolbar {
    background-color: rgba(0,0,0,.9) !important;
    border: none !important;
    box-shadow: none !important;
    width: 50px !important;
}

#floatingtoolbar div.submenu {
    left: 50px !important;
    padding: 0 !important;
    top: 0 !important;
}

#floatingtoolbar div.submenu .pictos, #floatingtoolbar div.submenu .pictosthree, #floatingtoolbar div.submenu .pictostwo {
    left: 15px !important;
    position: absolute !important;
    text-align: center !important;
    width: 20px !important;
}

#floatingtoolbar div.submenu ul {
    border: 0 !important;
    border-radius: 0 !important;
    box-shadow: 0 !important;
    padding: 0 !important;
    width: 200px !important;
}

#floatingtoolbar div.submenu ul li {
    color: #444 !important;
    padding-left: 45px !important;
    padding-right: 20px !important;
}

#floatingtoolbar div.submenu ul li span {
    padding: 0 !important;
}

#floatingtoolbar div.submenu ul li:hover {
    background-color: #99d8ed !important;
}

#floatingtoolbar li {
    border: 0 !important;
    color: #ccc !important;
    height: 50px !important;
    line-height: 50px !important;
    padding: 0 !important;
}

#floatingtoolbar li.activebutton, #floatingtoolbar li:hover {
    background-color: white !important;
    color: #111 !important;
}

#floatingtoolbar li:last-child {
    border-bottom: none !important;
}

#floatingtoolbar ul {
    box-shadow: none !important;
}

#page-toolbar {
    background-color: #4d4b48 !important; 
    border-radius: 0 !important;
    height: 160px !important;
    left: 0 !important;
    right: 0 !important;
    width: initial !important;
}

#page-toolbar .availablepage {
    box-sizing: border-box !important;
    height: 160px !important;
    margin: 0 !important;
    padding: 10px !important;
    vertical-align: top !important;
    width: 160px !important;
}

#page-toolbar .availablepage .pagethumb {
    margin-top: 5px !important;
}

#page-toolbar .availablepage span {
    bottom: 30px !important;
    left: 0 !important;
    right: 0 !important;
    text-shadow: none !important;
    color: #eee !important;
    font-size: 12px
 !important;
}

#page-toolbar .container {
    transition: opacity .4s ease-in-out !important;
}

 #page-toolbar .handle {
    background: #111 !important;
    border-radius: 0 0 100% 100% !important;
    bottom: -29px !important;
    color: #eee !important;
    height: 29px !important;
    opacity: 1 !important;
    width: 100px !important;
    right: 50% !important;
    margin-left: -50px !important;
    padding: 0 !important;
}

#page-toolbar .handle .pictos {
    display: none !important;
}

#page-toolbar .handle:after {
    content: 'PAGES' !important;
    font-size: 11px !important;
    letter-spacing: 2px !important;
    height: 100% !important;
    left: 0 !important;
    position: absolute !important;
    top: 0 !important;
    width: 100% !important;
}
