.actions_menu {
    width: 120px !important;
    z-index: 110 !important;
    /* above sidebar */
}

.actions_menu > ul {
    width: initial !important;
    padding: 0 !important;
    border: 0 !important;
}

.actions_menu ul li {
    padding: 0 10px !important;
    line-height: 30px !important;
    min-width: 90px !important;
    border-color: #ddd !important;
    white-space: nowrap !important;
}

.actions_menu ul li:lastchild {
    border-bottom: 0 !important;
}

.actions_menu ul li ul.submenu {
    left: 90px !important;
    border: 0 !important;
    width: initial !important;
    opacity: 1 !important;
    box-shadow: 0 1px 15px rgba(0,0,0,.1) !important;
}

#measure img {
    -webkit-filter: brightness(2.3) !important;
}

.nav-tabs li a {
    outline: none !important;
}

#sidebarcontrol {
    opacity: 1 !important;
    padding: 0 !important;
    -webkit-transform: translateX(0) rotate(-90deg) !important;
    -moz-transform: translateX(0) rotate(-90deg) !important;
    width: 100px !important;
    text-align: center !important;
    border: none !important;
    font-size: 12px !important;
    color: #eee !important;
    background-color: #111 !important;
    height: 30px !important;
    line-height: 30px !important;
    text-transform: uppercase !important;
    letter-spacing: 2px !important;
    -webkit-transform-origin: 100% 100% !important;
    -moz-transform-origin: 100% 100% !important;
    border-radius: 100% 100% 0 0 !important;
    top: 50% !important;
    margin-top: -75px !important;
}

#sidebarcontrol .fonticon-menu {
    display: none !important;
}

#sidebarcontrol:after {
    content: 'Menu' !important;
}

/* Character sheet stuff */

.sheet-rolltemplate-5eDefault * {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  
  max-width: 950px;
}
.sheet-overall-wrapper {
  min-width: 832px;
  padding: 12px;
  padding-top: 40px;
}
.charsheet {
  padding-top: 0 !important;
  background: url('http://i.imgur.com/32RluLn.jpg');
}
.charsheet textarea {
    padding: 10px;
}
.charsheet .sheet-dnd-image-wrapper img{
display:none;
}
.charsheet .sheet-last-updated{
display:none;}
.sheet-character-name{
  font-family: 'Book Antiqua', Palatino, 'Palatino Linotype', 'Palatino LT STD', Georgia, serif;
  font-size: 0.9em;
  font-weight: bold;
   color:rgb(243, 230, 214);
  padding: 15px 10px 15px 10px;
    }
.charsheet .sheet-character-name input[type=text] {
  font-family: 'Book Antiqua', Palatino, 'Palatino Linotype', 'Palatino LT STD', Georgia, serif;
  font-size: 1.3em;
	padding:10px;
  font-weight: bold;
  color: #821F1F;

}
.charsheet label.sheet-slide-toggle-pc .sheet-slide-cover,.charsheet label.sheet-slide-toggle-custom-skills .sheet-slide-cover,.charsheet label.sheet-slide-toggle-spell-slots-points .sheet-slide-cover{
	box-shadow:none;
}
.charsheet input.sheet-toggle-language, .charsheet span.sheet-toggle-language{
display:none;}
.charsheet .sheet-wrap-box, .charsheet .sheet-resistances-immunities textarea, .charsheet .sheet-ability-score-wrapper .sheet-ability-score{
    background-color: #F5F2EE;
}
.charsheet .sheet-heart::after{
    color: #FDE8E7;
}
.charsheet input[class^=sheet-toggle-page-], .charsheet span[class^=sheet-toggle-page-]{
margin-bottom:10px;
}
.charsheet input.sheet-pseudo-disabled, .charsheet input[disabled]{
    background-color: #EAE4DD;
}
.charsheet input[disabled].sheet-passive-skill{
    background-color: #D0C7BD;
}
.charsheet .sheet-spell-slots-points .sheet-shaped-row:nth-child(even){
    background-color: #DCEAE1;
}
.charsheet h4{
    background-color: #8A7372;
    padding: 5px;
    font-size: 1.1em;
    color: #E8E2E2;
    font-family: Georgia,serif;
}
body > div:nth-child(141) > div.dialog.characterdialog.ui-dialog-content.ui-widget-content > div > div > form > div > div > div.sheet-section-inventory.sheet-pc > div:nth-child(2) > div.sheet-col-25.sheet-center.sheet-pad-r-sm > input,body > div:nth-child(142) > div.dialog.characterdialog.ui-dialog-content.ui-widget-content > div > div > form > div > div > div.sheet-section-inventory.sheet-pc > div:nth-child(2) > div.sheet-col-25.sheet-center.sheet-pad-r-sm > input{
    font-size: 1.8em;
    color: #BD6A12;
    font-family: Georgia,serif;
}
.charsheet .sheet-wrap-box{
        border: 1px solid #AFA59A;
}
.charsheet .sheet-wrap-box-title,.charsheet .sheet-header-info .sheet-wrap-box-label, .charsheet .sheet-sense input, .charsheet .sheet-speed input{
    color: #225B71;
}
.charsheet input[class^=sheet-toggle-page-], .charsheet span[class^=sheet-toggle-page-]{
        margin-right: 1px;
}
rolltemplate-5eDefault .sheet-rt-header.sheet-spell {
    background-color: #AD1212;
}
.sheet-rolltemplate-5eDefault .sheet-rt-title {
    font-family: Georgia,serif;
    font-size: 1.7em;
}
.sheet-rolltemplate-5eDefault .sheet-rt-subheader{
    color: #CECDB8;
}
.textchatcontainer .message.you{
background-color: #E6E4DE;
}

