css:
@-moz-document domain("userstyles.org") {
  body {
    background: #151515;
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
  }
  
  #left-sidebar > *, #main-header {
    border: 1px solid #D1EEEE;
    color: #D1EEEE;
    background: rgba(0, 0, 0, 0.2);
    box-shadow: 0px 0px 5px #D1EEEE;
  }
  
  #main-header > h1 {
    text-shadow: 0px 0px 5px #000;
    color: #A8D0D0 !important;
  }
  
  a, a:visited {
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
  }
  
  a:visited:hover, a:hover {
    color: #A8D0D0 !important;
  }
  
  #search-terms {
    border: 1px solid #D1EEEE;
    border-radius: 4px;
    background: rgba(0, 0, 0, 0.2);
    color: #EEEEEE;
    box-shadow: 0px 0px 1px #D1EEEE;
  }
  
  #search-terms:focus {
    box-shadow: none;
  }
  
  #search-submit {
    border: 1px solid #D1EEEE;
    border-radius: 4px;
    background: rgba(0, 0, 0, 0.2);
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
    box-shadow: 0px 0px 1px #D1EEEE;
  }
  
  #search-submit:hover {
    border: 1px solid #A8D0D0;
    color: #A8D0D0;
    box-shadow: none;
  }
  
  #main-article dl, #main-article ul, #main-article p, #more-screenshots span, #more-screenshots, form-controls, table {
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
  }
  
  .author-styles, #main-article h2, .style-brief, .ad + .pagination, #style-info, #additional-info-text {
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #D1EEEE;
    border-radius: 4px;
    padding: 5px 0px 5px 8px;
    background: rgba(0, 0, 0, 0.2);
    box-shadow: 0px 0px 5px #D1EEEE;
  }
  
  #more-screenshots span {
    margin-left: 5px;
  }
  
  .NavButton {
    color: #D1EEEE;
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #D1EEEE;
    border-radius: 4px;
    background: rgba(0, 0, 0, 0.2);
    box-shadow: 0px 0px 5px #D1EEEE;
  }
  
  .NavButton:hover {
    box-shadow: none;
  }
  
  dl.About dt {
    color: #EEEEEE;
  }
  
  .style-brief-stats span, .style-brief header, #show-button, #discussions-area h3, a[rel="nofollow"], .form-controls label {
    color: #A8D0D0 !important;
    text-shadow: 0px 0px 5px #000;
  }
  
  #screenshots {
    border: none;
  }
  
  #show-button:hover, a[rel="nofollow"]:hover {
    color: #8CB2B2 !important;
  }
  
  pre {
    border: 1px dashed #8CB2B2;
    background-color: #151515;
    margin: 10px 10px 10px 3px;
  }
  
  input[type="button"], input[type="submit"] {
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #D1EEEE;
    color: #D1EEEE;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
    box-shadow: 0px 0px 2px #D1EEEE;
  }
  
  input[type="button"]:hover, input[type="submit"]:hover {
    box-shadow: none;
  }
  
  #style_short_description, #style_long_description, #style_additional_info, #css, #example-url, input[name="new_screenshot_description_1"], input[name="new_screenshot_description_2"], input[name="new_screenshot_description_3"], input[name="new_screenshot_description_4"], input[name="new_screenshot_description_5"], #style_pledgie_id, .CodeMirror,  input.InputBox, .Info, #login-existing, #password-existing, #login-new, #email-new, #password-new, #password-new-confirm, #obsoletion-message, #obsoleting-style-id {
    color: #EEEEEE;
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #95BCBC;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
    box-shadow: 0px 0px 5px #D1EEEE;
    padding: 3px;
  }
  
  input[name="new_screenshot_description_1"]:focus, input[name="new_screenshot_description_2"]:focus, input[name="new_screenshot_description_3"]:focus, input[name="new_screenshot_description_4"]:focus, input[name="new_screenshot_description_5"]:focus, input.SmallInput:focus, input.InputBox:focus, textarea:focus, #login-existing:focus, #password-existing:focus, #login-new:focus, #email-new:focus, #password-new:focus, #password-new-confirm:focus, #obsoletion-message:focus, #obsoleting-style-id:focus {
    box-shadow: none;
  }
  
  .linking-note {
    color: #D1EEEE;
  }
  
  .lb-outerContainer {
    background: rgba(0, 0, 0, 0.2);
  }
  
  .CodeMirror-gutter {
    background: #151515;
    color: #EEEEEE !important;
    text-shadow: 0px 0px 5px #000;
  }
  
  .CodeMirror *::-moz-selection {
    background: none repeat scroll 0% 0% #151515 !important;
  }
  
  .screenshot-thumbnail img {
    border: 1px solid #A8D0D0;
    border-radius: 4px;
  }
  
  .btn-password, .btn-password:hover {
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #95BCBC;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
    box-shadow: 0px 0px 2px #D1EEEE;
    color: #D1EEEE;
  }
  
  .btn-password:focus {
    text-shadow: 0px 0px 5px #000;
    border: 1px solid #95BCBC;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 4px;
    box-shadow: none;
    color: #D1EEEE;
  }
  
  .install {
    background-color: #151515;
    background-image: linear-gradient(#151515, #252525);
    border: 1px solid #95BCBC;
    color: #D1EEEE;
    box-shadow: 0px 0px 3px #D1EEEE;
  }
  
  .install:hover {
    background: #151515;
    box-shadow: 0px 0px 1px #D1EEEE;
  }
  
  .install:active {
    background: #252525;
    box-shadow: none;
  }
  
  .install-info, .install-info:hover {
    background: #151515;
    border: 1px solid #95BCBC;
    color: #D1EEEE;
  }
  
  .notice {
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid #95BCBC;
    color: #95BCBC !important;
    border-radius: 4px;
    box-shadow: 0px 0px 3px #D1EEEE;
    font-style: italic;
    text-align: center;
  }
  
  @-moz-document domain("forum.userstyles.org") {
    h1, #Head a, .DataList .Title, h2 {
      color: #A8D0D0 !important;
      text-shadow: 0px 0px 5px #000;
    }
    
    #Head, .Button, .DataList .Item, .Pager, .FormWrapper {
      color: #A8D0D0;
      text-shadow: 0px 0px 5px #000;
      border: 1px solid #A8D0D0;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      margin: 5px 5px 0px 5px;
      box-shadow: 0px 0px 5px #D1EEEE;
    }
    
    #Head .SiteSearch .InputBox, .CommentFormWrap {
      color: #EEEEEE;
      text-shadow: 0px 0px 5px #000;
      border: 1px solid #A8D0D0;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      margin: -15px 5px 0px 5px;
      box-shadow: 0px 0px 5px #D1EEEE;
    }
    
    .SiteSearch .Button {
      margin-top: 0px;
    }
    
    #Panel {
      color: #D1EEEE;
      text-shadow: 0px 0px 5px #000;
      border: 1px solid #D1EEEE;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      margin: 5px 5px 0px 5px;
      box-shadow: 0px 0px 5px #D1EEEE;
      padding: 5px;
    }
    
    a:hover {
      color: #A8D0D0;
    }
    
    .Count {
      background: rgba(0, 0, 0, 0.5);
      border-radius: 4px;
      color: #FFF;
      border: 1px solid #D1EEEE;
      box-shadow: 0px 0px 1px #D1EEEE;
    }
    
    .PanelCategories li:first-child, .PanelCategories li:nth-last-child(2) {
      border-bottom: 1px solid #737373;
      padding-bottom: 4px;
    }
    
    #Panel .FilterMenu li, .PanelInfo li {
      border-bottom: 1px solid #737373;
      padding-bottom: 4px;
    }
    
    .Button:hover {
      color: #FFF !important;
      border: 1px solid #FFF;
    }
    
    .Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active {
      box-shadow: none;
      background: rgba(0, 0, 0, 0.2);
    }
    
    .DataList .Item {
      margin: 15px 0px 15px 0px;
    }
    
    .Pager .Highlight {
      color: #8CB2B2;
    }
    
    .MItem, .ShowDiscussionAuthor {
      color: #EEEEEE;
    }
    
    .CommentFormWrap {
      margin-top: 5px;
    }
    
    .PoweredByVanilla {
      pointer-events: none !important;
      cursor: default !important;
      background: transparent !important;
    }
    
    .CommentForm textarea.TextBox {
      background: #151515;
      border: 1px solid #D1EEEE;
      color: #EEEEEE;
    }
    
    .EmoticonBox {
      background-color: #151515;
      border: 1px solid #D1EEEE;
      box-shadow: 0px 0px 5px #D1EEEE;
    }
    
    .EmoticonContainer {
      background: rgba(0, 0, 0, 0.2);
    }
    
    .EmotifyDropdownActive {
      background-color: rgba(0, 0, 0, 0.2);
    }
    
    .DiscussionForm input.InputBox, .DiscussionForm textarea {
      color: #EEEEEE;
      text-shadow: 0px 0px 5px #000;
      border: 1px solid #A8D0D0;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      box-shadow: 0px 0px 5px #D1EEEE;
    }
    
    .DiscussionForm label {
      color: #A8D0D0;
    }
  }
  
  @-moz-document url-prefix("https://forum.userstyles.org/discussion/") {
    .FormWrapper {
      border: none;
      box-shadow: none;
    }
  }
  
  @-moz-document url-prefix("https://userstyles.org/help/stylish_firefox"), url-prefix("https://userstyles.org/help/stylish_chrome") {
    h3 {
      color: #EEEEEE;
      text-shadow: 0px 0px 5px #000;
      border: 1px solid #95BCBC;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      box-shadow: 0px 0px 5px #D1EEEE;
      padding: 3px;
    }
  }
}
