css:
@-moz-document domain("furaffinity.net") {
div.sitebanner {visibility:hidden!important;height:28px!important;}
div.in {display:none!important;}
ul#mainnav {font-size:14px!important;top:45px!important;}
div.userpage-tabs a {font-size:14px!important;margin-top:-10px!important;}
div.userpage-tabs {padding:0px!important;margin:0px!important;height:30px!important;float:right!important;position:relative!important;bottom:-110px!important;left:-2%!important;background-color:#232323!important;border-top-left-radius:10px!important;border-top-right-radius:10px!important;}
a.current {background-color:#c11616!important;height:40px!important;}
.ads {display:none!important;}
table.userpage-data.bg3 {width:33%!important;margin-left:5px!important;margin-top:15px!important;border-top-left-radius:10px!important;border-top-right-radius:10px!important;border-spacing:10px!important;}
span.user {font-size:24px!important;font-family:serif!important;font-style:italic!important;}
input.button {border-radius:7px!important;margin:2px!important;background-color:#454545!important;}
input.button.remove-nuke {background-color:#891c1c!important;}
.button {line-height:20px!important;font-size:12px!important;}
a.oldest {border-radius:7px!important;margin:2px!important;line-height:20px!important;font-size:12px!important;}
a.newest {border-radius:7px!important;margin:2px!important;line-height:20px!important;font-size:12px!important;}
a.more {border-radius:7px!important;margin:2px!important;line-height:20px!important;font-size:12px!important;}
center.flow img {border:0px!important}
form#messages-form {width:67%!important;margin:0 auto!important;}
h1 {text-align:center!important;}
.bg1 {background-color:#343434!important;background-image:url(http://www.transparenttextures.com/patterns/az-subtle.png)!important;}
.bg2 {background-color:#454545!important;}
fieldset#messages-journals {font-size:12px!important;background-color:#434343!important;padding:10px!important;border-radius:10px!important;}
center.flow.messagecenter {background-color:#454545!important;padding-top:10px!important;border-radius:10px;}
.content {background-color:#454545!important;border-top-right-radius:7px;border-bottom-right-radius:7px;border-bottom-left-radius:7px;}
span.userpage-section-header {background-color:#232323;padding:7px!important;border-radius:7px;padding-top:2px!important;margin-top:7px!important;}
.userpage-module {background-color:#343434!important;}
img.img_ad {display:none!important;}
.bg2.p20l.aligncenter {text-align:left!important;height:55px!important;}
.bg2.p20l.aligncenter form {bottom:-15px!important;position:relative!important;}
.bg2.p20l.aligncenter input#toggle-descriptions {bottom:-15px!important;position:relative!important;}
.bg2.p20l.aligncenter input {border-radius:5px!important;}
.col3 {min-width:90px!important;width:90px!important;}
#ad-extra-flat {display:none!important;}
.usercommentbubble.bg3 {margin-top:30px!important;}
.usercommentbubble.bg2 {border-top-left-radius:0px!important;border-bottom-left-radius:0px!important;margin-left:-10px!important;}
.row2 {font-size:10px!important;}
.row1 {font-size:10px!important;}
a.button.padleft {margin-left:0px!important;}
a.button.red {padding-left:24px!important;padding-right:24px!important;}
.col2 {min-width:220px!important;}
.imgshad {margin-top:20px!important;background-color:transparent!important;}
#submission_page .content {background-color:transparent!important;}
img#submissionImg {border:20px solid #343434!important;}
#submission_page span.fontsize20.aligncenter.p20 {background-color:#454545!important;padding:0px!important;margin:20px auto!important;padding-bottom:10px!important;width:75%!important;border-radius:10px!important;font-size:14px!important;}
#submission-details span.fontsize16 a {font-family:serif!important;font-size:24px!important;font-style:italic!important;}
.replyto-name {font-family:serif!important;font-size:24px!important;font-style:italic!important;background-color:#232323!important;padding-left:10px!important;padding-right:10px!important;position:relative!important;left:-15px!important;border-top-right-radius:10px!important;}
.usercommenticon img {border:10px solid #232323!important;border-top-left-radius:5px!important;border-bottom-right-radius:5px!important;border-bottom-left-radius:5px!important;}
.usercommentseperator {margin:0 auto!important;float:none!important;}
#submission-details {background-color:#232323!important;width:90%!important;margin:auto!important;border-radius:10px!important;}
#submission-details ul.details {font-size:14px!important;background:#343434!important;width:75%!important;height:90%!important;padding:15px!important;}
#submission-details img {border:10px solid #343434!important;}
#submission-details .desc-col {padding-left:160px!important;}
.responsenav.reply {position:relative!important;left:-20px!important;border-top-left-radius:0px!important;border-top-right-radius:0px!important;top:0px!important;}
.userpage_colright {margin:auto!important;float:right!important;}
.usershouticon img {border:10px solid #232323!important;}
#userpage_shouts .fontsize20 {font-family:serif!important;font-size:24px!important;font-style:italic!important;background-color:#232323!important;padding-left:10px!important;padding-right:10px!important;position:relative!important;bottom:-2px!important;left:-15px!important;border-top-right-radius:10px!important;}
#userpage_shouts .usershoutbubble {position:relative!important;bottom:-2px!important;left:-8px!important;border-top-left-radius:0px!important;border-bottom-left-radius:0px!important;}
#shoutboxentry {width:80%!important;min-width:80%!important;}
}
