@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.cc"), domain("reactor.cc") {
 
/*Раскомментировать для удаления рекламы и плашки adblock (вероятны баги из-за структурной подвижности рекламы)
  * > li:nth-child(1) > div:nth-child(1) {
    display: none;
  }
  #header + span a {
    display: none !important;
  }
  
  #pageinner>div:not([id="content"]):not([id="sidebar"]):not([id="tagList"]) {
    display: none !important;
  } 
*/
/*Общие элементы*/
  body {
    color: #7Bf;
  }
  * {
    font-family: Tahoma,Arial,Verdana,sans-serif;
    font-size: 14px;
    color: #7Bf;
  }
  textarea, input[type="text"], .sfSignin input[type="password"] {
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px !important;
    border: 1px solid rgb(50,150,250) !important;
    color: #bbb;
    font-size: 14px;
  }
  textarea:focus, input[type="text"]:focus {
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px #000 !important;
  }
  input[type="submit"], input[type="button"], button, .big_button {
    background: transparent;
    border: 0px;
    border-radius: 3px;
    box-shadow: 0px 0px 10px 3px rgb(50,150,255) inset;
    color: #0FF;
    min-height: 20px;
  }
  input[type="submit"]:hover, input[type="button"]:hover, button:hover, .big_button:hover {
    opacity: 1;
    box-shadow: 0px 0px 10px 3px rgb(100, 250, 250), 0px 0px 5px 5px rgb(100,250,250) inset;
    color: #0FF;
  }
  input[type="submit"]:focus, input[type="button"]:focus {
    outline: 0px;
  }
  input[type="text"]:focus, button:focus, textarea:focus, .sfSignin input[type="password"] {
    outline: 0px;
    color: #0D0;
  }
  .sidebarContent p.login_link a {
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px transparent !important;
    border: 1px solid rgb(50,150,250);
    border-radius: 3px;
    line-height: 40px;
    font-size: 22px;
  }
  .sidebarContent p.login_link a:hover {
    opacity: 1;
  }
  .sidebarContent p.login_link {
    width: 299px;
    border-radius: 3px;
  }
  .sidebarContent p.login_link:hover {
    box-shadow: 0px 0px 10px 7px rgb(50,150,255) inset;
  }
  .ui-widget-content {
    color: inherit !important;
  }
  .ui-widget-content a {
    color: #FFA000;
  }
  
  /*Фон*/
  html {
    background: url(http://www.mediafire.com/convkey/c3e8/yxdky35k1qlenaqzg.jpg) repeat-y scroll center 0px rgb(3,4,19) !important;;
  }
  body {
    background: rgb(3,4,19) !important;;
  }
  #container {
    background: url(http://www.mediafire.com/convkey/8916/tsodi143fmxc2pszg.jpg) no-repeat scroll center top transparent !important;;
    margin: 0px;
    padding: 0px;
    position: absolute;
    width: 100%;
    min-width: 1111px;
  }
  #page {
    overflow: visible;
    position: relative;
  }
  #page::after {
    content: " ";
    background: linear-gradient(to left, rgb(10,10,10), rgb(30,40,80));
    position: absolute;
    width: 50px;
    height: 100.1%;
    right: -50px;
    top: -3px;
    border-radius: 0px 50px 5px 5px;
    border: 3px outset rgb(10,10,10);
    border-top: 3px solid rgb(30,30,30) ;
  }
  #page::before {
    content: " ";
    background: linear-gradient(to right, rgb(10,10,10), rgb(30,40,80));
    position: absolute;
    width: 50px;
    height: 100.1%;
    left: -50px;
    top: -3px;
    border-radius: 50px 0px 5px 5px;
    border: 3px inset rgb(10,10,10);
    border-bottom: 3px solid rgb(30,30,30);
  }
  
  /*Снесение периферийного мусора*/
  #container::before, #container::after {
    display: none;
  }
  
  /*Шапка Space Reactor*/
  #header {
    position: absolute;
    top: 0px;
    height: 160px;
    left: 0px;
    right: 0px;
    margin: 0px auto !important;
  }
  #header .logo a, #header .logo a:hover {
    width: 375px;
    height: 85px;
    background: url("http://www.mediafire.com/convkey/dfc3/sgq8skn5ijjxemkzg.jpg") no-repeat scroll 0px 0px transparent;
  }
  #header .logo {
    position: absolute;
    width: 375px;
    padding: 0px 0px 0px;
    top: 40px;
    left: 475px !important;
    margin-top: 0px;
  }
  #header .description {
    color: #FFF;
    text-shadow: 0px 0px 5px #0096fF;
    font-size: 14px;
    left: 685px;
    top: 120px;
    margin-top: 0px;
  }
  
  /*Верхняя плашка*/
  #topbar {
    position: relative;
    margin-top: 160px;
  }
  .topbar_wr {
    position: relative;
    width: 1150px;
    background: transparent;
    z-index: 0;
    line-height: 40px;
    margin: 0px auto 0px;
  }
  .topbar_wr a {
    color: rgba(0,0,0,0);
  }
  .top_logo::after {
    text-shadow: 
      0px 0px 1px rgb(0,150,250),
      0px 0px 1px rgb(0,150,250),
      2px 0px 1px rgb(0,150,250),
      -2px 0px 1px rgb(0,150,250),
      0px 0px 3px rgb(0,150,250),
      0px 0px 10px rgb(0,150,250);
    color: rgb(0,0,0);
    content: 'SpaceReactor';
    position: relative;
    right: 80px;
  }
  .topbar_inner {
    padding: 0px 30px;
    height: 45px;
    background: rgba(94, 200, 255, 0.2);
    border-top: 1px solid rgba(94, 165, 255, 0.81);
    border-right: 1px solid rgba(94, 165, 255, 0.81);
    border-left: 1px solid rgba(94, 165, 255, 0.81);
    box-shadow: 
      0px 0px 5px 3px rgb(50, 150, 255) inset;
    z-index: 2;
    border-radius: 50px 50px 0px 0px;
    width: auto;
  }
  .icon_mail {
    position: relative;
    top: 0px;
  }
  .topbar_inner * {
    color: #0aF;
  }
  .login_wr a {
    color: #0D0;
    line-height: 40px;
  }
  .sswither {
    border: 2px solid #0af;
    margin: 7px 10px;
    border-radius: 25px 25px 10px 10px;
  }
  .sswither:hover  {
    opacity: 1;
    box-shadow: 
      0px 0px 3px 1px #0af inset;
  }
  .swf_switcher span {
    text-transform: uppercase;
  }
  .sswither span {
    border-radius: 25px;
    background: none repeat scroll 0% 0% #0af;
    box-shadow: 
      0px 0px 5px 3px rgb(0,0,0) inset;
  }
  .sswither span:hover {
    box-shadow: 
      0px 0px 5px 3px rgb(0,0,0) inset;
    background: none repeat scroll 0% 0% rgb(0, 250, 250);
  }
  
  /*Нав. панель*/
  #navcontainer {
    overflow: visible;
    height: 60px;
    width: 1260px;
    padding: 0px;
    box-shadow: 
      0px 0px 25px 5px rgb(50, 150, 255) inset;
    transition: all .2s ease 0s;
    z-index: 5;
    border-radius: 0px;
    position: relative;
  }
  #navcontainer::after {
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll 0px 0px /25px transparent;
    box-shadow: 
      0px 0px 5px 3px rgb(50, 150, 255) inset;
    border-radius: 10px;
  }
  #navcontainer ul {
    height: 58px;
  }
  #navlist {
    height: 58px;
    padding: 8px 10px 10px 20px;
  }
  .post_random {
    display: inline-block;
    position: absolute;
    right: 280px;
    top: 10px;
  }
  .post_random a {
    padding: 0px 15px;
    background: transparent;
    box-shadow: 
      0px 0px 10px 3px rgb(100,100,255) inset;
    border-radius: 3px;
    color: rgb(100,100,255);
  }
  .post_random a:hover {
    opacity: 1;
    box-shadow: 
      0px 0px 10px 3px rgb(100,100,255), 
      0px 0px 5px 5px rgb(100,100,255) inset;
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll -2px 4px /25px rgb(100,100,255);
    color: #FFF;
    text-shadow: 0px 0px 3px #00F;
  }
  .post_random a::after {
    display: none;
  }
  #searchmenu {
    position: absolute;
    width: 240px;
    padding: 0px 10px;
    display: inline-block;
    top: 10px;
    right: 15px;
    border-radius: 3px;
    border: 2px solid rgb(50,150,250);
    float: none;
  }
  #s {
    background: none !important;
    border: 0px !important;
    font-size: 14px;
    color: #0CF;
  }
  #s:focus {
    color: #0D0;
  }
  #searchsubmit {
    box-shadow: none;
  }
  #navcontainer ul li a{
    color: #AFF;
    z-index: 3;
  }
  #navcontainer ul li.current_page_item a, #navcontainer ul li.current_page_ancestor a, #navcontainer ul li#konkurs.current_page_ancestor a {
    border-radius: 3px;
    background: transparent;
    box-shadow: 
      0px 0px 10px 3px rgb(50,150,255) inset;
    color: #0FF;
    z-index: 3;
  }
  #navcontainer ul li.current_page_item a:hover, #navcontainer ul li.current_page_ancestor a:hover, #navcontainer ul li#konkurs.current_page_ancestor a:hover {
    opacity: 1;
    box-shadow: 
      0px 0px 10px 3px rgb(100, 250, 250), 
      0px 0px 5px 5px rgb(100,250,250) inset;
    color: #FFF;
    text-shadow: 0px 0px 3px #00F;
    z-index: 3;
  }
  #navcontainer ul > li.current_page_item:nth-child(2):hover {
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll 5px 4px /25px rgb(100,250,250);
  }
  #navcontainer ul > li.current_page_item:nth-child(3):hover {
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll 1px 4px /25px rgb(100,250,250);
  }
  #navcontainer ul > li.current_page_item:nth-child(4):hover {
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll -3px 4px /25px rgb(100,250,250);
  }
  #navcontainer ul > li.current_page_item:nth-child(5):hover {
    background: url(http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg) repeat scroll -8px 4px /25px rgb(100,250,250);
  }
  #navcontainer ul li.current_page_item a::after, #navcontainer ul li.current_page_ancestor a::after, #navcontainer ul li#konkurs.current_page_ancestor a::after {
    display: none;
  }
  #searchBar {
    height: 45px;
    padding: 0px 0px;
    border-radius: 0px 0px 50px 50px;
    background: rgba(94, 200, 255, 0.2);
    box-shadow: 
      0px 0px 5px 3px rgb(50, 150, 255) inset;
    border-bottom: 1px solid rgba(94, 165, 255, 0.81);
    border-right: 1px solid rgba(94, 165, 255, 0.81);
    border-left: 1px solid rgba(94, 165, 255, 0.81);
    width: 1150px;
    position: relative;
    margin: 0px auto 15px;
}
  #submenu {
    height: 45px;
  }
  #submenu a, #submenu b {
    font-family: Tahoma,Arial,Verdana,sans-serif;
    color: #8ad;
    font-size: 14px;
    padding: 0px 40px;
  }
  #submenu div.submenuitem.active a {
    height: 45px;
    color: #0D0;
    line-height: 45px;
    padding: 0px 40px;
    border-radius: 2px;
    box-shadow: 0px 0px 10px 5px rgb(100,150,250) inset;
    background: transparent;
  }
  #submenu div.submenuitem.active a:hover {
    opacity: 1;
    box-shadow: 0px 0px 10px 5px rgb(100,250,250) inset;
  }
  #submenu div.submenuitem, #submenu span {
    height: 45px;
    line-height: 45px;
  }
  #submenu div.submenuitem:hover, #submenu span:hover {
    box-shadow: 0px 0px 10px 5px rgb(100,250,250) inset;
  }
  div.submenuitem:nth-child(1) > a:nth-child(1) {
    border-radius: 2px 2px 2px 50px !important;
  }
  div.submenuitem:nth-child(1):hover {
    border-radius: 2px 2px 2px 50px;
  }
  div.submenuitem:nth-child(4) {
    border-radius: 0px 0px 50px 0px;
  }
  #submenu div.tagname {
    padding: 0px 20px 0px 40px;
    color: #0D0;
  }
  
  /*Наполнение страницы*/
  #pageinner {
    padding: 15px;
    border-radius: 0px;
    background: rgba(94, 200, 255, 0.2);
    box-shadow: 
      0px 0px 5px 3px rgb(50,150,250) inset;
  }
  #content {
    width: 900px;
  }
  #add_post > div > div.add_post_footer {
    overflow: visible;
  }
  #add_post span.super-tag {
    padding: 0px 5px;
    border-radius: 3px;
    padding: 0px 5px;
    background: none repeat scroll 0% 0% rgba(0,0,0,0.6);
    color: #6CE;
    border: 1px solid rgb(50,150,250);
  }
  #add_post span.super-tag:hover {
    box-shadow: 0px 0px 3px 1px #0fF inset;
  }
  #add_post div.add_post_tags_input input.tip {
    text-shadow: 
      2px 0px 1px #000,
      -2px 0px 1px #000,
      0px 0px 1px #000,
      0px 0px 3px #000,
      0px 0px 5px #000;
  }
  .redactor_editor {
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px !important;
  }
  .redactor_editor:focus {
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px #000 !important;
  }
  .redactor_box, .redactor_box textarea {
    background-color: transparent;
  }
  .redactor_box {
    border: 1px solid rgb(50,150,250);
  }
  .redactor_toolbar {
    border-bottom: 1px solid rgb(50,150,250);
    background: linear-gradient(to top, #58D 0%, #5AF 100%) repeat scroll 0% 0% transparent;
  }
  .redactor_toolbar li.redactor_separator {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
  }
  .article .ufoot span.comments a:hover {
    opacity: 1;
    box-shadow: 0px 0px 5px 3px rgb(50,250,50) inset;
    color: #0D0;
  }
  .article .ufoot span.comments a::before {
    display: none;
  }
  .article .post_content_expand {
    height: 30px;
    box-shadow: 
      0px 0px 3px 1px rgb(50,250,50) inset;
    background: transparent !important;
    border-radius: 2px;
  }
  .article .post_content_expand:hover {
    opacity: 1;
    box-shadow: 
      0px 0px 5px 3px rgb(50,250,50) inset;
  }
  .article .post_content_expand span {
    line-height: 30px;
    text-align: center;
    color: #0D0;
    text-transform: uppercase;
  }
  .article .post_content_expand span::after {
    display: none;
  }
  .article .post_top {
    padding-right: 0px;
  }
  .commentnum {
    line-height: 30px !important;
    padding: 0px 14px 0px 10px;
    border: 0px solid #FDB201;
    background: #000 !important;
    border-radius: 3px;
    box-shadow: 
      0px 0px 3px 1px rgb(50,250,50) inset;
    color: #0D0;
  }
  .commentnumDelta {
    border: 2px solid rgb(50,150,250);
    border-radius: 3px 3px 3px 25px;
    background: #000;
    height: 22px;
    line-height: 20px !important;
  }
  
    /*-----------------------------------------------------------------------------------------------*/
    /*Здесь новые плюсы и минусы к постам и комментариям, если не нравятся, просто закомментируйте раздел*/
     .comment_rating div.vote-plus, .comment_rating div.vote-minus {
       width: 20px;
       height: 20px;
     }
     .comment_rating div.vote-minus {
       background: url('http://www.mediafire.com/convkey/49fd/cvq2ldu2ns9ssvezg.jpg') no-repeat scroll 0px -20px /100% transparent;
       margin-left: 0px;
       position: relative;
       bottom: 1px;
       opacity: 0.7;
     }
     .comment_rating div.vote-minus:hover {
       background-position: 0px 0px !important;
       opacity: 1;
     }
     .comment_rating div.vote-plus {
       background: url("http://www.mediafire.com/convkey/2cb5/re9pom2tepawv2qzg.jpg") no-repeat scroll 0px -20px /100% transparent;
       position: relative;
       bottom: 1px;
       margin-right: 3px;
       opacity: 0.70;
     }
     .comment_rating div.vote-plus:hover {
       background-position: 0px 0px !important;
       opacity: 1;
     }
     div.vote-minus {
       background: url("http://www.mediafire.com/convkey/49fd/cvq2ldu2ns9ssvezg.jpg") no-repeat scroll 0px -33px /100% transparent;
       margin: 0px;
       position: relative;
       bottom: 5px;
     }
     div.vote-minus:hover {
        background-position: 0px 0px;
     }
  
     div.vote-plus {
       background: url("http://www.mediafire.com/convkey/2cb5/re9pom2tepawv2qzg.jpg") no-repeat scroll 0px -33px /100% transparent;
       margin: 0px 8px 0px 0px;
       position: relative;
       bottom: 5px;
       float: left;
     }
    /*-----------------------------------------------------------------------------------------------*/
  
  .comment * {
    color: #7Bf;
  }
  .comment {
    border-radius: 2px;
    background-color: rgb(0,0,0); 
    background-image: url("http://www.mediafire.com/convkey/f60e/emr6z8t1aed5llfzg.jpg") !important;
    background-size: 25px !important;
    background-repeat: repeat !important;
    border: 1px solid rgba(50,150,250, 0.6);
    padding: 5px 10px;
  }
  .comment:hover {
    box-shadow: 0px 0px 5px 1px rgba(50,150,250, 0.8) inset;
    background: url("http://www.mediafire.com/convkey/9c11/6yi9kh2s15psd77zg.jpg") 0px 0px /25px #000 !important;
  }
  .comment .txt > div:nth-child(1) {
    display: inline;
    line-height: 20px;
    vertical-align: middle !important;
    font-size: 14px;
  }
       .comments_bottom {
       overflow: hidden !important;
       font-size: 12px;
       margin: 0px 0px 0px 8px !important;;
       display: inline;
       line-height: 20px !important;
       vertical-align: middle !important;
     }
     .comments_bottom:before {
       content: "—";
       margin-right: 5px;
       text-decoration: none !important;
     }
     .comments_bottom > * {
       vertical-align: middle;
