     }
     .comments_bottom .date {
       margin-right: 5px;
     }
     .comment_username, .comment_username:hover {
       display: inline !important;
       color: rgb(50,150,250);
       font-size: 12px;
     }
     .comment_link {
       font-size: 12px;
     }
  .reply-link {
    height: auto;
  }
  .comment_list {
    margin-left: 15px;
  }
  .article .ufoot span a.response {
    background: none;
    padding: 0px 5px;
    color: #DA0;
    line-height: 20px;
    background: transparent;
    display: inline;
  }
  .article .ufoot span a.response:hover {
    color: rgb(250,250,250);
  }
  .article .ufoot span a.response::after {
    display: none;
  }
  .article .ufoot span, .article .ufoot span a {
      line-height: 20px;
     }
  .comments_bottom .comment_date span {
    font-size: 11px;
  }
  .article .ufoot .txt span.comment_rating {
       line-height: 20px;
       margin-left: 15px !important;
       float: none;
       font-size: 16px;
     }
  .article {
    border: 2px solid rgb(50,150,250) !important;
    margin: 0px 0px 20px 0px;
    padding: 10px;
    background: rgba(0,0,0,0.6);
  }
  .article .uhead {
    margin-bottom: 5px;
  }
  .article .uhead a {
    color: #0d0;
  }
  .taglist {
    margin: 0px 0px 5px;
  }
  .taglist a {
    color: rgb(50,200,250);
    background: none repeat scroll 0% 0% rgba(50,150,250,0.3);
    border: 1px solid rgb(50,150,250);
    border-radius: 3px;
    padding: 0px 5px 1px 5px;
    text-decoration: none;
    margin-right: 0px;
    font-size: 12px;
  }
  .taglist a:hover {
    box-shadow: 0px 0px 3px 1px #0FF inset;
  }
  .manage_post_wr {
    background: rgba(50,150,250,0.3);
    line-height: 30px;
    border-radius: 3px;
  }
  
  /*Плашка с соц. сетями и избранным*/
  .article .uhead .uhead_share {
       color: #CCC;
       height: 36px;
       line-height: 30px;
       float: right;
       width: 175px;
       border-radius: 5px;
       padding: 5px;
       position: static
     }
  .article .uhead .uhead_share span {
       vertical-align: middle;
       line-height: 20px;
       display: inline-block;
       position: relative;
       bottom: 0px;
       right: 0px;
     }
  .article .uhead .uhead_share span.favorite_link {
       background: url("http://www.mediafire.com/convkey/7c6d/k07w5cao5q7iyj7zg.jpg") no-repeat scroll 0px -0px transparent;
       position: relative;
       left: 135px;
       top: 0px;
       background-size: 220%;
