css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.org"), domain("thepiratebay.se") {
    
    html,body{
        margin: 0;
        padding: 0;
    }
    body{
        font-size: 11px;
        font-size: 0.71em;
        min-width: 800px;
        line-height: initial;
    }
    
    select, input{
        border: 1px solid #ccc;
        border-radius: 3px;
    }
    
    #fp h1 a {
        margin-bottom: 50px;
    }
    
    a, a:hover, a:visited, a:focus, a:link, #browseContainer a{
        text-decoration: none;
        border: none;
        color: #A4724D;
        transition: 0.2s linear color;
    }
    a:hover, #browseContainer a:hover, table#searchResult a:hover, dt#upload a:hover {
        color: #614226;
        border: none;
        text-decoration: none;
    }
    
    #fp{
        width: 95%;
    }
    #fp #navlinks a {
       font-size: 1.1em;
    }

    #fp #inp input {
       padding: 10px;
       width: 500px;
        border: 1px solid #ccc;
        border-radius: 3px;
    }
    #fp #inp span {
       line-height: 1.5em;
       font-size: 0.9em;
    }
    
    #subm input, .submitbutton, input[type="submit"], input[type="button"]{
        border: 1px solid #ccc;
        border-radius: 3px;
        padding: 5px;
        transition: 0.1s linear background, 0.1s linear color, 0.1s linear border;
        background: #eee;
        cursor: pointer;
    }
    #subm input:hover, .submitbutton:hover, input[type="submit"]:hover, input[type="button"]:hover{
        border: 1px solid #614226;
        color: #eee;
        background: #614226;
    }
    
    input[type="text"]{
        padding: 0.2em 0.5em;
    }
    
    footer{
        margin-top: 150px;
    }
    footer nav a, #foot a{
        padding: 3px 5px;
        font-size: 1.1em;
    }
    #foot{
        padding-top: 50px;
    }
    
    
    /*table*/
    #content {
       min-width: 800px;
       margin: 0 auto;
    }
    #main-content{
        margin: 40px 0;
    }
    
    table#searchResult tr, table#searchResult tr.alt {
       background: #eee;
    }
    table#searchResult tr:hover, table#searchResult tr.alt:hover  {
       background: white;
    }
    table#searchResult tr td{
        padding: 6px 3px;
    }
    table#searchResult tr td:nth-child(3), table#searchResult tr td:nth-child(4){
        text-align: center;
    }
    
    table#searchResult th, table#searchResult td {
       padding: 8px 10px;
    }
    
    thead#tableHead th, thead#tableHead th a, thead#tableHead th a:hover{
        color: white;
        background: #614226;
    }
    .detLink{
        font-weight: bold;
    }
    
    
    /*header*/
    
    #header{
        padding: 15px 0;
        margin: auto;
    }
    #q{
        margin: auto;
        width: 900px;
        font-size: 1.1em;
    }
    #q .inputbox{
        border: 1px solid #ccc;
        border-radius: 3px;
        padding: 5px 7px;
        margin: 10px 10px 10px 0;
        width: 450px;
    }
    .submitbutton{
        padding: 4px 8px;
    }
    #TPBlogo{
        margin-right: 20px;
    }
    
    h2 {
       font-size: 1.1em;
       line-height: 1.5em;
       text-align: center;
       padding: 10px;
       border-bottom: none;
       color: white;
       background: #614226;
    }
    h2 span{
        float: none;
        margin-right: 15px;
    }
    h2 a{
        color: #eee !important;
    }
    
    /*torrent*/
    
    #detailsouterframe {
       min-width: 800px;
       text-align: right;
    }
    #details{
        background: #eee;
    }
    #detailsframe #title {
       padding: 10px;
        color: white;
        background: #614226;
    }
    #details dt {
       color: black;
    }
    #details dl {
       margin: 10px 20px 10px 0px;
    }
    
    #details dd, #details dt {
       border-bottom: none;
    }
    
    #details dl.col1 {
       width: 370px;
    }
    #details dl.col2 {
       width: 370px;
    }
    #details dl.col2 dd, #details dl.col1 dd {
        margin: 0 0 10px 0px;
    }
    
    #details #CommentDiv{
        width: 100%;
        height: auto;
        background: transparent;
    }
    #details #CommentDiv textarea{
        resize: vertical;
        width: 90%;
        padding: 1em;
    }
    
    .download{
        font-size: 1.1em;
    }
    
    .nfo {
       margin: 20px 0;
       width: 760px;
       padding: 10px;
    }
    #details pre{
        font-size: 14px;
        line-height: 1.0em;
    }
    
    
    #delete{
        padding: 0.5em;
        background: darkred !important;
    }
    #delete:hover{
        background: red !important;
    }
    #delete :hover{
        border: none;
        outline: none;
    }
    #delete a{
        color: white;
    }
    #detailsouterframe #delete{
        text-align: center;
    }
    
    
    /* language */
    tbody tr td center{
        margin: 3px;
    }
    table[dir="ltr"]{
        width: 800px;
        margin: 40px 0;
    }
    
    
    /*category*/
    
    #categoriesTable {
       width: 1000px;
       min-width: 800px;
       margin: 20px auto;
    }
    #browseContainer dt {
        background: #eee;
        padding: 7px 10px;
    }
    #browseContainer dd {
       margin: 0px 0px 35px 10px;
    }
    dt#showall a, dt#showall a:hover, dt#latest, dt#latest a:hover{
       border-bottom: none;
    }
    
    #browseContainer .categoriesContainer {
       width: 33%;
    }
    
    #browseContainer dt {
        font-size: 1.3em;
    }
    
    
    /*about*/
    #about, .policy, #searchcloud{
        width: 800px !important;
        margin: 40px auto;
    }
    
    
    /*comments*/
    #commentsheader{
        font-size: 1.2em;
    }
    #comments{
        font-size: 1.0em;
    }
    #comments > div{
        margin: 20px 0;
    }
    #comments p{
        margin-left: 0;
    }
    .comment{
        padding: 10px;
        border-radius: 3px;
    }
    
    #blog #comments dt{
        background: #eee;
        padding: 0.5em;
        margin: 2.5em 0 0.5em 0;
    }
    
    .browse-coms >*{
        padding: 0.4em 0.7em;
        font-weight: bold;
    }
    .browse-coms a:not(:first-child):not(:last-child):hover, .browse-coms strong{
        color: #eee;
        background: #614226;
    }
    
    
    /*blog*/
    #blog{
        width: 800px;
    }
    .post h3, .doodle h3{
        background: #eee;
        margin: 0 0 10px 0;
        padding: 10px;
    }
    .doodle{
        margin: 0 0 60px 0;
        border: none;
    }
    .doodle img{
        max-width: 100%;
        height: auto;
        margin: 0;
    }
    
    /*register*/
    #registerTable {
       width: 500px;
        margin: 40px auto;
        line-height: initial;
    }
    #registerTable input{
       width: 250px;
        padding: 5px 10px;
        margin: 5px 0;
    }
    #registerTable input[type="submit"]{
       width: 150px;
    }
    
    #recaptcha_area{
        margin: 2em 0;
    }
    
    /*login*/
    
    #loginForm {
       width: 370px;
       margin: 40px auto;
    }
    #loginForm input[type="submit"]{
        width: 150px;
        position: initial;
        display: block;
        margin: auto;
    }
    #loginForm label {
       display: block;
       margin-top: 0;
        line-height: 2em;
        height: 40px;
    }
    #loginForm input {
        width: 200px;
        padding: 5px 10px;
    }
    
    /*user panel*/
    table#cpl input[type="password"]{
        width: 90%;
        padding: 0.2em 0.5em;
    }
    table#cpl input[type="submit"]{
        padding: 0.2em 0.5em;
    }
    table#cpl hr{
        line-height: 2.5em;
    }
    
    /*guide*/
    .guide{
        width: 600px;
    }
    .guide a:hover{
        border-bottom: none;
    }
    
    
    /*ads*/
    
    #sky-right, #sky-center, #sky-banner, #foot iframe, .ads, iframe[src*="adbrau"]{
        display: none !important;
    }
    .download a:nth-child(2){
        display: none;
    }
    
    
    
    
    
    
    
    
    
    
}
