td.thead, div.thead { padding: 4px; }



/* ***** basic styles for multi-page nav elements */

.pagenav a { text-decoration: none; }

.pagenav td { padding: 2px 4px 2px 4px; }



/* ***** define margin and font-size for elements inside panels ***** */

.fieldset { margin-bottom: 6px; 

border: 2px solid #000000;

padding: 4px;

}

.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px;}



/* ***** don't change the following ***** */

form { display: inline; }

label { cursor: default; }

.normal { font-weight: normal; }

.inlineimg { vertical-align: middle; }



/* ***** sets background for header area - No need to change this ***** */

#hdr {

background: #0a0a0b;

width: 100%;

height: 90px;

	margin: 0;

	padding: 0; 

}



/* ***** dispalys 1 px border around header area - No need to change this ***** */

#hdr_bdr {

border-left: 1px solid #2a2a2a;

border-right: 1px solid #2a2a2a;

margin: 0;

padding: 0; 

}



/* ***** SETS LOGO AREA FOUND IN HEADER AND ALL OF IT'S PROPERTIES ***** */

#logo {


width: 175px;

height: 90px; 

align: left;

	margin: 0;

	padding: 0; 

}



/* ***** used to create shadow affect for sides ***** */

#brd_in {

border-left: 1px solid #2a2a2a;

border-right: 1px solid #2a2a2a;

}



/* ***** used to create shadow affect for sides ***** */	

#brd_outer {

border-left: 4px solid #1a1a1a;

border-right: 4px solid #1a1a1a;

}



/* ***** small blue bar above nav area and below main header ***** */	

#hdr_btm {

background: #363536;

width: 100%;

height: 14px;

	margin: 0;

	padding: 0; 

}



/* ***** used for navigation background ***** */

#nav_back {

background: #111111;

width: 100%;

height: 29px;

}



/* ***** used to 1 px sides for bottom category etc  ***** */

.ftr_bdr {

border-left: 1px solid #2a2a2a;

border-right: 1px solid #2a2a2a;

margin: 0;

padding: 0; 

}



.info {

padding-left: 3px;

padding-right: 0px;

padding-top: 3px;

padding-bottom: 3px;

background-color: #393939;

border-top: 1px solid #0e0e0e;

border-right: 2px solid #0e0e0e;

border-left: 1px solid #0e0e0e;

border-bottom: 2px solid #0e0e0e;

margin-bottom: 3px;

}



.announce_l {

background: #0f0f0f;

color: #7a7a7a;

