/*======================================
  ubuntuapps ja jp liquid compact 2015/04
  @-moz-document domain("ubuntuapps.blog67.fc2.com")
  ======================================*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntuapps.blog67.fc2.com"), domain("ubuntuapps.info") {
/*--- unwanteds ---*/
.spd_link,
[class$="336"],
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: inherit !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#container {width: auto !important;}

/*-- absolute pane --*/
#ubuntu_linktop, #cse-search-box {left: inherit !important; right: 0;}
#rss {left: inherit !important; right: 220px;}
#menubar1 {top: 100px !important;}

/*--- header ---*/

/*--- footer ---*/

/*--- main contents ---*/
.primary-column, .secondary-column {position: relative !important;}
.primary-column {width: 100% !important; margin: 1em -250pt 0 0 !important;}
.primary-column>* {width: auto !important; margin: 0 250pt 0 0 !important;}
.primary-column>* * {max-width: 100% !important;}
.secondary-column {width: 250pt !important; margin: 1em 0 0 0 !important;}
.secondary-column * {max-width: 250pt !important;}
}
