css:
@-moz-document domain("rollspel.nu") {
/* TA BORT LOGOTYPEN */

#header .header-cell {
   display: none;
  }

#header {
  height: 0px;
  margin-top: 8px;
}

/* FLYTTA NER SÖKRUTAN */

#header .toolbar .search-container {
  position: absolute;
  margin-left: -240px;
  margin-top: 2px;
}

/* DÖLJ RUBRIK */
div.canvas-layout-row.no-columns.h-clearfix.l-row:first-child {
  display: none;
}

.main-header-title {
  margin-bottom: 0px !important;
}

/* FLYTTA NER PRENUMERERA */
div.channel-controls, div.conversation-controls {
  position: absolute;
  z-index: 1;
  right: 1px;
  margin-top: -8px;
  top: 9px;
}

div.conversation-controls .button, div.channel-controls .button {
  background: none;
  border: none;
}

div.conversation-controls .button:hover, div.channel-controls .button:hover{
  background-color: rgba(0, 0, 0, 0.1);
  border: none;
}

/* FÖRTYDLIGA BRÖDSMULOR */
li.crumb.ellipsis a {
  opacity: 0.7;
}

li.crumb.ellipsis:last-child a {
  font-weight: bold;
  font-size: 1.2em;
  opacity: 1;
}

#breadcrumbs {
  margin-bottom: 15px;
  margin-top: 8px;
}

/* FLYTTA NER TOPBAR */

#main-navbar-wrapper {
  padding-left: 0px;
  padding-right: 0px;
  max-width: 1200px;
  min-width: 320px;
  width: 100%;
  height: 0px;
  position: relative;
}

#main-navbar {
  position: absolute;
  top: 45px;
  background: none;
  margin-left: -10px;
  right: 0px;
  max-width: 320px;
  min-width: 320px;
}

/* GÖMMER GILLA-COUNT I TRÅDLISTAN */

div.votes-count {
  display: none;
}

/* GÖR FÖRFATTAREN MINDRE I TRÅDLISTAN */

div.topic-info {
  font-size: smaller;
}

/* FIXAR OVERFLOW FÖR TOPICS I SIDEBAR */

.l-small .topic-list-container .topic-list .topic-item .cell-topic .topic-wrapper {
    overflow: hidden;
    text-overflow: ellipsis
}

/* TAR BORT INFO OM TRÅDSTARTARE FRÅN TRÅDLISTAN I SIDEBAR */
.l-small .topic-list-container .topic-list .topic-item .cell-topic .topic-info {
    display: none;
}

/* TAR BORT AVATAREN FRÅN TRÅDLISTAN I SIDEBAR */
.l-small .avatar {
    display: none;
}

/* MINSKAR STORLEKEN PÅ RUTAN MED FÖRFATTARE AV SENASTE TRÅD I TRÅDLISTAN I SIDEBAR  */
.l-small .topic-list-container .topic-item td.cell-lastpost {
    min-width: 0;
}

/* TAR BORT AVATARBILD FRÅN DENSAMMA */
.cell-topic .vb-icon-topic-status {
    display: none;
}

/* BEGRÄNSAR STORLEKEN PÅ RUTORNA MED TRÅDLÄNKAR I TRÅDLISTAN I SIDEBAR  */
.l-small .topic-list-container .topic-list .cell-topic {
    max-width: 170px;
}

/* TAR BORT DÅLIGT FUNGERANDE LÄNK- OCH BILDKNAPPAR */

.js-toolbar-primary {
    display: none;
}
}
