@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://japan.zdnet.com/) {
  div.frame_col_right
  , .block_large_top
  , .block_article_left_glossary_search
  , .frame_topstory
  , .frame_half
  , .frame_footer
  , .frame_footer_sub
  , .site_footer
  , .frame_col_article_right
  , .block_ad_reg_banner
  , .navi_ctrlpanel
  , .subtab_inner
  , #ad_leaderboard
  , #col2
  , #block_whitepaper
  , #ad_headerlarge
  , #ad_greenvideo
  , #ad_toptext
  , .ad_square_leaf
  , #ad_undertext {
    display: none !important;
  }

  div#col1_inner {
    margin-right: 0 !important;
  }

  div#col1_inner div.style_banner2 div.style_toolbox > div {
    display: none !important;
  }
  div#col1_inner div.style_banner2:hover div.style_toolbox > div {
    display: block !important;
  }


  #ad_greenvideo {
    width: 642px !important;
  }


  #content {
    background-image: none !important;
  }


  #header {
    padding: 0 !important;
  }
  #logo {
    height: auto !important;
    width: auto !important;
  }
  #logo a {
    margin: 0 !important;
  }
}
