css:
@-moz-document url-prefix("http://forums.cockos.com"), url-prefix("http://forum.cockos.com") {
/* 
BG color:
#9DABAB; 

alt:
#4B5454

http://www.cockos.com/reaper/siteimages/div_dark_v.jpg

http://reaper.fm/siteimages/logo4.jpg

*/

a[href^="showthread.php?"] {
    text-decoration: none !important;
}

td > span.smallfont, .tcat, td.smallfont, a[href="search.php?"] > strong {
    color: #eee;
    }

body {
background: #333333 url(http://www.cockos.com/reaper/siteimages/bg_top4.jpg) repeat-x top center ;
    color: #eee;
}

.smallfont, span.time, td[id^="project_typecount"] {
    color: #000;
}

.alt2, .alt2Active {
    background: #889a9a;
    color: #222;
}

.alt1, .alt1Active {
	background: #9DABAB;
    color: #222;
}

/*
.thead {
    background: none !important;
    border: none !important;
    color: black !important;
}
*/

table[bgcolor="#ABB8B8"], .vbmenu_control, .tcat, td[bgcolor="#eeeeee"] , .page, table[bgcolor="#000000"], table[style="border-bottom-width:0px"] > tbody > tr > td.tcat {
    background: none !important;
    border: none !important;
}

table[id^="post"], .panelsurround, #usercss {
    box-shadow: 1px 2px 10px #222;
    margin-bottom: 6px;
    border-top: 1px solid #d7dddd !important;
    border-bottom: 1px solid #474d4d !important;
}

.tfoot {
background: #222;
color: #ddd;
}

/* , td.tcat, td.thead */
#threadslist > tbody > tr, table[id^="post"], .panelsurround, #usercss, .tborder, tbody[id^="collapseobj"] > tr, .vbmenu_control, .thead, .tcat {    
    background: #b2bbba !important;
	background: -moz-linear-gradient(top, #b2bbba 0%, #889a9a 100%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#b2bbba), color-stop(100%,#889a9a)) !important;
	background: -webkit-linear-gradient(top, #b2bbba 0%,#889a9a 100%) !important;
	background: -o-linear-gradient(top, #b2bbba 0%,#889a9a 100%) !important;
	background: -ms-linear-gradient(top, #b2bbba 0%,#889a9a 100%) !important;
	background: linear-gradient(to bottom, #b2bbba 0%,#889a9a 100%) !important;
}

.tborder, td[id^="td_post"], td[style="border: 1px solid #33454B; border-top: 0px; border-bottom: 0px"], td[style="border: 1px solid #33454B; border-left: 0px; border-top: 0px"], td[style="border: 1px solid #33454B; border-top: 0px"], .panel, div[style^="background:url(http://www.cockos.com/reaper/siteimages/forum-head.jpg"], .thead[id="currentPost"], .thead[style^="font-weight:normal; border: 1px solid #33454B; border"] {
    border: none !important;
    background: none !important;
}


.bigusername {
position: relative;
left: 1px;
top: 1px;
font-size: 20px;
font-weight: bold;
color: #EBF2F2 !important;
padding-bottom: 12px;
background-position: bottom;
background-repeat: repeat-x;
    text-shadow: -1px -1px #8A9A9A;
}

table[style="border-top-width:0px"] {
width: auto;
    float: right;
}


/*
br + table[cellpadding="0"] {
    float: left;
    width: auto !important;
}
*/


/* QUOTES: */
td[style="border:1px inset"], pre[dir="ltr"] {
    background: rgba(75, 85, 85, 0.3);
    border: none !important;
    border-radius: 6px;
}

a[href^="report.php?"]::before {
content: "!";
z-index: 2;
left: 8px;
margin-top: -8px;
font-size: 14px;
font-weight: bold;
color: #000;
}


a[href^="report.php?"]::after {
z-index: 1;
border-width: 0 11px 18px;
border-style: solid;
border-color: #F8D201 transparent;
margin-top: -10px;
background: transparent;
}

a[href^="report.php?"] > img {
    display: none;
}


img[src^="http://www.cockos.com/reaper/siteimages/forum-buttons/"]
 {
  	content: "";
    display: inline-block;
    opacity: 0.9;
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/firstnew.gif"] {
	height: 12px;
    width: 12px;
    background: url(http://www.cockos.com/reaper/siteimages/forum-buttons/firstnew.gif);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/lastpost.gif"] {
    height: 12px;
    width: 12px;
    background: url(http://www.cockos.com/reaper/siteimages/forum-buttons/lastpost.gif);
}


img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/quote.gif"] {
    height: 22px;
    width: 63px;
 	background: url(http://i.imgur.com/S4YBE1I.png);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/multiquote_on.gif"] {
    height: 22px;
    width: 25px;
 	background: url(http://i.imgur.com/vy9W6iF.png);
}


img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/multiquote_off.gif"] {
    height: 22px;
    width: 25px;
 	background: url(http://i.imgur.com/kXD828x.png);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/reply.gif"] {
    height: 22px;
    width: 117px;
 	background: url(http://i.imgur.com/IKJxCFF.png);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/newthread.gif"] {
	height: 22px;
	width: 117px;
	background: url(http://i.imgur.com/Ako3Wvu.png);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/edit.gif"] {
    height: 22px;
    width: 44px;
background: url(http://i.imgur.com/WQ3tcRd.png);
}

img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/quote.gif"]:hover, img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/multiquote_on.gif"]:hover, img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/multiquote_off.gif"]:hover, img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/reply.gif"]:hover, img[src="http://www.cockos.com/reaper/siteimages/forum-buttons/edit.gif"]:hover {
    opacity: 1;
}


/* Generic button */

.button, a[href^="projectpost.php?do=addissue"], a[href^="#post"] {
background: #435c62;
background: -moz-linear-gradient(top,  #435c62 0%, #304247 50%, #273539 50%, #364950 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#435c62), color-stop(50%,#304247), color-stop(50%,#273539), color-stop(100%,#364950));
background: -webkit-linear-gradient(top,  #435c62 0%,#304247 50%,#273539 50%,#364950 100%);
background: -o-linear-gradient(top,  #435c62 0%,#304247 50%,#273539 50%,#364950 100%);
background: -ms-linear-gradient(top,  #435c62 0%,#304247 50%,#273539 50%,#364950 100%);
background: linear-gradient(to bottom,  #435c62 0%,#304247 50%,#273539 50%,#364950 100%);
    padding: 4px;
    color: #ddd !important;
    border-radius: 4px;
    border: 1px solid #57747a;
    text-decoration: none !important;
    opacity: 0.9;
    cursor: pointer;
}

.button:hover, a[href^="projectpost.php?do=addissue"]:hover, a[href^="#post"]:hover {
    opacity: 1;
}

img[src="http://www.cockos.com/reaper/siteimages/forum-head.jpg"], img[src="http://www.cockos.com/reaper/siteimages/forum-head-l.jpg"] {
    display: none !important;
}

img[src="http://www.cockos.com/reaper/siteimages/forum-head-rs.jpg"] {
    content: "" !important;
    display: inline-block;
    position: absolute;
    z-index: -99;
    top: -20px;
    left: 5%;
    width: 420px;
    height: 224px;
    background: url(http://www.reaper.fm/siteimages/logo4.jpg);
}

}
