css:
@-moz-document url-prefix("http://nzz.ch/"), domain("nzz.ch") {

   .header
     {position: relative !important;}
   body
     {background-color: #FFF !important; min-width: 240px !important; box-sizing: padding-box !important; overflow-x: hidden !important;}
  .page--front .container:first-of-type, .page--front .adholder--full:first-child + .container
     {max-width: initial !important; width: auto !important; top: 0px !important; padding-top: 1em !important;margin-top: -1em !important;}
  .container .l--row
     {max-width: initial !important; width: auto !important; margin: 0 -3em 1.5em !important; padding: 0 2em !important;}
  .teaser, .teaser--medium
     {padding: 1em 0 !important;}
  .metainfo
     {display: none !important;}
  .container--freehtml
     {display: none !important;}
   .l--col--two-thirds + .l--col--third, .l--col--third + .l--col--third
     {display: none !important;}
  .mobmenu__toggle
     {left: -2em !important;}
  .teaser__lead
     {font-size: 1.2em !important;}
  .teaser__link .title__name, .title
     {margin-bottom: -4em !important; padding-bottom: 4em !important;}
  .title__nam
     {margin-right: 0px !important; float: right !important;}
  .container--footer
     {padding: 1em 2em 0 !important; margin-bottom: -2em !important;}
   
  .comments
     {margin: -3em 0 -4em!important;}
  .comments__btn:link
     {width: 100% !important;}
  
  .mobmenu__body
     {margin: -4.8em -3em 0 !important;}
   

   @media screen and (min-device-width: 765px) {
   .content
     {width: 100% !important;}
      
   .comments__btn:link, .comments
     {width: 100% !important;}
      
   .l--col--two-thirds
     {width: 100% !important; box-sizing: border-box !important;}
   .l--col--two-thirds > .teaser
     {width: 100% !important; margin: 0 !important; padding: 0.5em !important;}
  
    .page--reportage .content__separator
      {max-width: 70em !important; margin: 0 auto;}
   }
   
   @media screen and (max-width: 765px) {
     body
       {min-width: 240px !important;}
     .l--premain
       {display: none !important;}
     .l--main .l--inner
       {margin: 0 -3em 0 0 !important; padding: 0 !important; max-width: 100% !important;}
     .page--front .container:first-of-type, .page--front .adholder--full:first-child + .container
       {top: 0px !important; padding-top: 1em !important;margin-top: -5.1em !important;}
    .figure--teaser
       {width: 7.4em !important; height: auto !important;}
      
     .w--content
       {margin: -1.1em 0 0 !important; max-width: initial !important; width: auto !important;}
     .mobmenu__toggle
       {left: -1.5em !important;}
     .container
       {margin-bottom: 1.5em !important;}
      
     .comments
       {margin-bottom: -3.5em !important;}
     .comments__btn:link
       {margin: -2em 0 -2em !important; padding: 0px !important;}
     .l--row
       {margin: 0 -1em 2em !important;}
      
     .container--footer
       {margin: -1em -1em -1.5em !important;}
     .container--footer:nth-child(1) > .l--row
       {margin-bottom: -2em !important;}
  }
   
  
  @media screen and (max-width: 610px) {
    .l--premain
       {display: none !important;}
    .page--front .container:first-of-type, .page--front .adholder--full:first-child + .container, .container
       {margin: 0px !important;}
     .page--reportage .content__separator + .title--content
       {margin: 0 !important; padding: 0 !important;}
    .figure--teaser
       {width: 7.4em !important; height: auto !important;}
    
    .w--content
       {margin: -1.1em 0 0 !important;}
    .mobmenu__toggle
       {left: 0px !important;}
    .container
       {margin-bottom: 1.5em !important;}
    .comments__btn:link
       {margin: 2.3em 0 -2em !important; padding: 0px !important;}
    .l--row
       {margin: 0 -1em 2em !important;}
    .container--footer:nth-child(1) > .l--row
       {margin-bottom: -1.5em !important;}
  }
}
