css:
@-moz-document url("http://washboard.ws/dash"), url-prefix("http://washboard.ws/"), url-prefix("http://washboard.ws/tagged/"), url-prefix("http://washboard.ws/blog/") {
.post{
background-color: rgb(40, 40, 40);
}

body{
color:#D0D0D0;
}

div#middle{
background-color:#000000;
}

div#top div{
background-color:#303030;
color:#F0F0F0;
}

#menus ul{
color:#CDCDCD;
background-color:#494949;
}

#menus ul a{
color:#CDCDCD;
}

a {
  color: #868686;
}

.action-box {
  background-color: #323232;
  border: 1px solid #8B8B8B;
  }
  
  .action-box .section {
  border-bottom: 1px solid #202020;
      }
      .notes li {
  background-color: #080808;
      }
      
      .question {
  background-color: #0B0B0B;
        }

div#posts p{
    line-height:16pt;
}

.question:after {
  border-color: #0b0b0b transparent;
}
div#bottom div{
background-color: rgb(40, 40, 40);
}

.shiny{
    border: 1px solid #848484;
    background: -moz-linear-gradient(top, #666666 0%, #575757 50%, #454545 51%, #373737 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #616161), color-stop(50%, #575757), color-stop(51%, #494949), color-stop(100%, #353535));
  background: -webkit-linear-gradient(top, #666666 0%, #575757 50%, #454545 51%, #373737 100%);
  background: linear-gradient(to bottom, #666666 0%, #575757 50%, #454545 51%, #373737 100%);
  background-image: linear-gradient(rgb(102, 102, 102) 0%, rgb(87, 87, 87) 50%, rgb(69, 69, 69) 51%, rgb(55, 55, 55) 100%);
  color: #EAEAEA;
}

.shiny:hover, .shiny:active, .shiny.on {
  background: #6F6F6F;
  background: -moz-linear-gradient(top, #335271 0%, #29425b 50%, #203346 51%, #162431 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #717171), color-stop(50%, #595959), color-stop(51%, #424242), color-stop(100%, #2D2D2D));
  background: -webkit-linear-gradient(top, #6E6E6E 0%, #565656 50%, #3F3F3F 51%, #343434 100%);
  background: linear-gradient(to bottom, #6F6F6F 0%, #545454 50%, #424242 51%, #2C2C2C 100%);
}

.action-box .section{
  border-top: 1px solid #565656;
}

.dropdown .dropdown-menu LI > A:hover, .dropdown .dropdown-menu LABEL:hover{
  background-color: #575757;
}

#blog a {
  color: #EFEFEF;
}

.reblog-box .controls span.on {
  color: #D7D7D7;
}

#nav .link {
  color: #C8C8C8;
}

#mini-logo{
color:#ABABAB;
background-color:#EFEFEF;
}

#top .link:hover {
  background-color: #222222;
}

.chat .dialogue li:nth-child(2n) {
  background-color: #5F5F5F;
}

#menus ul a:hover {
  background-color: #696969;
}

.action-box:before {
  border-bottom: 8px solid #898989;
}

.action-box:after {
  border-bottom: 6px solid #202020;
}
}

@-moz-document url("http://washboard.ws/rules"){
#content {
  background-color: black;
  color: white;
}

.row:nth-of-type(2n+1) {
  background-color: #4D4D4D;
}

input[type="text"], textarea {
  background-color: #2C2C2C;
  color: lightgrey;
}

.row.add .label, #importruleslink, #exportruleslink {
  color: #ECECEC;
}

.submit-button {
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #C3C3C3), color-stop(50%, #BBBBBB), color-stop(51%, #B2B2B2), color-stop(100%, #ABABAB));
  background: -webkit-linear-gradient(top, #BEBEBE 0%, #C0C0C0 50%, #B3B3B3 51%, #AEAEAE 100%);
  background: -o-linear-gradient(top, #799ec3 0%, #6b94bd 50%, #5d8ab6 51%, #4f7fb0 100%);
  background: -ms-linear-gradient(top, #799ec3 0%, #6b94bd 50%, #5d8ab6 51%, #4f7fb0 100%);
  background: linear-gradient(to bottom, #C0C0C0 0%, #B8B8B8 50%, #B3B3B3 51%, #747474 100%);
  border: 1px solid #C0C0C0;
}

.submit-button:hover {
  background: #CDCDCD;
  background: -moz-linear-gradient(top, #8eadcc 0%, #799ec3 50%, #648fb9 51%, #4f7fb0 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #CACACA), color-stop(50%, #C0C0C0), color-stop(51%, #B5B5B5), color-stop(100%, #ABABAB));
  background: -webkit-linear-gradient(top, #C7C7C7 0%, #C3C3C3 50%, #B6B6B6 51%, #B0B0B0 100%);
  background: linear-gradient(to bottom, #CFCFCF 0%, #BEBEBE 50%, #B6B6B6 51%, #ABABAB 100%);
}

.submit-button:active {
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #C3C3C3), color-stop(50%, #BBBBBB), color-stop(51%, #B2B2B2), color-stop(100%, #ABABAB));
  background: -webkit-linear-gradient(top, #BEBEBE 0%, #C0C0C0 50%, #B3B3B3 51%, #AEAEAE 100%);
  background: -o-linear-gradient(top, #799ec3 0%, #6b94bd 50%, #5d8ab6 51%, #4f7fb0 100%);
  background: -ms-linear-gradient(top, #799ec3 0%, #6b94bd 50%, #5d8ab6 51%, #4f7fb0 100%);
  background: linear-gradient(to bottom, #C0C0C0 0%, #B8B8B8 50%, #B3B3B3 51%, #747474 100%);
}

#links{
  color: #CDCDCD;
}

}
