@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org"),  domain("googleads.g.doubleclick.net") {
  
 html, body {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;	background-attachment: fixed !important;	background-clip: border-box !important;	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important;  font-family:Constantia!important} 
  .PoweredByVanilla {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/vanilla-forums%20red_zpsokq9zug5.png) no-repeat scroll 0% 0% transparent !important;background-size: contain !important;transform:scale(3.7,3.7) !important}
  /*.JsConnect-Connect   {background: url(http://savepic.org/7211417.png) left no-repeat  transparent !important;background-size: contain !important}
  .JsConnect-Connect .UserPhoto {opacity:.0!important}*/
  Sprite16 {background: rgba(0,0,0,.05)url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/UserstyleRed_zpsd9ecsvmf.png)!important;}
  .SpNotifications, .SpGlobe {background-position: -32px -84px!important}
  .SpInbox, .SpEnvelope {background-position: 0px -100px!important}
  .SpBookmarks, .SpStar {background-position: -80px -116px!important}
  .SpDashboard, .SpOptions, .SpCog {background-position: 0px -84px!important}
  a.Bookmark, a.Bookmarking {background: rgba(0,0,0,.05)url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/red-star_zpsm9iso9bh.png)!important;background-size: contain !important;transform:scale(1.4,1.4) !important}
  a.Bookmarked {background: rgba(0,0,0,.05)url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/red_star_zpsfuzxjabu.png)!important;background-size: contain !important;transform:scale(.8,.8) !important}
  a.Bookmark:hover {transform:scale(1.7,1.7) !important}
  a.Bookmarked:hover {transform:scale(1,1) !important}
  
  .more, #screenshots, .Flyout:after, .MenuItems hr  {border:none!important}
  .MenuItems a:hover, .edit-setting {background:transparent!important}
  #main-header, #Head  {background: linear-gradient(to top, rgba(155, 155, 155, 0.19) 55%, #000000 53%, rgba(0, 0, 0, 0.44) 120%)!important;background-color: transparent !important;border-radius: 10px !important;box-shadow: 0 0 34px #000 !important;}
  #left-sidebar > *  {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.75) 45%, rgba(155, 155, 155, 0.25) 100%)!important;border-radius:10px!important}
  blockquote.Quote, blockQuote.UserQuote  {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.55) 45%, rgba(44, 44, 44, 0.35) 100%)!important;border-radius:10px!important}
  #content-wrapper, #Panel, #Body .ContentColumn    {background: rgba(0, 0, 0,  0.9)!important; box-shadow: 0px 0px 5px rgba(155, 155, 155,  .9 ) !important;border-radius:10px!important}
  code, pre, .Item.Read {background:transparent!important;box-shadow: 0px 0px 5px rgba(155, 155, 155,  .9 ) !important;border-radius:10px!important;border:none!important}
  #search-terms, #search-submit, input.DateBox, input.InputBox, input.SmallInput, textarea, #style_short_description, #example-url, input, #style-settings select {background-color: rgba(0, 0, 0, 0.25)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(155, 155, 155, .9 ) inset!important;border: none !important;border-radius:5px!important}
  #search-terms:hover, #search-submit:hover, input.DateBox:hover, input.InputBox:hover, input.SmallInput:hover, textarea:hover, #style-settings select:hover{box-shadow: 0px 0px 5px rgba(155, 155, 155, .9 ) inset!important}
  .Button, .NavButton, span.Warning, div.Warning, .Info, .notice {background: rgba(0, 0, 0, 0.25)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(155, 155, 155, .9 ) inset!important;text-shadow: none !important;border: none !important;border-radius:7px!important}
  .install {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.75) 45%, rgba(155, 155, 155, 0.25) 100%)!important;border-radius: 20px !important}
  .install:hover, .MenuItems a:hover {box-shadow: 0 0 5px #3cf!important}
  .lb-outerContainer, .Tag, .HasNew {background: linear-gradient(to right bottom , rgba(0, 0, 0, 0.35) 45%, rgba(155, 155, 155, 0.05) 100%)!important}
  pre, .FlyoutMenu {background: rgba(0, 0, 0,  0.5)!important;border-color:#3ce!important}
  .FlyoutMenu, .MenuItems {background: rgba(0, 0, 0,  0.9)!important;border-color:#3cf!important}
.listing-no-screenshot {background: rgba(0, 190, 255,  0.2)!important}
section#obsoletion-message  {background-color: rgba(255, 0, 0,  0.05)!important}
    
  a, #show-advanced-search, body, .install-status, a.TextColor, a .TextColor, .Count, .Pager span, .Tag, .HasNew, dl.About dt, .DiscussionForm label, .FlyoutMenu, .MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {color:#3ce!important;text-decoration:none!important}
  p, .ItemDiscussion .Meta, .DataList .Meta, .Message small, #main-header>h1>a:hover, .PopList .Meta  {color:#39f!important;text-decoration:none!important}
  a:hover, a.TextColor:hover, a .TextColor:hover {color:#39f!important;text-decoration:none!important;text-shadow: 0 0 2px #3ce !important}
  h1,h2,h3,h4,h5,h6, #main-header>h1>a {color:#000!important;text-decoration:none!important;text-shadow: 0 0 2px #3ce !important}
  
 .ad-note, #left-sidebar .ad, #ads ul li, #adunit, #google_ads_frame1, #aswift_0, #aswift_0_anchor, #below-install-ad, #below-meta-ad, #browse-bottom-ad, #browse-middle-ad, #misc-bottom-ad{display:none!important}}
