css:
@-moz-document domain("tweakers.net") {
/* Een en ander positioneren */
#logo+.sitename {
  position: absolute;
  top: 0;
  left: -40px;
  width: 219px;
  padding-left: 1px;
  height: 44px;
  margin: 0;
  text-indent: -999px;
  overflow: hidden;
  z-index: 1;
}
#logo {
  position:absolute;
  top:0;
  left:-40px;
  z-index:200;
  height:44px
}
#menu .sitename+ul {
  height:44px;
  width:550px;
  margin-left:182px;
  position:relative;
  z-index:95
}
/* Oude V3 logo laden */
#logo a {
  display:block;
  width:203px;
  height:44px;
  padding:0 10px;
  background:url(https://web.archive.org/web/20150427215251if_/https://tweakimg.net/g/if/v3/framework/tweakers_logo.png) no-repeat 50%
}
}
