css:
@-moz-document domain(sync-video.com) {

/*SV dark style*/

/*version*/
.navbar .brand:hover .vmid::after {
  content: " - SV Dark v1.3";
}




/*global*/
body {
  font-family: 'Trebuchet MS';
  background: #000 url('http://i.imgur.com/vTJc6tL.jpg') top center no-repeat fixed !important;
  background-size: cover;
}

#wrap {
  background: rgba(0,0,5,.6) !important;
}

.navbar-inner {
  border: 0;
  box-shadow: none;
  filter: none;
  padding: 3px 0;
  background: linear-gradient(#347696,#255267);
  opacity: .6;
  transition: .2s;
}

.navbar-inner:hover {
  opacity: .95;
}

.navbar .brand:hover .vmid::after {
  color: rgba(255,255,255,.2);
  text-shadow: none;
  padding-bottom: 0px;
  padding-top: 5px;
}



/*buttons*/
.btn {
  box-shadow: none;
  text-shadow: none;
  border: 1px solid transparent;
  transition: .2s;
}

.btn.active, 
.btn:active {
  box-shadow: none;
}

.btn.hover, 
.btn:hover {
  border: 1px solid rgba(255,255,255,.3);
}




/*titles*/
h5.inline {
  color: #ddd !important;
}

#room-title-body {
  color: #f9a123 !important;
  text-shadow: 1px 1px 1px #222;
}
#title-area {
  height: auto;
  margin-bottom: 10px;
  background: rgba(255,255,255,.2);
  padding: 10px;
  border-radius: 5px;
}




/*chat*/
.chat {
  border: 0 !important;
  background: rgba(255,255,255,.05) !important;
  border-radius: 5px;
}

.chat .dialog {
  height: 500px !important;
  border-radius: 0 0 5px 5px;
}

.chat .dialog li {
  padding: 6px 10px;
  border-bottom: 1px dotted rgba(255,255,255,.2);
  line-height: 16px;
}

.chat .dialog li:nth-child(odd), 
.chat .dialog li:nth-child(even) {
  background: rgba(50,50,60,.4) !important;
}

.chat .timestamp {
  color: #aaa !important;
  float: right;
  padding: 0;
}

.chat .user {
  color: #f9a123 !important;
  padding: 0;
  display: inline-block !important;
}

.chat .user::after {
  content: "" !important;
}

.chat .user.muted::after {
  content: " *" !important;
}

.chat .user.muted:hover::after {
  content: " unregistered" !important;
  color: #999 !important;
  font-size: 11px;
}

.chat .msg {
  color: #eee !important;
  padding: 0 0 5px 10px;
}

.chat .info {
  color: rgba(255,255,255,.4) !important;
  font-size: 11px !important;
  font-style: normal !important;
  display: block !important;
}

.chat input {
  background: none !important;
  border: 0 !important;
  box-shadow: none !important;
}

.chat input:focus {
  border: 0 !important;
}

.input-msg {
  color: #444 !important;
  border: 0 !important;
  padding: 5px 10px !important;
}

.chat .input-msg-fix {
  background: linear-gradient(90deg,#fff,#bbb) !important;
  border-radius: 5px 5px 0 0;
}




/*player*/
.aspectwrapper {
  height: 200px !important;
  overflow: hidden;
}

#playertools {
  background: rgba(255,255,255,.2);
  border-radius: 5px;
  padding: 5px;
  color: #eee;
}




/*remove ads and footer*/
.playlist-left>div:last-child, 
#footer {
  display: none;
}

#current-video-name, 
#current-username {
  color: #f9a123 !important;
}




/*playlist*/
.playlist-left .mtop0 {
  color: #ddd;
}

#playlist {
  border-radius: 5px;
  max-height: 325px;
}

#playlist>li {
  background: rgba(255,255,255,.2);
  border-bottom : 1px dotted rgba(0,0,0,.3);
}

#playlist>li:last-child {
  border-bottom : 0;
}

#playlist>li.active {
  background-color: rgba(255,255,255,.5);
}

.span12.id {
  color: #eee;
}

.active .span12.id {
  color: #222;
}

/*playlist size*/
@media (min-width: 1200px) {
#playlist .row {
  margin-left: 0;
}

.row-fluid #playlist .span8 {
  width: 65%;
}

}

.row-fluid #playlist .span12.id {
  font-size: 11px;
  line-height: 15px;
}

#playlist>li>div>div.miniature {
  padding-left: 0;
  width: 40px;
}




/*config*/
#config-table {
  background: rgba(255,255,255,.2);
  border :0;
  border-radius: 5px;
  margin-bottom: 20px;
}

#config-table tr {
  height: 30px;
  color: #ddd;
}




/*user list*/
.playlist-left>div>h5, 
.playlist-left>h5 {
  margin: 0;
  height: auto;
}

#userlist {
  background: rgba(255,255,255,.05) !important;
  border-radius: 5px;
  padding: 10px;
  margin: 0;
}

.nick {
  color: #f9a123;
  font-size: 12px;
}

li.muted .nick {
  color: #887 !important;
}

#invite-button {
  display: none;
}




/*faq*/
.faq>.pytanie>h1 {
  color: #87BBD3;
}

.odpowiedz {
  color: #eee;
}




/*home*/
.sub-hero-unit {
  background: rgba(255,255,255,.8);
}

.hero-plus {
  background: rgba(240,240,220,.9);
  border: 0;
  text-shadow: none;
}

.hero-plus hr {
  border-top: 0;
  border-bottom: 1px solid #B9AC55;
}

.sub-hero-unit h5.inline {
  color: #222 !important;
}
  
}
