css:
@-moz-document domain("fanfiction.net"), domain("fictionpress.com") {
/*
STYLE AUTHOR: NHansen
STYLE URI: http://userstyles.org/styles/17556/
STYLE VERSION: 1.5.9
LAST UPDATE: MAR 26, 2014
*/
/*
STYLE AUTHOR: NHansen
STYLE URI: http://userstyles.org/styles/17556/
STYLE VERSION: 1.5.9
LAST UPDATE: MAR 26, 2014
*/

/************************************* 
HIDE ADVERTISEMENTS - Thanks, BullFrog
*************************************/  
#menu-extra-c,
DIV[style^="border-bottom"],
[id^="google_ads"],
[style="padding-left:1em;padding-right:1em;padding-top:0.5em;"] > DIV > TABLE[style="float:right"]:first-child, 
div#content_wrapper_inner > div.maxwidth,  
div#review > table[style="border=0 padding=0 cellspacing=0 style='width:100%;'"]:first-child  {
	display:none!important
	}

/************************************* 
BODY
*************************************/
* {
    text-shadow: none !important;
    }
body {
	background: #e5dbbc !important;
 	color: #774F38 !important;
	}

body div {
	background-color: transparent !important;
	color: #774F38 !important;
	}

div.sep {
	margin: 5px 0 5px 0 !important;
	border-bottom: 1px solid #774F38 !important;
	}

hr {
	background: #774F38 none !important;
	color: #F1D4AF !important;
	size: 1px !important;
	opacity: .2;
	}

b {
 	color: #E08E79 !important;
	}

/************************************* 
WRAPPERS
*************************************/


div#content_wrapper_inner {
    background-color: #ECE5CE !important;
    border-left: 1px solid #e5dbbc !important;
    border-right: 1px solid #e5dbbc !important;
    border-bottom: 1px solid #e5dbbc !important;
    }
       
.lc {
    background:#ECE5CE !important;
    border: 1px solid #d7c897 !important;
    }

.lc-wrapper:after{
    border-top:solid 1px #d7c897 !important;
    }

/************************************* 
BODY LINKS 
*************************************/

a:link, span.fake_link {
	color: #487c9a !important;
	text-decoration: none !important;
	border-bottom:0px solid #333 !important;
    transition: all 0.2s ease-in-out;
	}

a:visited {
	color: #E08E79 !important;
	text-decoration: none !important;
	border-bottom:0px solid #333 !important;
	}

a:hover, a:visited:hover, span.fake_link:hover { 
	color: #774F38 !important;
	text-decoration: none !important;
	border-bottom: 0px solid #fff !important;
	}

a:active { 
	color: #E08E79 !important;
	text-decoration:none !important;
	}

a[href='http://m.fanfiction.net/m/yes_mobile.php'] { 
	border: 0 !important;
	}

/*************************************
TOP MENU LINKS
*************************************/

.zui a:link {
	color: #774F38 !important;
	background: #ECE5CE !important;
	text-decoration: none !important;
	border: none !important;
    border-radius: 4px !important;
	padding: 5px 10px 5px 10px !important;
	}

.zui a:visited {
	color: #774F38 !important;
	background: #ECE5CE !important;
	text-decoration: none !important;
	border: none !important;
	}

.zui a:hover, .zui a:visited:hover { 
	color: #E08E79 !important;
	background: #ECE5CE !important;
	text-decoration:none !important;
	}

.zui a:active { 
	color: #ECE5CE !important;
	background: #9a6648 !important;
	text-decoration:none !important;
	}
	
li.divider {
	background: #F1D4AF !important;
	border: 0 !important;
	}
	
.dropdown-menu li {
	margin: 1px !important;
	}
	
ul.dropdown-menu {
	background: #774F38 !important;
	}
	
.btn, .btn.pull-right.icon-heart,  a:link.btn{
	background: #774F38 !important;
	color: #ECE5CE !important;
	border: 1px solid #774F38 !important;
	box-shadow: 0px 0px 0px !important;
    }

/*************************************
TOP MENU/NAVIGATION BARS
*************************************/

.zmenu {
	background-color: #774F38 !important;
	border-bottom: 1px solid #774F38 !important;
	border-top: 1px solid #774F38 !important;
	padding: 0px !important;
	}

.menu-child {
	background-color: #774F38 !important;
	border-bottom: 1px solid #774F38 !important;
	}

.zhide {
	display:none;
	}
	
div[style='width:100%;border-bottom:1px solid #CDCDCD;background-image:url("http://ffcdn2012.fictionpressllc.netdna-cdn.com/static/images/adbg.gif");'] {
    display:none !important;
    }

/* for fictionpress.com */
table.xxmenu {
	background-color: #774F38 !important;
	border-bottom: 1px solid #774f38 !important;
	}
	
/*************************************
INPUTS (DROP-DOWNS, SELECTION, TEXTAREAS, ETC.)
*************************************/

select, input {
	background: #845842 !important;
	color: #ECE5CE !important;
	border: 1px solid #774F38 !important;
	-moz-appearance: none !important;
	margin-right: 3px!important;
	}

input.searchfield {
	border: 1px solid #774F38 !important;
	}

textarea {
	background: #774F38 !important;
	color: #ECE5CE !important;
	border: 1px solid #774F38 !important;
	-moz-appearance: none !important;
	}

::-webkit-input-placeholder {
    color: #ded2a9 !important;
	}

select:hover, select:focus, input:hover, input:focus, textarea:hover, textarea:focus {
	border: 1px solid #774F38 !important;
	}

/*************************************
TABLE COLORING/FIXES
*************************************/

.tborder, .trowh {
	background: #ECE5CE !important;
	border: 0px solid #2D2D2D !important;
	}

.tcat {
	background: #F6F7EE !important;
	border: 1px solid #F3EFE0 !important;
	border-bottom: 0px solid !important;
	}

.tcat a:link, .tcat a:visited {
	color: #F1D4AF !important;
	}

.tcat a:hover, .tcat a:active {
	color: #845842 !important;
	}

.thead {
	background: #F6F7EE !important;
	color: #C65B45 !important;
	}

.myborder {
	background: #774F38 !important;
	border: 1px solid #F1D4AF !important;
	padding-bottom: 5px !important; 	color: #F6F7EE !important;
	}

.myborder.menulinks {
	background: #F1D4AF !important;
	border: 1px solid #774F38 !important;
	padding-bottom: 0px !important;
	}

iframe {
	border: 0px !important;
	}

#list_output table tr td {
	border-style: solid  !important;
 	border-color: #ECE5CE !important;
	border-width: 1px 1px 1px 3px !important;
	padding-left: 5px !important;
	background: #ECE5CE !important;
	}
	
td[style="width:260px;vertical-align:top;padding:5px;border: solid 1px #6581C1;-moz-box-shadow: -1px 0px 2px #cdcdcd;-webkit-box-shadow: -1px 0px 2px #cdcdcd;box-shadow: -1px 0px 2px #cdcdcd;"] {
    border: solid 1px #F1D4AF !important;
    -moz-box-shadow: 0px 0px 0px !important;
    -webkit-box-shadow: 0px 0px 0px !important;
	box-shadow: 0px 0px 0px !important;
	background-color: #F1D4AF !important;
	}
 
.table-bordered {
    border: solid 0px #F1D4AF !important;
    }
    
.table-bordered td {
    border: 0 !important;
    }
    
th {
    border-top: 1px solid #F1D4AF !important;
    border-left: 0 !important;
    border-radius: 1px !important;
    }
    
#gui_table1i td, td#gui_table1i.table  , .z-list:hover {
    border: 0 !important;
    background-color: #F6F7EE !important;
    }

.z-list{transition: all 0.2s ease-in-out;}
    
#gui_table2i {
    background-color:#F6F7EE !important;
    }

#gui_table3i td {
    border: 0 !important;
    }

.table-striped tbody>tr:nth-child(even)>td,.table-striped tbody>tr:nth-child(even)>th{
    background-color:#F6F7EE !important;
    }

#bio_tbl {
    background:#ECE5CE !important; 
    border: 0 !important;
    }

span.lc-left {
    background: #ECE5CE !important;
    }

.z-list.zhover.zpointer:hover {
  background-color: #ECE5CE !important;
}

/* profile tabs */
    
#l_st, #l_fs, #l_fa, #l_cc {
    background-color: #ECE5CE !important;
    border-color: #774F38 !important;
    }    

.favstories[style='min-height:77px;border-bottom:1px #cdcdcd solid;'], .mystories[style='min-height:77px;border-bottom:1px #cdcdcd solid;'], #mytab {
    border-bottom: 1px #774F38 solid !important;
    }
    
span.badge {
    background-color: #ECE5CE !important;
    color: #774F38;
    font-size:80%;
    }
    
div[style='font-size:0.9em;color:gray;'] > span.gray {
    color: #E08E79 !important;
    cursor: pointer;
    border-bottom: 1px solid #ECE5CE;
    }

div[style='font-size:0.9em;color:gray;'] > span.gray:hover {
    color: #C5E0DC !important;
    }

/*************************************
BORDERS
*************************************/

.myborder_no_bottom, .myborder_only_bottom, .myborder_only_leftbottom {
	border-style: solid !important;
	border-color: #F3EFE0 !important;
	}

.myborder_only_bottom.grayback {
	background-color: #F6F7EE !important;
	}

img.cimage {
    border: 1px solid #F3EFE0 !important;
    }

dl>img.cimage.round36,dl>img.lazy.cimage{width:5% !important; height:5% !important; margin-top:-5px !important;}

.bubbledNNormal>img.lazy{width:4% !important; height:4% !important;}
.bubbledNNormal {padding-bottom:8px !important;}

 
#xtab ul#primary a {
    border: 1px solid #774F38 !important;
    border-bottom: 1px solid #774F38 !important;
    background-color: #845842 !important;
    }

#xtab ul#primary a.current {
    border-bottom: 1px solid #F1D4AF !important;
    background-color: #F1D4AF !important;
    }

#xtab ul#primary a:hover {
    border-bottom: 1px solid #9A6648 !important;
    background-color: #774F38 !important;
    }
    
div[style='border:1px solid #666;padding-top:5px;'] {
    background-color: #F1D4AF !important;
    border: 1px solid #F1D4AF !important;
    }

/*************************************
LOGIN AREA + ALTERNATING BACKGROUNDS
*************************************/

.topnav {
	border: 0px #774F38 solid !important;
	}

ul.topnav li a {
	border-bottom: 0px solid #111 !important;
	color: #E08E79 !important;
	}

ul.topnav li a:hover {
	background-color: #ECE5CE !important;
    border-bottom:0px solid #222 !important;
	color: #774F38 !important;
	text-decoration:underline !important;
	}

ul.topnav ul li a {
	border-bottom: 0px solid #111 !important;
	color: #E08E79!important;
	}

ul.topnav ul li ul li a {
	border-bottom: 0px solid #111 !important;
	color: #E08E79 !important;
	}

ul.topnav ul li a:hover, ul.topnav ul li.active, ul.topnav ul ul li a:hover {
	background-color: #F3EFE0 !important;
	}

.table-striped tbody>tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color:#F3EFE0 !important
    }
    
#gui_table1i tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color:#F3EFE0 !important;
    border:0px solid !important;
    }

.fhigh {
	background: #e5dbbc !important;
	}
	
.xtabc, .admintabs a{
	border-color: #2D2D2D !important;
	}

.admintabs a:hover, .admintabs a.selected, td[bgcolor="#f0f0f0"] {
	background: #E5DBBC !important;
	}

a.positive {
	border: 1px solid #9A6648 !important;
	}

div.panel_normal, div.panel_warning, div.panel_success {
	border: 1px solid #e5dbbc !important;
	background-color: #F3EFE0 !important;
	color: #774F38 !important;
	}

span.gui_warning, span.gui_success {
	color: #774F38 !important;
	}

div.panel_normal hr, div.panel_warning hr, div.panel_success hr {
	background-color: #966eb4 !important;
	color: #966eb4 !important;
	}

/*************************************
TABLE SPACING
*************************************/

table#gui_table15i,table#gui_table14i,table#gui_table13i,table#gui_table12i,
table#gui_table11i,table#gui_table10i,table#gui_table9i,table#gui_table8i,
table#gui_table7i,table#gui_table6i,table#gui_table5i,table#gui_table4i,
table#gui_table3i,table#gui_table2i,table#gui_table1i {
	border-spacing: 0px !important;
	border: 0px solid #2D2D2D !important;
	-moz-appearance: none !important;
	border-style: solid !important;
	}

.x-high, .z-high {
	background: #774F38 !important;
	}

/*************************************
REVIEW BUTTON CUSTOMIZATION
*************************************/

.xinput_style {
	color: #ece5ce !important;
	border: solid 0px #2D2D2D !important;
	background-color: #774F38 !important;
	background: -webkit-gradient(linear, left top, left 30, from(#774F38), color-stop(4%, #774F38), to(#774F38)) !important;
	background: -moz-linear-gradient(top, #774F38, #774F38 1px, #774F38 30px) !important;
	}

.share_link a {
	color: #774F38 !important;
	}
	
div.toast {
    background: rgba(236,229,206,.9) !important;
    border-color: #774F38 !important;
    color: #774F38 !important;
    }

div.modal-body, div.modal-footer {
    background: rgba(236,229,206,.9) !important;
    border: 0px solid #d7c897 !important;
    color: #aaa !important;
    }
    
div.modal-body td { 
    color: #774F38 !important;
    }
    
label.checkbox {
    color: #774F38 !important;
    margin-bottom: 5px !important;
    }
    
table[style='padding:6px;color:black !important;'] {
    color: #774F38 !important;
    }
    
a.xdrop_login:hover, ul.dropdown-menu a:hover {
    background: #774F38 !important;
    border-bottom: 0px solid #444 !important;
    }
    
/************************************* 
FAKE LINKS FIX 
*************************************/
 
div.simplemodal-container {
    background-color: #774F38 !important;
    border: 0px solid #2d2d2d !important;
    }
    
div.simplemodal-overlay {
    background-color: #774F38 !important;
    }

/************************************* 
MISC FIXES 
*************************************/

.gray {
	color: #c9b572 !important;
	font-style: italic !important;
	}

.bm {
	border-bottom: 1px solid #f3efe0 !important;
	}

.drop_m {
	border-bottom: 1px solid #774F38 !important;
	border-top: 1px solid #774F38 !important;
	}

div[style="height:10px;border-top:1px solid #cdcdcd;"] {
	border-top: 1px solid #774F38 !important;
	margin-top: 5px !important;
	margin-bottom: 5px !important;
	}

span.sort-sort {
    color: #774F38 !important;
    border-bottom:1px solid #774F38 !important;
    }
    
.lc > span#bio_text {
    cursor: pointer;
    }

.xcontrast_txt, #live_counter {
    color:#386077 !important;
}

#myform .modal-body {color:#774F38 !important;}

span.btn.pull-right.btn-primary, button.btn.btn-block.pull-right, btn.pull-right{color:#ece5ce !important;}

	.storytext {
		line-height: 1.5;
		width: 50%;
		max-width: 600px;
        min-width:500px;
		font-family: 'PT Serif', serif !important;
		font-size: 16px;
		text-align: justify;
        background-color:#f3efe0!important;
        padding: 25px 50px 15px 50px !important;
        box-shadow: inset 0px 0px 10px 2px rgba(18,13,9,1);
        color:#090604 !important;
	}

	.storytext p {
    	text-indent: 1.75em;
	}


	#storytextp,div#storytextp.storytextp {
		clear: both;
        background-color:#1a110c !important;
	}

#ffto-menu-wrapper {background-color: #F6F7EE !important;	box-shadow: 0px 2px 15px !important;}


/* This changes the css from their current setting of none to the normal setting of auto. */
    DIV#storytext.storytext.xcontrast_txt.nocopy,.storytextp {
        -webkit-touch-callout: auto !Important;
        -webkit-user-select: auto !Important;
        -khtml-user-select: auto !Important;
        -moz-user-select: auto !Important;
        -ms-user-select: auto !Important;
        user-select: auto !Important;
    }

.trans {color: #f3efe0 !important;}

input[type="file"], input[type="radio"], input[type="checkbox"] {
    margin: 2px 0 0 !important;}

input[type="file"] {padding:8px 0px 0px 8px !important;}
/*************************************
Firefox 3 Top Menu Fix - DO NOT DELETE! DO NOT DELETE! DO NOT DELETE! DO NOT DELETE!
*************************************/

div.menu-child form {
	width: 50% !important;
	}

/*************************************
ExtensionFanfiction.net Story Parser
*************************************/

.ui-dialog .ui-dialog-content{
	background: #e5dbbc !important;
 	color: #774F38 !important;
	}

.ui-widget-content,.ui-widget-header,.ffnet_Config_Button {
  border: 0px solid #e5dbbc !important;
  background:#e5dbbc !important;
  color: #774F38 !important;
} 
}

@-moz-document url("http://www.fanfiction.net/"), url("https://www.fanfiction.net/"), url("http://www.fanfiction.net/#"), url("https://www.fanfiction.net/#") {
#gui_table1i tr:nth-child(odd)>td {
    background-color:#F6F7EE !important;
	}
}
