@namespace url(http://www.w3.org/1999/xhtml);

/*LastUpdate: 20181107 By special-feng@QQ.com*/

@-moz-document regexp("http(:|s:)//(tieba|xingqu)\.baidu\.com\/f\?.*kw=.*"),regexp("http(:|s:)//\www\.tieba\.com\/f\?.*kw=.*") {
	/*版面*/
	
	.split,.u_appcenterEntrance,.u_member,.u_split,.u_bdhome {
		display: none;
		/*隐藏顶部右上角分隔线、应用中心、会员官网、百度首页链接模块*/
	}

	/*---------------------------------------------背景相关---------------------------------------------*/	
	
	#topic_post_thread,.thread_top_list_folder,.forum_foot,.j_thread_list,.thread_list_bottom {
		background: none !important;
		/*透明化需要*/
	}

	.head_inner {
		background: transparent url("http://tb2.bdstatic.com/tb/static-pfrs/img/frs_bright/images_bmg/f_editor_94fd854.jpg") repeat scroll 0% 0%;
		background-color: #F5F7FA;
	}

	.wrap2 {
		background: transparent url("http://tb2.bdstatic.com/tb/static-pfrs/img/frs_bright/images_bmg/f_editor_94fd854.jpg") repeat scroll 0% 0%;
		background-color: #F5F7FA;
		/*主体背景颜色*/
	}

	/*---------------------------------------------布局相关---------------------------------------------*/	

	
	#head {
		height: 30px;
		/*顶部导航栏区域高度*/
	}
	
	#head a {
		font-weight: 600;
		text-decoration: none;
		/*导航栏字体加粗、链接无下划线*/
	}
	
	.search_nav,.search_nav b {
		color: #F92664;
		/*“贴吧”文字颜色，第二个选择器为新田惠海等吧独有*/
	}
	
	.search_nav {
		float: none !important;
		width: 560px !important;
		margin: 0 auto !important;
		/*导航栏居中，宽度包含签到按钮*/
	}
	
	.header_divider {
		display: none;
		/*隐藏搜索栏之下分隔线*/
	}
	
	.search_top {
		padding-bottom: 0px;
		margin-bottom: 0px;
		border: none;
		/*导航栏下侧间隔*/
	}
	
	.head_inner .search_logo {
		width: 108px;
		height: 36px;
		/*LOGO大小*/
	}
	
	#search_logo_large {
		position: absolute;
		top: 26px;
		/*“回贴吧首页”LOGO位置*/
	}
	
	.search_form_fixed .senior-search-link {
		margin-left: 8px;
		/*滚动时固定标题栏的“高级搜索”位置*/
	}
	
	.search_bright .search_ipt {
		height: 22px;
		/*修复新版搜索栏高度超出*/
	}
	
	.search_bright .search_btn {
		margin-left: 6px;
		height: 30px;
		line-height: 30px;
		/*修复新版搜索按钮遮盖及高度超出*/
	}

	#wd1 {
		background-color: #FFF;
		border: 1px solid #2D64B3 !important;
		/*搜索栏背景颜色*/
	}

	.search_bright .search_ipt {
		background: none;
		/*去除搜索框阴影*/
	}
	
	.forum_header,.official_head {
		margin-top: 36px;
		/*修复Chrome下搜索框被头图遮盖的问题*/
	}
	
	.card_banner,.head_banner {
		display: none;
		/*屏蔽头图*/
	}
	
	.card_top_theme .card_head, .card_top_theme .card_head_img,.card_top_theme1 .card_head, .card_top_theme1 .card_head_img,.card_head_img {
		height: 60px;
		width: 60px;
		/*本吧头像大小*/
	}
	
	.card_head {
		left: 90px;
		top: -20px;
		/*本吧头像位置*/
	}
	
	.card_top_theme {
		margin-top: 36px !important;
		/*解决屏蔽头图后Chrome下框架上移*/
	}
	
	#plat_recom_carousel {
		display: none;
		/*屏蔽头图，解决广告过滤后留下的空白*/
	}
	
	div[id$="head_content_middle"] {
		display: none !important;
		/*屏蔽视频推广模块*/
	}
	
	.app_forum_top {
		margin-top: 32px !important;
		padding-top: 10px !important;
		/*修复“应用中心”吧顶部错位及搜索框无法点击*/
	}
	
	#game_live_widget .game_live_body,#barrage_screen_head .barrage_screen_head_body,#forum_not_exist {
		margin-top: 40px;
		/*游戏直播、弹幕视频框架、贴吧暂不开放提示*/
	}

	li.j_tbnav_tab:last-child {
		margin-left: 10px;
		padding: 0 10px;
		/*签到按钮*/
	}

	.content,.content_top,.plat_skin .content,.head_main .head_middle, .head_main .head_content,.head_content {
		width: 92% !important;
		max-width: 1440px;
		min-width: 980px;
		border: none;
		/*主体内容宽度，去除边框。修复如“100度吧”等宽屏未适配，修复“应用中心”吧顶部导航栏宽屏未适配。*/
	}

	.plat_forum_intro {
		width: calc(100% - 560px);
		/*部分贴吧的简介区域 例：100度吧*/
	}

	#top_activity .activity_info {
		width: calc(100% - 285px);
		/*贴吧活动简介区域*/
	}

	.threadlist_bright .thread_pic_bright {
		width: calc(100% - 40px);
		border-right: none;
		/*图片话题框架及右边白线*/
	}
	
	.threadlist_bright .imgtopic_album {
		width: calc(100% - 120px);
		/*图片话题展示框架*/
	}
	
	.threadlist_bright .thread_pic_bright .pic_thread_main {
		width: calc(100% - 70px);
		/*图片话题幻灯片区域*/
	}

	.j_threadlist_piclist_detail > div:nth-child(1),.threadlist_bright .threadlist_media {
		width: 100%;
		/*图片话题区域*/
	}

	.live_thread_cnt {
		width: 98% !important;
		/*今日话题框架*/
	}

	.live_item_wrapper,.live_item_cnt {
		width: 100% !important;
		/*今日话题内容区域鼠标悬停*/
	}

	.live_item_contents {
		width: calc(100% - 149px) !important;
		/*今日话题贴子内容区域，宽度为100%-头像边框1px*2-头像内边距1px*2-头像宽度-头像与贴子内容间距-手机客户端文字宽度*/
	}

	.edui-container,#j_ed_container,.tt_guidetexture {
		width: 100% !important;
		/*今日话题文本框*/
	}

	#contet_wrap,.forum_content .main {
		width: calc(100% - 310px) !important;
		min-width: 739px;
		/*主题区域宽度*/
	}

	#content_leftList {
		width: calc(100% + 70px);
		min-width: 739px;
		/*贴子标题及作者宽度*/
	}

	.threadlist_li_left {
		min-width: 70px;
		/*回帖计数区域*/
	}

	.threadlist_li_right {
		width: calc( 100% - 70px) !important;
		/*主题+简介区域*/
	}

	.threadlist_title {
		width: calc( 100% - 165px) !important;
		min-width: 460px !important;
		/*贴子标题区域*/
	}
	
	.threadlist_text {
		width: calc( 100% - 245px) !important;
		min-width: 690px !important;
		/*贴子简介区域，100%-20px-70px-155px*/
	}

	.icon_author {
		margin-right: 3px;
		/*增加作者图标后间距，解决文字对齐问题*/
	}
	
	.frs-author-name {
		max-width: 87px !important;
		/*楼层作者*/
	}
	
	.threadlist_bright .frs_bright_icons {
		display: none;
		/*屏蔽各类印记，整洁版面*/
	}
	
	.threadlist_abs_onlyline {
		width: 100% !important;
		/*增加贴子内容简介宽度*/
	}

	#aside {
		min-width: 240px;
		background: none;
		/*侧边个人信息栏*/
	}

	.pager .first {
		margin-left: 220px;
		/*翻页按钮位置*/
	}

	.forum_content {
		background: none;
		border-bottom: none;
		/*去除背景和底部分隔线*/
	}

	.tbui_aside_float_bar {
		left: auto !important;
		right: 0.5%;
		/*侧边导航栏*/
	}
}

/*---------------------------------------------分隔线 By Judgelight @ 20150427---------------------------------------------*/

@-moz-document regexp("http(:|s:)//(tieba|xingqu)\.baidu\.com\/(p\/|f\?.*(kz=|ct=)).*"),regexp("http(:|s:)//www\.tieba\.com\/(p\/|f\?.*(kz=|ct=)).*") {
	/*贴子*/
	
	.split,.u_appcenterEntrance,.u_member,.u_split,.u_bdhome {
		display: none;
		/*隐藏顶部右上角分隔线、应用中心、会员官网、百度首页链接模块*/
	}
	
	.search_nav {
		float: none !important;
		width: 450px !important;
		margin: 0 auto !important;
		/*导航栏居中*/
	}
	
	.search_top {
		margin-bottom: 0px;
		border: none;
		/*去除搜索栏导航栏下方下边距和分隔线*/
	}
	
	.search_nav b {
		color: #F92664;
		/*“贴吧”文字颜色*/
	}
	
	.search_main {
		padding-bottom: 2px;
		/*搜索栏下方外边距*/
	}

	#head {
		height: 30px;
		/*顶部导航栏区域高度*/
	}

	#head a {
		text-decoration: none;
		/*导航栏链接无下划线*/
	}

	.head_inner {
		background-color: #FFF;
		/*搜索栏区域背景颜色*/
	}

	.plat_skin_default .content {
		width: 92% !important;
		max-width: 1340px;
		/*带贴吧头部的贴子页面*/
	}

	#tb_header_search_form {
		z-index: 999 !important;
		/*解决因内容框架上移造成的搜索区域无法点击的问题*/
	}

	.card_top_theme2 {
		padding-top: 20px;
		/*修复Chrome下吧名错位*/
	}
	
	.card_title {
		margin-top: 17px;
		/*修复Chrome下标题偏上的问题*/
	}
	
	.card_top_right {
		margin-top: 53px;
		/*已关注时的签到按钮*/
	}
	
	.l_container {
		clear: both !important;
		/*修复部分贴子吧名区域错位*/
	}

	.star_header_left {
		width: 50%;
		margin-top: 20px !important;
		/*修复部分贴吧贴子带精品模块展示造成的吧名错位*/
	}

	.star_header_right {
		margin-top: 20px;
		/*未关注时的签到按钮*/
	}

	.plat_header {
		background: none;
		/*带贴吧头部区域*/
	}

	.plat_forum_intro {
		width: calc(100% - 560px);
		/*带贴吧头部的贴子的贴吧简介*/
	}

	#container {
		width: 92% !important;
		min-width: 1340px;
		/*主体内容框架*/
	}

	.content {
		width: 92%;
		/*主体内容区域*/
	}

	#tb_nav,#thread_theme_5,#pb_content,.core_reply_border_top,.core_reply_wrapper,#j_editor_for_container,.lzl_panel_wrapper,.edui-container,.pb_footer,#thread_theme_7 {
		width: 100% !important;
		/*模块导航栏、回帖计数、内容主体框架、标题栏、楼中楼主题框架上部边线、回复框及文本框、底部区域*/
	}

	#j_core_title_wrap {
		width: 100%;
		/*贴子标题区域*/
	}
	
	.core_title_txt {
		width: calc(100% - 254px) !important;
		/*贴子标题文字*/
	}

	.core_title_absolute_bright {
		width: calc(100% - 534px) !important;
		min-width: 988px;
		/*滚动时的顶部固定标题栏*/
	}

	.left_section {
		width: calc(100% - 240px);
		min-width: 980px;
		/*贴子作者+内容区域*/
	}

	.d_author_anonym {
		min-width: 130px;
		/*楼层作者*/
	}
	
	.sofa_post_bright {
		width: 100%;
		/*沙发楼层框架*/
	}
	
	.sofa_post .d_post_content_main {
		width: calc(100% - 130px) !important;
		/*沙发楼层内容区域*/
	}

	.l_post_bright {
		width: 100%;
		min-width: 987px;
		/*贴子内容区域*/
	}

	.d_post_content_main {
		width: calc(100% - 150px) !important;
		min-width: 588px;
		/*贴子正文区域，宽度100%-作者区域宽度130px-贴子左侧内边距宽度20px*/
	}
	
	.hot_thread_body_contain {
		width: 100%;
		/*热门回帖区域框架*/
	}
	
	.delete_l_post {
		width: calc(100% - 27px) !important;
		/*热门回帖文字区域*/
	}

	.user-hide-post-up,.user-hide-post-down,.user-hide-post-action {
		right: 24px !important;
		opacity: 0.7;
		/*“对我隐藏该用户的所有贴子3天”按钮，同时增加透明属性，防止楼层正文内容遮盖*/
	}

	.pb_content {
		background: none;
		/*翻页后底部白色背景去除*/
	}
	
	.showlist_wap {
		display: none;
		/*右侧“热门直播榜”*/
	}

	#selectsearch-icon {
		z-index: 9999;
		/*选中文字后出现的悬浮“搜索”按钮*/
	}

	.tbui_aside_float_bar {
		left: auto !important;
		right: 0.5%;
		/*侧边导航栏*/
	}
}

@-moz-document regexp("http(:|s:)//tieba\.baidu\.com/hottopic\/.*") {
	/*贴吧热议*/
	
	.search_nav {
		float: none !important;
		width: 500px !important;
		margin: 0 auto !important;
		/*导航栏居中*/
	}
	
	.search_nav a {
		text-decoration: none !important;
		/*链接去下划线*/
	}
	
	.search_nav b {
		color: #F92664;
		/*“贴吧”文字颜色*/
	}
	
	.topic-wrapper {
		width: 92% !important;
		max-width: 1440px !important;
		/*主框架*/
	}
	
	#head,.search_top,.topic-bg {
		background: transparent url("http://tb2.bdstatic.com/tb/static-pfrs/img/frs_bright/images_bmg/f_editor_94fd854.jpg") repeat scroll 0% 0%;
		background-color: #F5F7FA;
		/*修改背景图*/
	}
	
	.search_top {
		border: none;
		margin-bottom: 0px;
		/*去除分隔线及多余间隔*/
	}
	
	.search_main {
		padding-bottom: 0px;
		/*去除多余间隔*/
	}
	
	.topic-header-img {
		height: 3px !important;
		/*背景图区域*/
	}
	
	.topic-header-img > img:nth-child(1) {
		display: none !important;
		/*屏蔽图片*/
	}
	
	.topic-desc {
		width: 100% !important;
		/*简介*/
	}
	
	.main {
		width: calc(100% - 262px) !important;
		/*主题列表区域*/
	}
	
	.right-aside {
		width: 250px;
		/*右边栏*/
	}
	
	#post-chandelier {
		width: calc(100% - 40px) !important;
		/*“发表贴子”*/
	}
	
	.thread-item > .center {
		width: calc(100% - 250px) !important;
		/*贴子标题*/
	}
	
	.author-info > .icon-text {
		width: 90px !important;
		/*贴子作者*/
	}
	
	.thread-item .last-reply-man .last-username {
		width: 120px !important;
		/*最后回复*/
	}
	
	p.last-username {
		margin-right: 2px !important;
		/*最后回复框架*/
	}
	
	a.j_user_card.icon-text {
		width: 100px !important;
		/*最后回复作者*/
	}
	
	.tbui_aside_float_bar {
		left: auto !important;
		right: 0.5%;
		/*侧边导航栏*/
	}
}
