css:
@-moz-document domain("play.pokemonshowdown.com") {
  body {
    background: url("http://fc06.deviantart.net/fs71/i/2013/043/0/5/pokemon__serperior_2_by_mark331-d5uobu8.jpg") !important;
    background-size: cover;
  }
  
  .ps-room.ps-room-light {
    background: rgba(0, 0, 0, 0);
    border-left: none;
    color: #212121;
  }
  
  .ps-room.ps-room-light.scrollable, .ps-room.ps-room-opaque {
    background: rgba(210, 212, 209, 0.4);
  }
  
  .header, .maintabbarbottom, .userlist, .chat-log-add {
    background: rgba(210, 212, 209, 0.4);
  }
  
  .header .logo {
    margin: 8px 0px 0px 2px;
  }
  
  .userbar {
    padding: 5px;
    border: ridge #7A9778 4px;
    box-shadow: 0px 0px 5px #151515;
    background: #7A9778;
    top: 8px;
  }
  
  .userbar button.icon {
    border-radius: 5px;
    border: 1px solid #4D674A;
    background: url("http://i.imgur.com/6b87xk5.png?1");
    box-shadow: 0px 0px 2px #7A9778;
  }
  
  .userbar button.icon:hover {
    background: #98D199;
    box-shadow: 0px 0px 2px #7A9778;
  }
  
  .userlist {
    border-right: none;
    left: 1px;
  }
  
  .userlist li em.group {
    color: #212121;
  }
  
  .userlist li {
    border-bottom: 1px solid #707070;
  }
  
  .userlist li button {
    background: #D2D4D1;
  }
  
  .chat-log-add {
    border-top: none;
  }
  
  .chat-log {
    bottom: 39px;
    background: rgba(210, 212, 209, 0.7);
  }
  
  .maintabbarbottom {
    border: none;
    box-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #000;
    background: rgba(255, 255, 255, 1);
    box-shadow: none;
    border-color: #000 #000 -moz-use-text-color;
    border-bottom: 1px solid #000;
    border-radius: 5px;
    width: 90px;
  }
  
  .tabbar a.button {
    border: 1px solid #212121;
    border-radius: 5px;
    margin-left: 5px;
    background: rgba(255, 255, 255, 0.45);
    text-shadow: none;
    color: #212121;
    width: 70px;
  }
  
  .tabbar a.button:hover {
    background: rgba(255, 255, 255, 0.7);
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 5px;
  }
  
  .tabbar li:first-child a.button {
    border-top-left-radius: 5px;
  }
  
  .tabbar, .maintabbar {
    position: absolute;
    bottom: 1px;
    left: 0px;
    right: 0px;
    display: block;
    list-style: outside none none;
    margin: 0px;
    padding: 0px;
    text-align: left;
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
    font-size: 10pt;
    line-height: 100%;
    height: 38px;
  }
  
  .mainmenuwrapper {
    opacity: 0.8;
  }
  
  .mainmenuwrapper:hover {
    opacity: 1;
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0);
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    transition:All 1.6116s ease;  
    text-shadow: none;
    background: #84A182;
    border: 1px solid #557455;
    color: #212121;
    box-shadow: 0px 0px 3px #98B597;
  }

  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    background: #D2D4D1;
    color: #212121;
    text-shadow: none;
    border-color: #212121;
    transition:All 1.6116s ease;  
  }

  .select {
    transition:All 1.6116s ease;  
    border: 1px solid #557455;
    background: #D2D4D1;
    color: #212121;
    box-shadow: 0px 0px 3px #98B597;
  }
  
  .select:hover {
    background: #EAE6DA;
  }
  
  .select:disabled, .select:disabled:hover {
    background: #EAE6DA;
  }
  
  .select::after {
    color: #212121;
  }
  
  .mainmenufooter {
    padding: 5px;
    border: ridge #7A9778 4px;
    box-shadow: 0px 0px 5px #151515;
    background: #7A9778;
    color: #212121;
  }
  
  .mainmenufooter a {
    color: #212121;
    text-shadow: none;
    font-weight: bold;
  }
  
  .roomlist a.ilink {
    background: rgba(255, 255, 255, 0.7);
    border: 1px solid #557455;
    color: #557455;
    text-shadow: none;
    box-shadow: 0px 0px 5px #98B597;
  }

  .roomlist a.ilink:hover {
    background: rgba(255, 255, 255, 0.87);
    border: 1px solid #557455;
    color: #557455;
    box-shadow: 0px 0px 5px #98B597;
  }
  
  .pm-window {
    box-shadow: 0px 0px 5px #557455;
    border-radius: 4px;
  }
  
  .pm-window h3, .pm-window h3:hover {
    background-color: rgba(210, 212, 209, 0.5);
    border-color: #557455;
    color: #212121;
    background: url("http://i.imgur.com/7QS4Sn0.png?1") top right no-repeat;
  }
  
  .pm-window.focused h3, .pm-window.focused h3:hover {
    background: url("http://i.imgur.com/7QS4Sn0.png?1") top right no-repeat;
    border-color: #212121;
  }
  
  .pm-log {
    background: #EEEEEE;
    border-color: #557455;
  }
  
  .pm-log-add {
    background: rgba(210, 212, 209, 0.5);
    border-color: #557455;
  }
  
  .pm-window.focused .pm-log, .pm-window.focused .pm-log-add {
    border-color: #212121;
  }
  
  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #212121;
  }
  
  .pm-window h3 .minimizebutton:hover {
    color: #E7C432;
  }
  
  .pm-window h3 .closebutton:hover {
    color: #99472F;
  }
  
  /*Popup css*/
  
  .popupmenu h3 {
    color: #4E6B4D;
  }
  
  .ps-popup {
    box-shadow: 0px 0px 5px #557455;
    border-color: #557455;
    background: #D2D4D1;
    border-radius: 5px;
  }
  
  /*Popup css ~end*/
  
  /*Battle css*/
  
  .battle-log-add, .battle-log {
    background: transparent;
    border: none;
  }
  
  .rightbar, .leftbar {
    background: rgba(210, 212, 209, 0.4);
    border-color: #212121;
  }
  
  /*Battle css ~end*/
  
  /*Textboxes css*/
  
  .textbox, .setchart input, .setchart-nickname input, .statform input.inputform, .setdetails, .setstats {
    border: 1px solid #557455;
  }
  
  .textbox:hover, .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setdetails:hover, .setstats:hover {
    border: 1px solid #212121;
  }
  
  .textbox:focus, .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .setdetails:focus, .setstats:focus {
    outline: 0px none;
    border: 1px solid #557455;
    box-shadow: 0px 1px 2px #C3BCB4 inset, 0px 0px 5px #557455;
    background: none repeat scroll 0% 0% #FFF;
  }
  
  /*Textboxes css ~end*/
  
  /*Teambuilder css*/
  
  .teamchartbox {
    border-top: 1px solid #212121;
  }
  
  .teambar {
    top: 1px;
  }
  
  .teambar button {
    border: 1px solid #212121 !important;
    height: 41px;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
  }
  
  .setchart {
    border-color: #557455;
    background: url("http://i.imgur.com/NSRIbL9.png?2");
    box-shadow: 0px 0px 5px #98B597;
  }
  
  .setchart-nickname {
    background: url("http://i.imgur.com/S077OI1.png?1");
    box-shadow: 0px 0px 5px #98B597;
    border-color: #557455;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: url("http://i.imgur.com/6b87xk5.png?1");
    border: 1px solid #557455;
    color: #212121;
    text-shadow: none;
  }
  
  .setchart label, .setchart-nickname label {
    color: #212121;
    text-shadow: none;
  }
  
  .setmenu button {
    color: #212121;
  }
  
  .setmenu button:hover {
    background: url("http://i.imgur.com/6b87xk5.png?1");
    border-color: #557455;
    box-shadow: 0px 0px 5px #98B597;
  }
  
  .teambuilder-results .result a:hover, .teambuilder-results .firstresult a {
    border: 1px solid #212121;
    background: #EAE6DA;
    border-radius: 5px;
  }
  
  .utilichart .numcol, .utilichart .statcol em, .utilichart .bstcol em {
    color: #454545;
  }
  
  .utilichart .col {
    color: #252525;
  }
  
  .utilichart .namecol, .utilichart .movenamecol, .utilichart .pokemonnamecol {
    font-style: italic;
  }
  
  /*Teambuilder css ~end*/
  
  /*Ladder css*/
  
  .ladder table, .ladder td, .ladder th {
    border: 1px solid #212121;
  }
  
  .ladder td {
    background: rgba(210, 212, 209, 0.4);
  }
  
  .ladder th {
    background: #D2D4D1;
  }
  
  /*Ladder css ~end*/

  /*Broadcast css*/
  
  .broadcast-blue {
    text-align: center;
    border: 1px solid #414D5E;
    background: #677992;
    font-family: helvetica,arial,sans-serif;
    font-size: 11pt;
    color: #C1DAFF;
    padding: 4px;
  }

  .broadcast-green {
    text-align: center;
    border: 1px solid #3F6043;
    background: #64946C;
    font-family: helvetica,arial,sans-serif;
    font-size: 11pt;
    color: #DBE5DC;
    padding: 4px;
  }

  .broadcast-red {
    text-align: center;
    border: 1px solid #985555;
    background: #D57575;
    font-family: helvetica,arial,sans-serif;
    font-size: 11pt;
    color: #FFD7D7;
    padding: 4px;
  }

  .broadcast-blue a {
    transition:All 1.6116s ease;
    color: #C1DAFF;
  }

  .broadcast-blue a:hover {
    transition:All 1.6116s ease;
    color: #8DB1E6;
  }

  .broadcast-green a {
    transition:All 1.6116s ease;
    color: #DBE5DC;
  }

  .broadcast-green a:hover {
    transition:All 1.6116s ease;
    color: #A3D0A7;
  }

  .broadcast-red a {
    transition:All 1.6116s ease;
    color: #FFD7D7;
  }

  .broadcast-red a:hover {
    transition:All 1.6116s ease;
    color: #ECBABA;
  }
  
  /*Broadcast css ~end*/
  
  /*************
  * Battle CSS *
  *************/
  
  .battle-log {
    background: transparent;
    border-left: none;
    color: #212121;
    text-shadow: none;
  }
  
  .battle-log .chat em {
    color: #000;
  }
  
  .chat em {
    color: #000 !important;
  }
  
  .battle-log .chat strong, em[style="color:#445566;display:block;"] {
    color: #000;
  }
  
  .battle-log h2 {
    background: transparent;
    color: #000;
    text-align: right;
    font-style: italic;
    border: none;
  }
  
  .battle-log-add {
    background: transparent;
  }
  
  .battle-log-add {
    border-top-left-radius: 5px;
    text-shadow: none;
  }
  
  .battle-log .rated strong {
    background: #000;
  }
  
  .battle-controls .whatdo {
    color: #212121;
  }
  
  .moveselect button, .switchselect button {
    color: #212121;
  }
  
  .chat small {
    color: #454545;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(210, 212, 209, 0.4);
    color: #212121;
    text-shadow: none;
    border-right: none;
  }

  .ps-room {
    position: absolute;
    display: block;
    top: 56px;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid #111;
    border-top: none;
  }

  .ps-room .battle{
    top: 60px;
    border-width: 0px;
  }

  .foehint{    
    position: relative;
    top: 45px;
  }        

  .battle {
    border: 0px solid #AAAAAA;
    width: 640px;
    height: 360px;
    overflow: visible;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
  }
    
  .ps-room .battle-controls {
    top: 430px;
  }

  .turn {
    position: absolute;
    top: -42px;
    left: 270px !important;
    width: 101px;
    height: 17px;
    font-size: 8pt;
    font-weight: bold;
    margin: 0px;
    padding: 0px;
    padding-top:35px;
    border: 0;
    border-radius: 0;
    opacity: 1 !important;
    background: url(http://i.imgur.com/86opqqK.png);
    text-align: center;
    color: #fff;
  }
  
  /* Battle Background */

  .backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
  }
  
  .backdrop:before{
    display: block;
    content: "";
    background: url("http://i44.tinypic.com/dqni4j.png");
    background-size: cover;
    width: 640px;
    height: 360px;
  }

  .backdrop:after{
    display: block;
    content: "";
    background: url(http://i.imgur.com/FKvgrXT.png);
    position: absolute;
    top: 0;
    left: 0;
    width: 640px;
    height: 360px;
  }
  
  /* Battle Background ~end */
  
  /* Leftbar/Rigthbar */

  .rightbar {
    position: absolute;
    top: -60px;
    right: 0;
    height: 100px;
    width: 320px;
    background-color: transparent;
    border: 0px solid rgba(215,215,215,0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/Ttx9rhp.png);
    background-position: 0px -35px;
  }

  .rightbar .trainer {
    margin-top: 0px;
    text-align: right;
  }

  .rightbar .trainersprite{
    position: absolute;
    top: 5px;
    left: 40px;
    opacity: 1 !important;
  }

  .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-right: 10px;
    color: #000;
    text-shadow: none;
  }

  .rightbar .trainer .teamicons {
    float:left;
    position: relative;
    top: -42px;
    left: 108px;
  }

  .leftbar {
    position: absolute;
    top: -60px;
    left: 0;
    height: 100px;
    width: 320px;
    background-color: transparent;
    border: 0px solid rgba(215,215,215,0.5);
    color: #777777;
    background-image: url(http://i.imgur.com/vinIupZ.png);
    background-position: 0px -35px;
  }

  .leftbar .trainer {
    margin-top: 0px;
    text-align: left;
  }

  .leftbar .trainersprite{
    position: absolute;
    top: 5px;
    right: 40px;
    opacity: 1 !important;
  }

  .leftbar .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-left: 10px;
  }

  .leftbar .trainer .teamicons {
    float:right;
    position: relative;
    top: -42px;
    right: 108px;
  }
  
  /* Leftbar/Rigthbar ~end */
  
  /* Battle Weather */

  .weather {
    text-shadow:none;
    color: rgba(0,0,0,0);
  }

  .weather em small {
    font-size: 7pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left:0;
    top: 85px;
    width: 95px;
    text-align:center;
  }

  .sunweather, .sunnydayweather {
    background: url(http://i.imgur.com/GCf4fyN.png) no-repeat scroll left top;
  }

  .rainweather, .raindanceweather {
    background: url(http://i.imgur.com/x7K1Zop.png) no-repeat scroll left top;
  }

  .hailweather {
    background: url(http://i.imgur.com/xCXcpE5.png) no-repeat scroll left top;
  }

  .sandstormweather {
    background: url(http://i.imgur.com/CbsvtPl.png) no-repeat scroll left top;
  }

  .pseudoweather {
    background: url(http://i.imgur.com/rPMtZca.png) no-repeat scroll left top;
  }
  
  /* Battle Weather ~end */
  
  /* Movemenu buttons */

  .movemenu button {
    width: 150px;
    height: 50px;
    border-radius: 25px;
    border-width: 0;
    box-shadow: none;
    color: black;
    font-size: 10pt;
    font-family: Arial, sans-serif;
    padding-top: 0px;
    margin: 0px;
  }

  .movemenu button:hover {
    top: -2px;
  }

  .movemenu button small {
    text-align: center;
    width: 62px;
    margin-top: 0px;
  }

  .movemenu button small.type {
    display: none;
  }

  .movemenu button small.pp {
    padding: 6px 0px 2px;
    color: white;
    font-size: 8pt;
    text-shadow: none;
  }

  .movemenu button small.pp:before {
    content: 'PP ';
  }

  .movemenu button.type-Normal, .movemenu button.type-Normal:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -50px !important;
  }

  .movemenu button.type-Fire, .movemenu button.type-Fire:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -50px !important;
  }

  .movemenu button.type-Water, .movemenu button.type-Water:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -150px !important;
  }

  .movemenu button.type-Electric, .movemenu button.type-Electric:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px 0px !important;
  }

  .movemenu button.type-Grass, .movemenu button.type-Grass:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -100px !important;
  }

  .movemenu button.type-Ice, .movemenu button.type-Ice:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px 0px !important;
  }

  .movemenu button.type-Fighting, .movemenu button.type-Fighting:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -150px !important;
  }

  .movemenu button.type-Poison, .movemenu button.type-Poison:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px 0px !important;
  }

  .movemenu button.type-Ground, .movemenu button.type-Ground:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -150px !important;
  }

  .movemenu button.type-Flying, .movemenu button.type-Flying:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -50px !important;
  }

  .movemenu button.type-Psychic, .movemenu button.type-Psychic:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px 0px !important;
  }

  .movemenu button.type-Bug, .movemenu button.type-Bug:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -100px !important;
  }

  .movemenu button.type-Rock, .movemenu button.type-Rock:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -200px !important;
  }

  .movemenu button.type-Ghost, .movemenu button.type-Ghost:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -50px !important;
  }

  .movemenu button.type-Dragon, .movemenu button.type-Dragon:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -150px !important;
  }

  .movemenu button.type-Dark, .movemenu button.type-Dark:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -100px !important;
  }

  .movemenu button.type-Steel, .movemenu button.type-Steel:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -200px !important;
  }

  .movemenu button.type-Fairy, .movemenu button.type-Fairy:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -100px !important;
  }

  .movemenu button.type-undefined, .movemenu button.type-undefined:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -200px !important;
  }

  .movemenu button:disabled {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -200px !important;
    opacity: .5;
  }

  .movemenu label {
    width: 85px;
    text-align: center;
    font-size: 0;
    position: absolute;
    top: 55px;
    right: 250px;
  }

  .movemenu label input {
    position: relative;
    top: 1px;
  }

  .movemenu label:after {
    content: " Mega evolution";
