css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("qt.io"){

body
{
	font-family: Verdana,DejaVu Sans,Geneva,sans-serif;	
	line-height: 2.25;
	color: #303030;
	background-color: #ffffff;
	font-size: 90%;
}

p {	
	font-size: 10pt;
	margin: 0px 0px 13px;	
	line-height: 1.40 ;
}
h1,h2,h3,h3.main-post,h4,.topic-title{font-weight: bold !important;
			color: rgb(17,17, 92);
			text-align: left !important;
	    line-height: 1 !important;	
}	
h1 {font-size: 18pt !important;
}
	
h2 {font-size: 12pt !important;	
}
	
h3 {font-size: 12pt !important;	
}
/*documentation doc.qt*/
.sidebar {width:0px ;visibility: hidden;}	
.context {width:95%;}	

/*forum.*/
 
.container	{width:100%;	}
.category-header {width:250px;	}
.qt-subcategories-home {width:250px;	background-color: #efefff;}
.category-box {width:30%;}	
	
.topic-title {font-size: 12pt;}	
.teaser-pic {
		  height:10px  !important;
		  width:10px  !important;
}
.user-img
	{
		  height:0px  !important;
		  width:0px  !important;
	}
	
.topic-stats {visibility: hidden; color: rgb(0, 255, 255);}	
.timeago {visibility: hidden; color: rgb(0, 255, 255);}	
	
.search-result-text
	{
		font-size: 10pt;
		background-color: rgb(255, 255, 255);
		color: rgb(10, 10, 10);
		line-height: 1.40 ;
	}
.details {animation-play-state:paused;}
	
	
	
a {
	font-family: Verdana,DejaVu Sans,Geneva,sans-serif;
	font-size: 13px;
	line-height: 1.10 !important;
	color: #00732f;
	
}
}
