css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("futalog.com") {
  body > table{
    width:100%;
  }
  body > table,tbody{
    margin: 0;
    padding: 0;
  }
  body > table{
    border-top:1px solid #666;
  }
  body > table > tbody > tr > td:first-child{
    display:none;
  }
  body,body > table,tbody,tr,td{
      background: #FFF;
  }
  blockquote{
    color:#666;
  }
  img{
    opacity: 1.0!important;
  }
  iframe,body > table > tbody > tr > td > table,#left_area > ins{
    display:none !important;
  }
}
