css:
@-moz-document domain(subdivx.com) {
/* Todos los cambios que le hice */
body {font-family: 'GT Pressura', HelveticaNeue-CondensedBold, Impact, sans-serif;}

h1 {font-size:18px;}
h2 {font-size: 20px;}
h2 input {font-size: 20px;color: white;background: #3498db;
  background-image: -webkit-linear-gradient(top, #3498db, #0069ab);
  background-image: -moz-linear-gradient(top, #3498db, #0069ab);
  background-image: -ms-linear-gradient(top, #3498db, #0069ab);
  background-image: -o-linear-gradient(top, #3498db, #0069ab);
  background-image: linear-gradient(to bottom, #3498db, #0069ab);}
h3 {font-size: 12px;height: 10px;}
h4 {font-size: 10px;}

input[type=submit] {
  background: #3498db;
  background-image: -webkit-linear-gradient(top, #3498db, #0069ab);
  background-image: -moz-linear-gradient(top, #3498db, #0069ab);
  background-image: -ms-linear-gradient(top, #3498db, #0069ab);
  background-image: -o-linear-gradient(top, #3498db, #0069ab);
  background-image: linear-gradient(to bottom, #3498db, #0069ab);
  text-transform: uppercase;
  color: #ffffff;
  font-size: 16px;
  padding: 8px 16px 8px 16px;
  text-transform: uppercase;
}

input[type=submit]:hover {
  background: #3cb0fd;
  background-image: -webkit-linear-gradient(top, #3cb0fd, #3498db);
  background-image: -moz-linear-gradient(top, #3cb0fd, #3498db);
  background-image: -ms-linear-gradient(top, #3cb0fd, #3498db);
  background-image: -o-linear-gradient(top, #3cb0fd, #3498db);
  background-image: linear-gradient(to bottom, #3cb0fd, #3498db);
}
#barra_buscador select {
  text-transform: uppercase;
  color: #fff;
  font-size: 12.5px;
  padding: 9px 15px 8px 15px;
  border: 2px solid darkblue;
  border-left: none;
  margin-left: -5px;
  background: #3498db;
  background-image: -webkit-linear-gradient(top, #3498db, #0069ab);
  background-image: -moz-linear-gradient(top, #3498db, #0069ab);
  background-image: -ms-linear-gradient(top, #3498db, #0069ab);
  background-image: -o-linear-gradient(top, #3498db, #0069ab);
  background-image: linear-gradient(to bottom, #3498db, #0069ab);
}
select {
  text-transform: uppercase;
  color: #fff;
  font-size: 12px;
  padding: 3px 7px 3px 7px;
  border: 1px solid darkblue;
  background: #3498db;
  background-image: -webkit-linear-gradient(top, #3498db, #0069ab);
  background-image: -moz-linear-gradient(top, #3498db, #0069ab);
  background-image: -ms-linear-gradient(top, #3498db, #0069ab);
  background-image: -o-linear-gradient(top, #3498db, #0069ab);
  background-image: linear-gradient(to bottom, #3498db, #0069ab);
}
#arriba_detalle {background-color: #F1F1F1;position:fixed;top:0;left:0;font-size:16px;color: cornflowerblue;font-weight:bold;margin-left:8.8%;}
#barra_logo {text-align:center;position:relative;left:33%;margin-top:30px;}
#barra_logo img {height:80px;}
#contenedor_gral {min-width: 500px;}
.buscador_arriba:hover {border: 2px blue solid;border-right:none;}	
.buscador_arriba {background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);border: 2px solid darkblue;height:30.5px;font-size: 18px;font-weight: bold;color: white;width: 325px;outline:0;border-right:none;}
#barra_buscador {text-align:center;width: 100%;height: 30px;padding-top: 40px;padding-bottom: 55px;border-bottom: dotted 1px darkblue;vertical-align: middle;}
#contenedor_interno {width:100%;text-align:center;margin-top:5px;}
#contenedor_izq {margin-left:0%;max-width:900px;width:auto;margin-top:20px;}
#buscador_detalle, #detalle_ficha {text-align:center;margin-left:17%;}
#menu_detalle_buscador, #menu_detalle {vertical-align:middle;margin-left:17%;text-align:center;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
#detalle_contenedor {margin-top:40px;position:relative;text-align:center;}
#notificaciones {position:fixed;top:0;left:0;font-weight:bold;margin-left:8.6%;margin-top:22px;}
.nu {position:absolute;margin-top:-100px;margin-left:-530px;}
input {margin-bottom:3px;font-size:14px;color:white;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
#reg_columna {color:cornflowerblue;}
#perfil_submit {float: left;width: auto;}
.result_busc3, .result_busc2, .result_busc {padding-left:15%;text-align:center;font-size:12px;display:block;}
.fuente4 {color:cornflowerblue;}
.fuente5 {color:cornflowerblue;}
.fuente6 {color: darkred;}
.fuente7 {font-size:15px;}
.fuente7 u {color:cornflowerblue;text-decoration:none;font-weight:bold;font-size:17px;}
.loguearse_olvido {font-size:15px;}
.loguearse_olvido:hover {color:cornflowerblue;}
.titulo_menu_izq {text-align:center;vertical-align:middle;margin-left:25px;}
.titulo_menu_izq::before {content:"Subtitulos para ";}
#reg_menu, #reg_menu_detalle  {margin-top:130px;}
#reg_menu_detalle, #prog_menu_detalle, #perfil_menu, #foro_tema_menu, #menu_largo {vertical-align:middle;height:23px;text-align:center;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
.publicar_rta {padding:10px;margin-top: 10px;margin-left:15%; background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);height:30px;font-size: 18px;font-weight: bold;color: white;display:inline-block;vertical-align:middle;}
#primer_msg_voto {border: solid 1px darkblue;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);height:30px;font-size: 14px;height: 42px;font-weight: bold;color: white;vertical-align:middle;}
#primer_msg {margin-left:20%;margin-bottom:5px;padding-bottom:25px;}
#primer_msg_mensaje {display:none;}
#contenedor_foro {margin-left:20%;}
.mensaje {text-align:justify;padding-right:15px;box-sizing:border-box;padding-top:10px;border-top: dotted 1px lightblue; }
.fecha {background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
#ultimos_foros, #legal, #contactenos {margin-left:23%;margin-top:5px;}
#prog_menu_detalle {margin-left:23%;}
#wrapper{overflow:hidden;padding-bottom:30px;}
.datos a > img {display:none;}
.datos {padding-top:5px;}
.nick_foro {color:cornflowerblue;font-size:18px;padding-bottom:10px;}
#resultados1, #resultados2, #resultados3, #resultados4, #resultados5, #resultados6, #resultados7, #resultados8, #resultados9, #resultados10, #resultados11, #resultados12, #resultados13, #resultados14, #resultados15, #resultados16, #resultados17, #resultados18, #resultados19, #resultados20  {margin-bottom:7px;}
#expand1 {text-align:center;margin-left:15%;}
#expand1 .publicar_rta {margin-left:0%;}
#expand1 textarea, textarea, #fila_upload textarea, .fuente4 textarea, #ultimos_foros textarea {margin-top:10px; width:500px;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
.buscador_home {width:500px;background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);}
#detalle_puntos {text-transform:capitalize;font-size:15px;}
#detalle_ficha {font-size:11px;}
#menu_titulo_buscador b::before {content:"Subtitulos para ";}
#ultimos_foros_top, #ultimos_foros_cat {float:none;width:700px;background-color:transparent;}
#ultimos_foros_top {margin-top:0px;text-align:right;float:right;margin-right:5%;margin-bottom:15px;}
#ultimos_foros_cat input {margin-top:10px;margin-bottom:15px;}
#perfil, #agregaf_detalle, #perfil_menu {margin-left:23%;}
.link1 {padding:10px;margin-top: 10px; background: #3498db;background-image: -webkit-linear-gradient(top, #3498db, #0069ab);background-image: -moz-linear-gradient(top, #3498db, #0069ab);background-image: -ms-linear-gradient(top, #3498db, #0069ab);background-image: -o-linear-gradient(top, #3498db, #0069ab);background-image: linear-gradient(to bottom, #3498db, #0069ab);height:33px;font-size: 20px;font-weight: bold;color: white;display:inline-block;text-transform:uppercase;text-decoration:none;}
.link1:hover {background: #3cb0fd;background-image: -webkit-linear-gradient(top, #3cb0fd, #3498db);
  background-image: -moz-linear-gradient(top, #3cb0fd, #3498db);background-image: -ms-linear-gradient(top, #3cb0fd, #3498db);
  background-image: -o-linear-gradient(top, #3cb0fd, #3498db);background-image: linear-gradient(to bottom, #3cb0fd, #3498db);text-decoration:none;text-transform:uppercase;color:white;}
#foroxuser_titulo a, .result_busc3 a {border:0;background:0;font-size:13px;color:black;font-weight:normal;display:inline;}
.result_busc3 a:hover {padding:0px;}
#perfil_mensaje {text-align:center;}
#ultimos_foros_top a, #ultimos_foros_cat, #pop_upcoment_der a, #pop_upcoment_der a:hover, #buscador_detalle_sub_datos a, #buscador_detalle_sub_datos a:hover {border:0;background:0;font-size:13px;color:black;font-weight:normal;padding:0;font-weight:bolder;display:inline;}
#buscador_detalle_sub {padding:10px;text-align:center;}
input#email {width:230px;}
#foro_tema_principal {text-align:center;}
#contenedor_izq {margin-left:0%;}
#foro_tema_menu, #foro_tema_ficha, #foro_tema_principal, #perfil_mensajes_renglon_priv {margin-left:23%;float:inherit;}
b>img {display:none;}
#foroxuser_datos a {border:0;background:0;font-size:13px;color:black;font-weight:normal;padding:0;}
#foroxuser_datos {margin-bottom:13px;display:inline;margin-top:-8px;text-align:left;}
#agregaf_detalle a {border:0;background:0;font-size:13px;color:black;font-weight:normal;padding:0;}
#agregaf_detalle a:hover {border:0;background:0;font-size:13px;color:black;font-weight:bold;}
#fb_login_b {position:fixed;top:0;left:23%;}
#home_centro {margin-left:16%;margin-top:10px;}
#menu_largo .titulo_menu_izq::before {content:"";}
#filtros {padding-top:5px;margin-bottom:5px;}
#perfil_mensajes_datos a {border:0;background:0;font-size:13px;color:black;font-weight:normal;padding:0;}
#perfil_mensajes_datos a:hover {border:0;background:0;font-size:13px;color:black;font-weight:bold;}

/* Cosas escondidas porque no me gustaban */
#error_buscador {display:none;}
#cuadrados_izq {display:none;}
#contenedor_foro form {display:none;}
#contenedor_der {display:none;width:0;}
#menu_top {display:none;}
#barra {display:none;}
#pie {display:none}
#chat_home {display:none;}
#home_der {display:none;}
#menu_largo_chat {display:none;}
#banner_728x90_top {display:none;}
#ad300x300 {display:none;}
#ad300x300_detalle {display:none;}
#buscador_rss {display:none;}
}
