css:
@-moz-document domain("replay.pokemonshowdown.com") {
  .dark .pfx-topbar,
  .pfx-topbar {
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    border-bottom: 1px solid #000 !important;
  }
  
  .nav a.button {
    border: 1px solid #000;  
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    color: #26979F;
    text-shadow: 0px -1px 0px #000;
    box-shadow: 1px 1px 1px #151515;
  }
  
  .nav a.button:hover {
    top: 1px;
    box-shadow: none !important;
    background: #171717 !important;
  }
  
  .button.cur,
  .button.cur:hover {
    background: #171717 !important;
    color: #26979F;
    text-shadow: 0px -1px 0px #000;
    box-shadow: none !important;
    border: 1px solid #000;
    top: 1px;
  }
  
  .pfx-panel, 
  .dark .pfx-panel {
     background: url("http://i.imgur.com/gl8Q11V.png?1");
  }
  
  .pfx-body-padded h1,
  .linklist h3,
  .replay-wrapper h1 {
    color: #FFF;
    text-shadow: 1px 1px #000;
  }
  
  a,
  .dark a {
    color: #26979F;
  }
  
  a:hover,
  .dark a:hover {
    color: #157279;
  }
  
  .pfx-body-padded p,
  .replay-wrapper p {
    color: #D8D8D8;
    text-shadow: 1px 1px #000;
  }
  
  .linklist a { 
    border: 1px solid #000;
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    border-radius: 3px;
    color: #26979F;
    text-shadow: 0px -1px 0px #000;
    box-shadow: 1px 1px 1px #151515;
  }
  
  .linklist a:hover,
  .linklist a.cur,
  .linklist a.cur:hover { 
    box-shadow: none;
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #171717, #171717) repeat scroll 0% 0% transparent;
    color: #2AAFB9;
    text-shadow: 0px -1px 0px #000;
    border: 1px solid #000;
  }
  
  .linklist a {
    transition:All 1.6116s ease;
  }
  
  .linklist a:hover {
    transition:All 1.6116s ease; 
    margin-right: 20px;
  }
  
  .linklist a small {
    color: #157279;
    text-shadow: 0px 0px 1px #000;
  }
  
  .textbox {
    transition:All 1.6116s ease; 
    background: #202020;
    color: #D8D8D8;
    box-shadow: 0px 1px 2px #303030 inset;
    border: 1px solid #000;
    padding: 3px;
  }
  
  .textbox:hover {
    background: #202020;
    color: #FFF;
    box-shadow: 0px 1px 2px #303030 inset;
    border: 1px solid #000;
  }
  
  .textbox:focus {
    transition:All 1.6116s ease; 
    background: #101010;
    border: 1px solid #000;
    box-shadow: 0px 1px 2px #303030 inset, 0px 0px 3px #157279;
  }
  
  ::-moz-selection {
    background: #000 !important;
    color: #157279 !important
  }
  
  button {
    border: 1px solid #000;
    border-radius: 3px;  
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    color: #26979F;
  }
  
  button:hover {
    background: #171717;
  }
  
  .urlbox,
  .dark .urlbox {
    border: 1px solid #000;
    border-radius: 3px;  
    background: #171717 !important;
    color: #26979F !important;
  }
  
  /* Broadcast CSS */
  
  .broadcast-red {
    background: #D57575;
    border: 1px solid #985555;
    color: #FFD7D7;
    padding: 5px 1px 3px 8px;
    text-shadow: 1px 1px 1px #985555;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
  }

  .broadcast-green {
    background: #578A54;
    border: 1px solid #375735;
    padding: 5px 1px 3px 8px;
    color: #BEFFB9;
    text-shadow: 1px 1px 1px #375735;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
  }

  .broadcast-blue {
    background: #677992;
    border: 1px solid #414D5E;
    padding: 5px 1px 3px 8px;
    color: #C1DAFF;
    text-shadow: 1px 1px 1px #414D5E;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
  }
  
  /* Broadcast CSS ~end */
  
  /* Battle CSS */
  
  .battle-log,
  .dark .battle-log {
    background: url("http://i.imgur.com/gl8Q11V.png?1");
    border-color: #000;
    color: #D8D8D8;
    text-shadow: 0px 0px 1px #000;
    box-shadow: 0px 0px 5px #000;
  }
  
  .battle-log .chat em {
    color: #D8D8D8;
  }
  
  .battle {
    background: #171717;
    border-color: #000;
    box-shadow: 0px 0px 5px #000;
  }
  
  .pfx-panel {
    border-color: #000;
  }
  
  .battle-log .chat strong {
    color: #157279;
  }
  
  em[style="color:#445566;display:block;"] {
    color: #157279 !important;
  }
  
  .message-throttle-notice,
  .message-error {
    color: #BF6A6A;
  }
  
  .battle-log h2,
  .battle-log-add,
  .dark .battle-log h2,
  .dark .battle-log-add {
    text-align: right;
    font-style: italic;
    color: #FFF;
    background: transparent;
    border: none;
  }
  
  .turn {
    position: absolute;
    top: 16px;
    left: 270px !important;
    width: 101px;
    height: 17px;
    font-size: 8pt;
    font-weight: bold;
    margin: 0px;
    padding: 0px;
    padding-top:35px;
    border: 0;
    border-radius: 0;
    opacity: 1 !important;
    background: url(http://i.imgur.com/86opqqK.png);
    text-align: center;
    color: #fff;
  }
  
  .backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
  }
  
  .backdrop:before{
    display: block;
    content: "";
    background: url("http://i44.tinypic.com/dqni4j.png");
    background-size: cover;
    width: 640px;
    height: 360px;
  }
  
  .trainer div {
    opacity: 0.8;
  }
  
  .rightbar {
    position: absolute;
    top: 0px;
    right: 0;
    height: 60px;
    width: 320px;
    background-color: rgba(0, 0, 0, 0.5);
    border: none;
    color: #777777;
    background-image: url(http://i.imgur.com/Ttx9rhp.png);
    background-position: 0px -35px;
  }

  .rightbar .trainer {
    margin-top: 0px;
    text-align: right;
  }

  .rightbar .trainersprite{
    position: absolute;
    top: -10px;
    left: 40px;
    opacity: 1 !important;
  }

  .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-right: 10px;
    color: #FFF;
    text-shadow: 1px 1px #000;
  }

  .rightbar .trainer .teamicons {
    float:left;
    position: relative;
    top: -42px;
    left: 108px;
  }

  .leftbar {
    position: absolute;
    top: 0px;
    left: 0;
    height: 60px;
    width: 320px;
    background-color: rgba(0, 0, 0, 0.5);
    border: none;
    color: #777777;
    background-image: url(http://i.imgur.com/vinIupZ.png);
    background-position: 0px -35px;
  }

  .leftbar .trainer {
    margin-top: 0px;
    text-align: left;
  }

  .leftbar .trainersprite{
    position: absolute;
    top: -10px;
    right: 40px;
    opacity: 1 !important;
  }

  .leftbar .trainer strong {
    display: block;
    font-size: 8pt;
    overflow: hidden;
    padding-top: 45px;
    padding-left: 10px;
  }

  .leftbar .trainer .teamicons {
    float:right;
    position: relative;
    top: -42px;
    right: 108px;
  }
  
  .weather {
    text-shadow:none;
    color: rgba(0,0,0,0);
  }

  .weather em small {
    font-size: 7pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left:0;
    top: 85px;
    width: 95px;
    text-align:center;
  }

  .sunweather,
  .sunnydayweather {
    background: url(http://i.imgur.com/GCf4fyN.png) no-repeat scroll left top;
  }

  .rainweather,
  .raindanceweather {
    background: url(http://i.imgur.com/x7K1Zop.png) no-repeat scroll left top;
  }

  .hailweather {
    background: url(http://i.imgur.com/xCXcpE5.png) no-repeat scroll left top;
  }

  .sandstormweather {
    background: url(http://i.imgur.com/CbsvtPl.png) no-repeat scroll left top;
  }

  .pseudoweather {
    background: url(http://i.imgur.com/rPMtZca.png) no-repeat scroll left top;
  }
  
  .playbutton button,
  .replay-controls button,
  .chooser,
  .dark .chooser {
    transition:All 1.6116s ease;
    border: 1px solid #000;
    background-color:#ababab;
    background-image: linear-gradient(#424242, #171717);
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    border-radius: 3px;
    color: #26979F;
    text-shadow: 0px -1px 0px #000;
    box-shadow: 1px 1px 1px #151515;
    opacity: 0.8;
  }
  
  .chooser em,
  .dark .chooser em {
    background: -moz-linear-gradient(center top , #424242, #171717) repeat scroll 0% 0% transparent;
    border: none;
    color: #26979F;
  }
  
  .playbutton button:hover {
    transition:All 1.6116s ease;
    opacity: 1;
  }
  
  .chooser button,
  .dark .chooser button {
    color: #26979F;
    text-shadow: 0px -1px 0px #000;
  }
  
  .chooser button.sel,
  .chooser button.sel:hover,
  .chooser button:hover,
  .dark .chooser button.sel,
  .dark .chooser button.sel:hover,
  .dark .chooser button:hover {
    border-color: #000;
    background: #101010;
    color: #26979F;
  }
  
  .statbar {
    position: absolute;
    margin-top: 15px;
    display: none;
    width: 186px;
    background: url(http://i.imgur.com/eYsfnK4.png);
    background-repeat: no-repeat;
    background-position: 0px 1px;
    min-height: 40px;
    padding: 2px 4px 2px 26px;
  }
  
  .statbar strong {
    position: absolute;
    left: 30px;
    top: 6px;
    font-size: 9px;
    font-weight: normal;
    display: block;
    text-align: center;
    color: #FFF;
    text-shadow: #000 1px 1px 0, #000 1px -1px 0, #000 -1px 1px 0, #000 -1px -1px 0;
  }
  
  .statbar strong small {
      font-size: 9px;
    font-weight: normal;
  }
  
  .statbar .hpbar {
    position: relative;
    border: 1px solid #131313;
    background: #FCFEFF;
    height: 6px;
    margin: 0 0 0 0;
    width: 151px;
    border-radius: 0px;
      padding: 0;
      top: -2px;
  }
  
  .hptextborder {
    display:none !important;
  }
  
  .statbar .hpbar .hp {
    height: 3px;
    border-top: 1px solid #007734;
    background: #00bb51;
    border-bottom: 2px solid #007734;
    border-radius: 0px;
  }
  
  .statbar .hpbar .hp-yellow {
    background-color: #f5d538;
    border-color: #be9f0a;
  }
  
  .statbar .hpbar .hp-red {
    background-color: #ee4928;
    border-color: #a3260d;
  }

  .statbar .hpbar .prevhp {
    background: #BBEECC;
    height: 6px;
    border-radius: 0px;
  }
  
  .statbar .hpbar .prevhp-yellow {
    background-color: #fcf4ca;
  }
  
  .statbar .hpbar .prevhp-red {
    background-color: #facec5;
  }

  .hptext {
    margin-top: 2px;  
  }

  .statbar .hpbar .hptext {
    position: absolute;
    color: #FFFFFF;
    text-shadow: 1px 0 black, -1px 0 black, 0 1px black, 0 -1px black;
    font-size: 9px;
    width: 32px;
    height: 12px;
    top: -1px;
    text-align: center;
  }
  
  .rstatbar .hpbar .hptext {
    width: 40px;
    height: 15px;
    background:url(http://i.imgur.com/1oaSYb8.png);
    right: -42px;
    border-radius: 0 4px 4px 0;
  }
  
  .lstatbar .hpbar .hptext {
    width: 35px;
    height: 15px;
    background:url(http://i.imgur.com/iAy0D88.png);
    left: -55px;
    border-radius: 4px 0 0 4px;
    padding-left: 2px;
  }

  .statbar .status {
    margin-top:15px;
    min-height: 10px;
    font-size: 7pt;
  }

  .statbar span.brn,
  .statbar span.psn,
  .statbar span.slp,
  .statbar span.par,
  .statbar span.frz {
    position: absolute;
    left: -21px;
    top: 10px;
    font-size: 8px;
    padding: 1px 2px;
    border: 0;
    border-radius: 3px;
  }
  
  .statbar span.bad {
    background: #e74c3c;
    color: #ecf0f1;
    border-color: #c0392b;
  }
  
  .statbar span.good {
    background: #27ae60;
    color: #ecf0f1;
    border-color: #067A37;
  }
  
  .statbar span.neutral {
    background: #7f8c8d;
    color: #ecf0f1;
    border: 1px solid #2c3e50;
  }
  
  /* Battle CSS ~end */
}
