css:
@-moz-document url-prefix("https://www.computerbase.de/") {
/*
ComputerBase
URLs beginnend mit
http://www.computerbase.de

/* ads */
.google-cjs,
.adbox-banner,
.adbox-wrapper,
.adbox-topbanner,
.adbox-skyscraper,
.ads-google-csa--loaded,
.adsbygoogle--content-list,
.adsbygoogle--content-footer {opacity: 0.15; transition: opacity 0.25s ease-in-out}

.google-cjs:hover,
.adbox-banner:hover,
.adbox-wrapper:hover,
.adbox-topbanner:hover,
.adbox-skyscraper:hover,
.ads-google-csa--loaded:hover,
.adsbygoogle--content-list:hover,
.adsbygoogle--content-footer:hover {opacity: 0.75}

.article-view__share-links-top {display: none}

/* centric alignment */
@media (min-width: 960px) {.block1 {padding: 0 !important;}}

/* article headlines */
.article-view__title {font-size: 2.0em}
.article-view__sharelinks-top, .article-view__sharelinks-bottom {display: none !important}
.article__title, .teaser__title--xl {font-size: 1.5em}
.article__title, .slide__title {font-size: 1.1em; line-height: 1.0em}
.slide__context__title, .slide__title {font-size: 0.85em; line-height: 1.25em}

/* news list */
.ticker li {padding: .125rem 0}

/* text in articles over full width */
.p, .text-ul, .text-ol {
    font-family: cbsans, sans-serif;
    font-size: 1.05em;
    text-align: justify
}
.text-width {max-width: 55rem;}

/* article navigation */
.text-asset {margin: 0 60px}

/* Schrift */
.p-breadcrumbs, .p-body-header, .p-body-main, .overlay-container, .tooltip, .u-bottomFixer, .message-body, .bbWrapper {
    font-family: cbsans, sans-serif !important;
    line-height: 1.35 !important;
    letter-spacing: 0.01em;
}
.node-extra {font-size: 14px}
.structItem-minor {font-size: 14px}
.structItem-title {font-size: 16.5px}

/* Zentrische Ausrichtung */
@media (min-width: 70em) {.padding-2 {padding-left: 3%; padding-right: 3%}}
@media (min-width: 75em) {.padding-2 {padding-left: 4%; padding-right: 4%}}
@media (min-width: 80em) {.padding-2 {padding-left: 5%; padding-right: 5%}}
@media (min-width: 85em) {.padding-2 {padding-left: 6%; padding-right: 6%}}
@media (min-width: 90em) {.padding-2 {padding-left: 7%; padding-right: 7%}}
@media (min-width: 95em) {.padding-2 {padding-left: 8%; padding-right: 8%}}
@media (min-width: 100em) {.padding-2 {padding-left: 9%; padding-right: 9%}}
@media (min-width: 105em) {.padding-2 {padding-left: 10%; padding-right: 10%}}
@media (min-width: 110em) {.padding-2 {padding-left: 11%; padding-right: 11%}}

/* Leiste - Liste - Tabelle */
.block-header {padding: 4px 10px}
.node-main {padding: 8px 10px}
.node-extra {width: 25%}

/* Nav-Leiste */
.row--forum-nav {padding: 0}
.p-sectionLinks-inner {padding-left: 0px; padding-right: 8px}
.p-sectionLinks-list .p-navEl-link {padding: 0 2px 0px 8px !important}
.p-sectionLinks-list .p-navEl-splitTrigger {padding: 0px 2px}
.p-sectionLinks-list {text-align: center}
.p-sectionLinks-list .p-navEl {
    padding: 2px 0px;
    font-family: Calibri light;
    font-size: 16px;
    font-weight: bold;
    color: #f2610d
}

/* Beitragsübersicht - Spalten */
.structItem-cell.structItem-cell--meta {width: 144px}
.structItem-cell.structItem-cell--latest {width: 192px}

/* Avatar - Icon */
.avatar.avatar--default.avatar--default--text {color: #555 !important}
.avatar--default--dynamic {opacity: 0.5}
.message-avatar .avatar--default {
    height: 20px;
    font-size: 11px
}
.message-avatar-wrapper .message-avatar-online:before, .fas {color: #e03030}
.structItem-status--locked::before {color: #f2610d}

/* Links - Mouseover */
a {color: #2c5b9c}
a {transition: all 0.5s; text-decoration: none;}
a:hover {color: #e84d0e; background-color: #b8b8b8; border-radius: 2.5px; text-decoration: none}
a:active {background-color: #ffd700}
}
