css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eloquentjavascript.net") {
  body {
    background-color: #161616;
    color: #CECECE;
  }
  
  pre, .editor-wrap, .CodeMirror {
    background-color: #202020;
  }
  
  .sandboxhint, .CodeMirror-linenumber, .CodeMirror-linenumbers {
    background-color: #373737;
  }
  
  .sandbox-open-menu > div {
    background-color: #373737;
    color: rgba(255, 255, 255, 0.79);
  }
  
  h1, h2, h3, h4, h5, h6, h7, 
  p, blockquote, blockquote p, .sandboxhint, pre, pre > span {
    color: rgba(255, 255, 255, 0.79);
  }
  
  .cm-number {
    color: #00b95d !important;
  }
  
  .cm-atom {
    color: #2700ec !important;
  }
  
  .cm-keyword {
    color: #b700dc !important;
  }
  
  .cm-variable {
    color: rgba(255, 255, 255, 0.85) !important;
  }
  
  .cm-variable-2 {
    color: #003ac9 !important;
  }
  
  .cm-property {
    color: #c6d6ff !important;
  }
  
  .cm-comment {
    color: #a45e00 !important;
  }
  
  .cm-string {
    color: #c40000 !important;
  }
  
  .cm-def {
    color: #005bff !important;
  }
  
  .cm-operator {
    color: rgba(255, 255, 255, 0.79) !important;
  }
  
  ol li::before { 
   color: #CECEC6;
  }
}
