css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.techdirt.com") {
  body, div.comments .callout {
    background-color: #282828 !important;
    color: #F4E8BA;
    
  }
  
  a {
    color: #FABD2F !important;
  }
  
  a:visited {
    color: #B57614 !important;
  }
  
  /* turn off bolding of links */
  .story a:link, .story a:visited {
    font-weight: inherit !important;
  }
  
  div.story, div.cbody {
    font-family: 'Georgia' !important;
    font-size:   17px      !important;
    line-height: 21px      !important;
    text-align:  justify   !important;
    
    max-width: 80ex;
  }
  
  div.story h1 {
    color: #FDF4C1;
  }
  
  div.story h3, div.comments h3 {
    color:#7C6F64;
  }
  
  div.storyblock {
    padding-bottom: 0 !important;
    margin-bottom: 22px !important;
  }
  
  /* make comment threads more obvious */
  ul.comments > li {
    margin:  0   !important;
    padding: 1ex !important;
    padding-bottom: 0 !important;

    list-style-type: none;
    border-top: 1px solid #2B3C44;
  }

  ul.comments {
    border-left: 1px solid #2B3C44 !important;
    padding-left: 0 !important;
  }
  
  div.comments div.callout {
    background-color: #32302F !important;
    color: #F4E8BA;
  }
  
  .staff, .author {
    background-color: #32302F !important;
  }

  .commentname {
    border: 0 none transparent !important;
  }

  .regcomment {
    margin-bottom: 1ex;
  }

  div.comments > ul.comments {
    border-bottom: 1px solid #2B3C44;
  }
  
  .commentsubject{
    color: #7C6F64 !important;
  }
  
  .cmt_ops {
    color:#7C6F64;
  }
  
  .rate_cmnt li.comment_rating {
    color: #8EC07C;
    background : #32302F;
    border-color: #1D2021;
  }
  
  .rate_cmnt li.negative {
    color: #FB4934;
  }
  
  div.expandermask {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, #282828 99%) repeat scroll 0% 0% transparent!important;
  }

  /* make the header more flexible */
  body, div#top,
  div#goldpromo, div#goldpromohide,
  div#blog, div#tdci, div#greenhouse,
  div#subnav, div#navigation {
    min-width: 0 !important;
  }
  
  input, textarea,select {
    background-color: #32302F;
    border: 1px #1D2021;
    color: #F4E8BA;
  }
  
  /* hide stuff */
  div#rightcolumn,
  div.sharebar,
  div.storyfooter,
  div.storyblock > br,
  div.relposts,
  #top,#maincol_top, .maincolumn_head,  .storynav, #bottom,
  .details, #subnav {
    display: none !important;;
  }
}
