css:
@-moz-document domain("tumblr.com") {
#dashboard_index > div.l-header-container.l-header-container--refresh.has-3d-logo > div > h1:before {
  content: url(http://s27.postimg.org/vt2xpw6jj/tumblr.png);
  visibility: visible;
  position: relative;
  top: 12px;
  transform: scale(1.1);
  margin-right: 15px;
  left: 20px;
}
#dashboard_index > div.l-header-container.l-header-container--refresh.has-3d-logo > div > h1 {
    visibility: hidden;
}
#search_form > div {
    margin-left: 30px;
    overflow: visible;
}
#search_form > div:before {
    left: 50px;
    position: relative;
}
#dashboard_index > div.l-header-container.l-header-container--refresh.has-3d-logo > div > h1 {
    overflow: visible;
}
#search_form > div > div:before {
    margin-left: 30px;
}
}
