css:
@-moz-document domain("forum.winpoin.com") {
/* Winpoin XF Blue
Create by Yusril Ibnu */

.navTabs {font-size: 12px}
#QuickSearch, #QuickSearch .formPopup, #QuickSearchQuery {padding: 2px;}
.nodeList .categoryStrip {font-size: 14px;background-color: rgb(44, 63, 123);padding: 12px 10px;margin: 3px auto 0;border-bottom: 3px solid #170000;}
.PageNav a.currentPage, .message .newIndicator, .PageNav a:hover, .PageNav a:focus {border-color:rgb(64, 83, 153);}
.button, .PageNav a.currentPage, #logoBlock, .subHeading, .discussionList .sectionHeaders, #logo a, a.callToAction span, .message .newIndicator, .PageNav a:hover, .PageNav a:focus, #SignupButton .inner {background-color: rgb(44, 63, 123);}
.socialicons li a:hover, .messageUserBlock a.username, .memberListItem h3.username, .footerLegal .pageContent .debugInfo a, .sharePage .textHeading.larger {color: rgb(64, 83, 153);}
.navTabs .navTab.PopupOpen .navLink, .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus , .secondaryContent a, .node .forumNodeInfo.unread .nodeIcon, .footer a:hover, .footer a:active {color: rgb(44, 63, 123);}
#logo {padding: 5px;}
.socialicons {margin-top: 15px;}
.node .tinyIcon {color: #333}
.messageContent {padding-left: 5px;padding-bottom: 5px;}
.DateTime {padding-left: 5px;}
.widget .statusPoster textarea, .widget .statusPoster textarea {margin: 5px;}
.listInline, .sidebar .section .secondaryContent .footnote, .pairsJustified {padding:5px;}
.messageText {
  font-size: 13px;
  font-family: 'Open Sans', sans-serif;
  line-height: 1.8;
}
.message .signature {opacity: 0.2;}
.message .signature:hover {opacity: 1;}

}
