css:
@-moz-document domain("cloud.feedly.com"), domain("feedly.com") {

 @font-face {
    font-family: 'iranian sans';
    font-style: normal;
    font-weight: 500;
}

* {
    font-family: 'iranian sans', "Tahoma"!important;
    text-decoration: none!important;
}

.title,
.metadata,
.entryBody {
    direction: rtl;
}

.summary .entryHolder,
.entryholder.u100frame.selectedEntry .u100frame .u100Frame .entryBody {
    padding-left: 0 !important;
    -moz-padding-start: 7px !important;
    -webkit-padding-start: 7px !important;
    direction: rtl;
    text-align: justify;
}

.title {
    text-align: right;
}                    

}
