}
  
#head_left .links_n.active {
  border-bottom: 2px solid #aaa !important;
}
  
#head_left {
  border-bottom: 1px solid #aaa !important;
}
 
#pagenavigation #jumpsbar span.active, #pagenavigation .numbers span.active {
  color: #fff !important;
}

#pagenavigation #jumpsbar span.active, .i_next, .i_prev {
  background: none !important;
}

.lemmon-slider-wrapper {
  border-top: 1px solid #aaa !important;
}
  
.settings .form-group .input-group input {
  color: black !important;
}

ul#video-lemmon-slider-ul li:hover {
  border: 1px solid #aaa !important;
}
  
#search-btn, #search-btn:hover {
  background: none repeat scroll 0% 0% #666 !important;
  color: #eee !important;
  border: none !important;
}
  
.navbar-header a div {
  background-color: #666 !important;
  border-radius: 5px !important;
}
  
.slide-wrapper .slide-menu {
    border: 1px solid #444;
    border-radius: 5px 0px 0px 5px;
    background: #222;
}
  
.slide-wrapper .slide-menu > li {
  border-bottom: 1px solid #444 !important;
}
  
/*Hidden*/

.sponsored, .head2, .midline_right, #settings, .enhanced_by_td
  {
    display: none;
  }
  
/*** Home ***/

body {
  background: none !important;
}

  #home_wrap #content {
  padding: 150px 0 0;
}
  
#search_form
.midline 
.midline_left
p,
.go-btn {
  border-radius: 0px;
}
 
  
/*** Search Pages ***/

/*Header bar*/
  
.navbar-default{
  background-image: none !important;
  height:48px !important;
}

.navbar-brand {
  padding-top: 10px;
  padding-left: 15px;
}
  
.navbar-header {
  padding: 0px;
}
  
.settings_cont.inner-pages {
  top: 9px;
}
  
#search {
  margin-left: 130px;
}

  
.search-field {
  border-radius: 0px;
  width: 650px;
  max-width: 650px;
  margin-top: -4px;
}
  
.small-go-btn {
  min-width: 60px;
  width: 80px;
  border-radius: 0px;
}
  
.text-fields {
  padding-right: 105px !important;
}

.navbar .navbar-header {
  background: none !important;
}

div#search {
  padding-top: 0px !important;
  margin-top: 7px !important;
}
  
/* Drop Down */

/*Home*/
body.air-theme div:nth-child(3).ac_results {
  width: 515px!important;
  left: 372px !important;
  margin-top: -15px!important;
  border-right: 1px solid #ccc!important;
  border-left: 1px solid #ccc!important;
  border-top: 1px solid #ccc !important;
  border-bottom: 1px solid #ccc !important;
  box-shadow: none !important;
}

