css:
@-moz-document domain("173.255.216.198") {
/* 作者:小飞侠 */

/* 背景细节颜色调整 */

body {
  background: #d0ebd0 no-repeat 50% 0;
    color:#333;
}
.bm {
  border: 1px solid #B2B266;
   background-color: #d0ebd0;
}
.bmw {
  border: 1px solid #B2B266;
}
.bmn, td.pls, .ad td.plc, div.exfm, .tb a, .tb_h, .ttp li.a a, div.uo a, input#addsubmit_btn, #gh {
  background-color: #d0ebd0;
}
.newthread tr th, .newthread tr td {
  background: #e7f7e7;
}
.tl th, .tl td {
  padding: 5px 0;
  border-bottom: 1px solid #B2B266;
}
.tl tr:hover th,.tl tr:hover td{background-color:#EEFAEE;}
.tl .th {
  margin-top: 1px;
  padding: 0 10px;
  border-bottom: 1px solid #B2B266;
  background: #EEFAEE;
}
.tl .threadpre td,.tl .threadpre:hover td{background-color:#FCFCFC;}
.tl .icn  {
  padding-left: 5px;
  width: 5px;
  text-align: left;  
}
.ttp a, .ttp strong {
  float: left;
  margin-right: 5px;
  padding: 4px 8px 3px;
  height: 18px;
  border: 1px solid #CDCDCD;
  background: #EEFAEE;
  white-space: nowrap;
}
.ad td.pls {
  background-color: #D0EBD0;
}
.pls {
  width: 160px;
  background: #E5EDF2;
  overflow: hidden;
  border-right: 1px solid #B2B266;
}
#nv_forum #ct {
  border-color: #B2B266;
}
.pi {
  border-bottom: 1px dashed #B2B266;
}
/* 分类背景 */
.cgtl caption {
  background: #EEFAEE;
  border-top: 1px solid #B2B266;
  font-size: 14px;
  font-weight: 700;
}
.cgtl td, .cgtl th, .cgtl caption {
  padding: 5px;
  border-bottom: 1px solid #B2B266;
}
/* 文本间距 */
.t_f, .t_f td {
  font-size: 16px;
line-height:21px;
}
/* 连接修改 */
a {
  color: #222;
  text-decoration: none;
}
.pi strong a {
  border: 0px solid #FFF;text-decoration:none;
}
.pg a, .pg strong, .pgb a, .pg label {
  border: 1px solid #B2B266;
  background-color: #EEFAEE;
}
#p_btn a {
background-color: #D0EBD0!important;
}
.tl th em, .tl th em a {
  color: #a3a30a;
}
.pg a:hover, .pgb a:hover {
  border-color: #B2B266;
  color: #000;
}
.ttp .a a {
  padding-right: 12px;
  padding-left: 12px;
  border-color: #cdcdcd;
  background: #EEFAEE;
  color: #222;
}
.authi a, .authi em, .authi span {
  color: #A3A30A;
}
/* 图片隐藏修改 */
.avatar img {
 max-width:70px;max-height:70px;border-radius: 50%; box-shadow: 0 0 1px #DDDDDD inset;
}
.authi img { display:none;}
.tns table,.xg2 table {display:none;}
.tl .icn img {display:none;}
.plhin .pi { height:25px!important}
.pcb h2 {display:none;}
.t_f, .t_f td {
  font-size: 14px;
}
body, input, button, select, textarea {
  font: 14px/1.6 'Microsoft Yahei',Tahoma;
  color: #222;
}
}
