css:
@-moz-document url-prefix("http://www.youtube.com/watch?"),
url-prefix("https://www.youtube.com/watch?")
{
  body
  {
  position: fixed !important;
}

 html
{
  
background: rgb(27,27,27) !important;
 overflow: hidden !important;
  
}
  .site-center-aligned #player.watch-medium,
.watch-medium .player-width,
.site-center-aligned #player.watch-large,
.watch-small .player-width {

}

.player-width {
  /* width: 854px; */
  /* width: auto !important; */

position: fixed !important;
margin-left: auto !important;
margin-right: auto !important;
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;
  background: rgba(0,0,0,1) !important;
      z-index: -1 !important;
  pointer-events:none;

}
  
  .player-height {
  height: 79.5% !important;
position: fixed !important;
margin-top: auto;
bottom: 0px !important;
  top: 0px !important;
padding-bottom: 0px !important;
  padding-top: 0px !important;
  background: rgba(0,0,0,0) !important;
      z-index: -1 !important;
-moz-transition-property: all  !important;
-moz-transition-duration: 1.3s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1.3s !important;
    
}
 
    .ytp-menu-container
{
 right: 0px  !important;
top: -188px  !important;
  z-index: 3334;
bottom: 28px !important;
-moz-transition-property: all  !important;
-moz-transition-duration: 1s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1s !important;
}


  .html5-video-controls
{
pointer-events:all !important;
bottom: -28px !important;
-moz-transition-property: all  !important;
-moz-transition-duration: 1s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1s !important;
}
  
 .html5-video-controls:hover
{

padding-bottom: 28px !important;

}
  
 .ytp-menu-container:hover
{

padding-bottom: 2px !important;

}

 .ytp-player-content, .ytp-subtitles-player-content{
         z-index: 3333
 }

 .ad-container,
  .html5-video-container{
    position: fixed !important;
    z-index: 1 !important;
  top: 0px !important;
bottom: 0px !important;
width: 100% !important;
height: 100% !important;
    pointer-events:all;
  }  
 
video.video-stream.html5-main-video,
video.video-stream.html5-main-video{
top: 0px !important;
left: 0px !important;
width: 100% !important;
height: 100% !important;
      position: fixed !important;
  
  
}

.playlist-videos-list
{
padding-bottom: 210px !important;
width: 100% !important;
}

#watch-appbar-playlist
{
  pointer-events:all;
  margin-top: 0px !important;
position: fixed !important;
top: 0px !important;
left: -6px !important;
width: 0px !important;
max-width: 450px !important;
border: 0px !important;
height: 50% !important;
padding-left: 7px !important;
  padding-bottom: 220px !important;
background: rgb(43, 43, 43) !important;
overflow: hidden !important;
-moz-transition-property: all !important;
-moz-transition-duration: 1.3s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1.3s !important;
}

#watch-appbar-playlist:hover
{
left: 0px !important;
max-width: 450px !important;
width: 99% !important;
height: 50% !important;
}

#watch7-user-header, #watch7-views-info
{
padding-top: 15px !important;
}

#watch7-content
{
  pointer-events:all;
position: fixed !important;
top: 0px !important;
right: -6px !important;
width: 0px !important;
max-width: 650px !important;
border: 0px !important;
height: 90% !important;
padding-left: 7px !important;
padding-right: 0px !important;
background: rgb(43, 43, 43) !important;
overflow: hidden !important;
-moz-transition-property: all !important;
-moz-transition-duration: 1.3s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1.3s !important;
  z-index: 999 !important;
}

#watch7-content:hover
{
background: white !important;
right: 0px !important;
max-width: 650px !important;
width: 99% !important;
padding-right: 0px !important;
overflow: scroll !important;
overflow-x: hidden !important;
height: 100% !important;
}

#masthead-search
{
position: fixed !important;
left: 8px !important;
top: -53px !important;
min-width: 210px !important;
max-width: 210px !important;
height: 27px !important;
}

#yt-masthead-container
{
background: none !important;
border: none!important;
}

#yt-masthead-user
{
position: absolute !important;
left: 88px !important;
top: -54px !important;
}

#masthead-expanded
{
position: absolute !important;
left: 283px !important;
top: -13px !important;
width: 0% !important;
}

  .html5-endscreen
  {
pointer-events:all !important;
     z-index: 5;

}
#footer-container, span.stat.attribution,
#masthead-upload-button-group, #yt-masthead-user-displayname,
#logo-container, #watch7-headline, #guide, 
#masthead-expanded-menus-container, #masthead-appbar-container, #appbar-guide-button, 
#search-btn, .watch-sidebar-section, #upload-btn
{
  
display: none !important
  
}
#body-container, #masthead-positioner { position: fixed !important;margin-left:-3383px !important; }
  #body { 
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;
    z-index: 5 !important;
  }
}
