@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photobucket.com") {
  
   body {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
  font-family:Constantia!important} 
  
  /* head */
 .desktop .navbar-inner, .navbar {background-color: #000 !important; background: linear-gradient(to bottom, #000 0%, #333 100%) !important;background: -webkit-linear-gradient(to bottom, #000 0%, #333 100%) !important}   
  
  /* button */
  .navbar .uploadContainer div.uploadButton, .navbar .printContainer div.printButton, .navbar .extraSpecialSignUpLink, .btn-special, .nav-tabs > li > a:not(.btn), .navbar .uploadContainer a.printCart .checkoutButton, .navbar .printContainer a.printCart .checkoutButton, .navbar .checkoutContainer a.printCart .checkoutButton, #albumsTarget h2.title.selected, .profilepage .profileHeader #actionButton, .profilepage .profileHeader .follow {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.85) 45%, rgba(195, 195, 195, 0.45) 100%)!important;background: -webkit-linear-gradient(to bottom , rgba(0, 0, 0, 0.85) 45%, rgba(195, 195, 195, 0.45) 100%)!important;border-radius: 20px !important;text-shadow: none!important;text-decoration:none!important}  
  
  .navbar .uploadContainer div.uploadButton:hover, .navbar .printContainer div.printButton:hover, .navbar .extraSpecialSignUpLink:hover, .btn-special:hover,  #loginBox #login-left .register-external #register-facebook-button:hover, .register-external #register-facebook-button:hover, #loginBox #login-left .register-external #register-twitter-button:hover, .register-external #register-twitter-button:hover, .btn:hover, .nav-tabs > li > a:not(.btn):hover, .paginationBar a:not(.btn):hover, .navbar .uploadContainer a.printCart .checkoutButton:hover, .navbar .printContainer a.printCart .checkoutButton:hover, .navbar .checkoutContainer a.printCart .checkoutButton:hover, .profilepage .profileHeader #actionButton:hover, .profilepage .profileHeader .follow:hover  {box-shadow: 0 0 5px #3cf!important}  
  
  #loginBox #login-left .register-external #register-facebook-button, .register-external #register-facebook-button, #loginBox #login-left .register-external #register-twitter-button, .register-external #register-twitter-button {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.85) 45%, rgba(195, 195, 195, 0.45) 100%)!important;background: -webkit-linear-gradient(to bottom , rgba(0, 0, 0, 0.85) 45%, rgba(195, 195, 195, 0.45) 100%)!important;border:none!important;border-radius: 20px !important}  
    
  /* opacity */
  .heroSlider {opacity:.6!important}  
    
  /* background & border remover */  
  .navbar.navbar .navbar-inner .nav > li > a, .navbar .divider-vertical, #loginBox #login-left .register-external #register-facebook-button .register-icon, .register-external #register-facebook-button .register-icon, #loginBox #login-left .register-external #register-facebook-button .register-text, .register-external #register-facebook-button .register-text, #loginBox #login-left .register-external #register-twitter-button, .register-external #register-twitter-button, #loginBox #login-left .register-external #register-twitter-button .register-icon, .register-external #register-twitter-button .register-icon, #loginBox #login-left .register-external #register-twitter-button .register-text, .register-external #register-twitter-button .register-text, .btn, #footer .siteLinks, .nav-tabs > li > a:not(.btn), .nav-tabs, #register .registrationWrapper .registrationContainer .registrationInner, #albumsTarget h2.title.selected, .profilepage .profileHeader #actionButton, .profilepage .profileHeader .follow {border:none!important}  
  .navbar-search .inputContainer, .signUpContainer .signUp #register-facebook-button .register-icon, #login-left>a>div>div, .categoryContent .categoryContainer .frame, .strip, .contentWrapper, #footer  {background:transparent!important}  
  .btn, .paginationBar a:not(.btn):hover, .mediawrapper, .btn-group {background-color:transparent!important} 
    
  /* searchbox */
  .navbar-search .search-query, #loginBoxPassword, #username, html input, textarea, .uneditable-input {background: rgba(0, 0, 0, 0.25)!important; color: #cef!important;box-shadow: 0px 0px 3px rgba(155, 155, 155, .9 ) inset!important;border: none !important;border-radius:15px!important}
  .navbar-search .search-query:hover, #loginBoxPassword:hover, #username:hover, html input:hover, textarea:hover, .uneditable-input:hover {box-shadow: 0px 0px 5px rgba(255, 255, 255, .9 ) inset!important}  
  
  /**/
  .navbar .subnav, #fileUploader {background: rgba(0, 0, 0, 0.6) !important;box-shadow: 0 0 34px #000 !important}  
  .topContainer {background: rgba(0, 0, 0, 0.85) !important;box-shadow: 0 0 30px #000 !important;}  
  .categories, .printPromoContainer #metalContainer, .printPromoContainer #canvasContainer, .printPromoContainer #framedContainer, .items .item .wrap {background: rgba(0, 0, 0, 0.7) !important;box-shadow: 0 0 20px #777 inset !important;}  
  #register .registrationWrapper .registrationContainer .registrationInner, select, #mediaTemplate .previous, #mediaTemplate .next   {background: rgba(0, 0, 0, 0.7) !important}   
  #explorepage .contentWrapper, #searchpage .contentWrapper {background: rgba(0, 0, 0, 0.7) !important}  
  .navbar .dropdownNav .dropdown-menu > li .storageMeterContainer, .navbar .dropdownNav .dropdown-menu > li > a.upgradeLink  {background: rgba(0, 0, 0, 0.65) !important}  
  .barMeter.progress {background: linear-gradient(to bottom , rgba(55, 55, 55, 1.0) 45%, rgba(195, 195, 195, 0.65) 100%)!important;background: -webkit-linear-gradient(to bottom , rgba(55, 55, 55, 1.0) 45%, rgba(195, 195, 195, 0.65) 100%)!important}     
  #footer, hr, .desktop select, #planselect-container, #libraryContainer, .libraryViewContainer  {border-color:#555!important}  
  .insetBox:not(.hide) .insetBoxContainer {padding: 130px 0px 80px !important}  
  .blackBackground {background: rgba(0,0,0, .05)url(http://kaputte-moderne.sweat-lodge.de/pngs/black_blurred.png) no-repeat center center!important;background-size: contain !important}  
  #mediaTemplateTarget {background: url(http://data.over-blog-kiwi.com/0/25/90/06/20140507/ob_c5ce57_loading-2.gif) no-repeat scroll 50% 50% rgba(0, 0, 0, 0.7) !important;
       border:none!important;}
    #fileUploaderWrapper .thumbnailLink:hover {background: url(http://pic2.pbsrc.com/uploader/addtoalbumicon_hover.png) no-repeat scroll 50% 30% rgba(0, 0, 0, 0.7) !important} 
  
  /* color */
  .navbar.navbar .navbar-inner .nav > li > a, .navbar .subnav .printCategories a, .navbar .subnav .printCategories p, body, h1, h2, h3, h4, h5, h6, .signUp .orText, a:hover, [class^="icon-"], [class*=" icon-"] {color:#cef!important;text-decoration:none!important;text-shadow: none!important}   
  .navbar .subnav .printCategories a:hover, .navbar .subnav .printCategories p:hover, a, .link-container .link {color:#3ce!important;text-decoration:none!important;text-shadow: none!important}   
  .profilepage .stats li, .caret, label {color:#000!important}  
  #loginBox #login-left .register-external #register-twitter-button .register-text, .register-external #register-twitter-button .register-text {text-shadow: none!important}
  
  
  /* ads */
  div.bannerAd, #slider-1161, div.mrec, #recentThumbnailList .blockItem.mrecContainer  {display:none!important} 
  div.ad {opacity:.0!important}  
  /* , div.mrec */


}
