css:
@-moz-document url-prefix(http://www.managerzone.){
  body.bg1{
	background-image: url(http://i.imgur.com/x5jWcgH.jpg);
 	background-color: #000;
  }
  .win_bg {
    background: #383838; /* Old browsers */
    background: -moz-linear-gradient(top,  #383838 0%, #0f0f0f 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#383838), color-stop(100%,#0f0f0f)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #383838 0%,#0f0f0f 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #383838 0%,#0f0f0f 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #383838 0%,#0f0f0f 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #383838 0%,#0f0f0f 100%); /* W3C */
    color: white;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
  	box-sizing: border-box;
    -webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
    border: 1px solid #666666;
    height: 28px;
  }
  .baz > div > .win_back {
    padding: 0px 0px 3px 0px !important;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
  }
  ul.mz li {
    padding: 4px;
    border-bottom: 1px solid #B5B5B5;
    background: #ffffff; /* Old browsers */
    background: -moz-linear-gradient(top,  #ffffff 0%, #e5e5e5 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#e5e5e5)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%); /* W3C */
  }
  ul.mz li a{
    font-size: 11px;
    font-weight: bold;
  }
  th.seriesHeader, tr.seriesHeader, td.subheader, h2.subheader, div.subheader {
    color: white;
    border: 1px solid #0a0a0a;
    background: #666666; /* Old browsers */
    background: -moz-linear-gradient(top,  #666666 1%, #212121 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#666666), color-stop(100%,#212121)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #666666 1%,#212121 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #666666 1%,#212121 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #666666 1%,#212121 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #666666 1%,#212121 100%); /* W3C */
  }
  #monitorDiv tr.seriesHeader, #monitorDiv td.subheader {
    background: rgb(27, 158, 24);
    border: none;
  }
  #menuDiv.menu2 ul.subnav {
    background: rgba(0,0,0,0.9);
    border-radius: 2px;
  }
  #menuDiv.menu2 ul.subnav li a, #menuDiv.menu2 ul.subnav li p, #menuDiv ul.subnav li ul.subpage_nav.scattered li a {
  	color: white;
  }
  #menuDiv.menu2 ul.subpage_nav li a {
    color: #ccc;
   	font-size: 11px;
  }
  #menuDiv.menu2 ul.subpage_nav.scattered li.selected a, #menuDiv.menu2 ul.subpage_nav li.selected a, #menuDiv.menu2 .arrow:after {
    color: black;
  }
  #menuDiv.menu2 ul.subnav.left li, #menuDiv.menu2 ul.subnav.right li {
    border: none;
  }
  #menuDiv.menu2 ul.subnav li {
    margin: 0px;
    border-right: none;
    width: 145px;
    text-overflow: ellipsis;
    border: none;
  }
  #menuDiv.menu2 ul.subnav {
    padding: 5px;
    width: 970px;
  }
  #livescores_content_close_icon {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://static.managerzone.com/nocache-319/img/mz/mz_button_down.gif) no-repeat;
    width: 18px;
    height: 19px;
    padding-left: 18px;
  }
  a.subheader, ui-widget a.subheader {
    color: white !important;
  }
  .mz_powerBoxContainer {
  	border: 2px solid black;
  }
  .mz_powerboxTop {
    background: #45484d; /* Old browsers */
    background: -moz-linear-gradient(top, #45484d 0%, #000000 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#45484d), color-stop(100%,#000000)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, #45484d 0%,#000000 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, #45484d 0%,#000000 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, #45484d 0%,#000000 100%); /* IE10+ */
    background: linear-gradient(to bottom, #45484d 0%,#000000 100%); /* W3C */
  }
  .mz_powerboxTitle {
    color: white;
  }
  a, .pseudo_a, .ui-widget-content a {
  	color: black;
	text-decoration: none;
    font-weight: bold;
  }
  a:hover {
	text-decoration: underline;
  }
  #secretaryReportContainer, #latestActivityContainer {
    font-size: 11px;
  }
  div.bar table {
    border-spacing: 0;
  }
  div[id*="monitordiv"]
  {
    padding: 4px;
    border-bottom: 1px solid #B5B5B5;
    background: #ffffff; /* Old browsers */
    background: -moz-linear-gradient(top,  #ffffff 0%, #e5e5e5 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#e5e5e5)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top,  #ffffff 0%,#e5e5e5 100%); /* IE10+ */
    background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%); /* W3C */
  }
  div[id*="monitordiv"] > table > tbody > tr:nth-child(2) {
    display: none;
  }
  div[id*="monitordiv"] > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(2) > td:nth-child(1){
    font-size: 11px;
    color: #303030;
  }
  div[id*="monitordiv"] > table > tbody > tr:nth-child(1) > td > table > tbody > tr:nth-child(3) > td {
  	font-weight: bold;
    font-size: 11px;
    color: #0B5106;
  }
  div.forum_content td[style*="text-align: center"] > b, div.baz.bazCenter td[align*="center"] > b, #searchPages > b {
  	background: black;
    color: white;
    padding: 0px 2px;
  }
  .player_skills > tbody > tr:nth-child(even){
    background: #DBDBDB;
  }
  .player_skills > tbody > tr:hover {
    background: #CDCDCD;
  }
  #mz_logo {
  	background-image: url(http://i.imgur.com/oNAWUyn.jpg);
  }
  #topDiv .bg_end {
    display: none;
  }
  #topDiv .bg {
    background: rgb(53,53,53); /* Old browsers */
    background: -moz-linear-gradient(top, rgba(53,53,53,1) 0%, rgba(0,0,0,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(53,53,53,1)), color-stop(100%,rgba(0,0,0,1))); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%); /* IE10+ */
    background: linear-gradient(to bottom, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%); /* W3C */
    width: 784px;
  	border-left: 4px solid black;
  }
  #infobar h5 {
    color: #EAEAEA;
    margin-left: 14px;
  }
  #infobar {
    color: #FFED34;
  }
  #quickSearchField.watermark {
  	text-align: left
  }
  #quickSearchField {
    background: #000000;
    border: 1px solid #414141;
    border-radius: 2px;
    font-size: 11px;
    outline: none;
  }
  #menuDiv ul.topnav li a {
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
    text-transform: uppercase;
    font-size: 11px;
  }
  #menuDiv ul.topnav .bg {
    background: rgb(124,124,124); /* Old browsers */
    background: -moz-linear-gradient(top, rgba(124,124,124,1) 0%, rgba(0,0,0,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(124,124,124,1)), color-stop(100%,rgba(0,0,0,1))); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%); /* IE10+ */
    background: linear-gradient(to bottom, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%); /* W3C */
  }
  #menuDiv ul.topnav li a.hover {
    background: rgb(186,186,186); /* Old browsers */
    background: -moz-linear-gradient(top, rgba(186,186,186,1) 0%, rgba(28,28,28,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(186,186,186,1)), color-stop(100%,rgba(28,28,28,1))); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(186,186,186,1) 0%,rgba(28,28,28,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(186,186,186,1) 0%,rgba(28,28,28,1) 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(186,186,186,1) 0%,rgba(28,28,28,1) 100%); /* IE10+ */
    background: linear-gradient(to bottom, rgba(186,186,186,1) 0%,rgba(28,28,28,1) 100%); /* W3C */
  }
  #menuDiv ul.topnav li a.selected {
    background: rgb(255,217,50); /* Old browsers */
    background: -moz-linear-gradient(top, rgba(255,217,50,1) 0%, rgba(228,200,0,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,217,50,1)), color-stop(100%,rgba(228,200,0,1))); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(255,217,50,1) 0%,rgba(228,200,0,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(255,217,50,1) 0%,rgba(228,200,0,1) 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(255,217,50,1) 0%,rgba(228,200,0,1) 100%); /* IE10+ */
    background: linear-gradient(to bottom, rgba(255,217,50,1) 0%,rgba(228,200,0,1) 100%); /* W3C */
  }
  #menuDiv.menu1 ul.subnav, #menuDiv ul.subnav li {
    background: rgb(53,53,53);
    background: -moz-linear-gradient(top, rgba(53,53,53,1) 0%, rgba(0,0,0,1) 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(53,53,53,1)), color-stop(100%,rgba(0,0,0,1)));
    background: -webkit-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%);
    background: -o-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%);
    background: -ms-linear-gradient(top, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%);
    background: linear-gradient(to bottom, rgba(53,53,53,1) 0%,rgba(0,0,0,1) 100%);
  }
  #menuDiv.menu1 ul.subnav li {
    border: none;
  }
  #menuDiv.menu1 ul.subnav li a {
    padding: 5px 8px;
    color: #D3D3D3;
  	font-size: 11px;
  }
  #menuDiv.menu1 ul.subnav li.selected a {
	background: #e4c800;
    color: black;
  }
  #menuDiv.menu1 ul.subnav li:hover { 
    background: rgb(124,124,124);
    background: -moz-linear-gradient(top, rgba(124,124,124,1) 0%, rgba(0,0,0,1) 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(124,124,124,1)), color-stop(100%,rgba(0,0,0,1)));
    background: -webkit-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%);
    background: -o-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%);
    background: -ms-linear-gradient(top, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%);
    background: linear-gradient(to bottom, rgba(124,124,124,1) 0%,rgba(0,0,0,1) 100%);
  }
  .ui-widget-content {
  	border: 1px solid #0C7B1E/*{borderColorContent}*/;
  }
  .slider .ui-widget-header {
    background: rgb(83,224,44); /* Old browsers */
    background: -moz-linear-gradient(top, rgba(83,224,44,1) 0%, rgba(3,188,0,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(83,224,44,1)), color-stop(100%,rgba(3,188,0,1))); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, rgba(83,224,44,1) 0%,rgba(3,188,0,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, rgba(83,224,44,1) 0%,rgba(3,188,0,1) 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, rgba(83,224,44,1) 0%,rgba(3,188,0,1) 100%); /* IE10+ */
    background: linear-gradient(to bottom, rgba(83,224,44,1) 0%,rgba(3,188,0,1) 100%); /* W3C */
  }
  .slider .ui-corner-all {
    border-radius: 2px;
  }
  .slider .ui-state-default, .slider .ui-widget-content .ui-state-default {
    background: rgb(36,142,28); /* Old browsers */
    background: -moz-radial-gradient(center, ellipse cover, rgba(36,142,28,1) 0%, rgba(17,73,0,1) 100%); /* FF3.6+ */
    background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(36,142,28,1)), color-stop(100%,rgba(17,73,0,1))); /* Chrome,Safari4+ */
    background: -webkit-radial-gradient(center, ellipse cover, rgba(36,142,28,1) 0%,rgba(17,73,0,1) 100%); /* Chrome10+,Safari5.1+ */
    background: -o-radial-gradient(center, ellipse cover, rgba(36,142,28,1) 0%,rgba(17,73,0,1) 100%); /* Opera 12+ */
    background: -ms-radial-gradient(center, ellipse cover, rgba(36,142,28,1) 0%,rgba(17,73,0,1) 100%); /* IE10+ */
    background: radial-gradient(ellipse at center, rgba(36,142,28,1) 0%,rgba(17,73,0,1) 100%); /* W3C */
    outline: none;
  }
  
}
