css:
@-moz-document url("https://instagram.com/") {
@media (min-width: 1226px) {  
  .timelineItem { position: relative; }
  .page-feed .main .wrapper { max-width: 1280px !important; }
  .timelineLikes { position: static !important; top: 5px; right: 0; left: 802px; }
  .timelineComments { position: absolute !important; top: 55px; right: 0; left: 802px; }
  .timelineItem.timelineFirst .timelineLikes { top: 31px; }
  .timelineItem.timelineFirst .timelineComments { top: 71px; }
  .timelineLikes{background:#000000!important;border-bottom:0px solid #ffff00!important;}
  .timelineLikeButton{background-color:#000000!important;}
  .pgmiImageLink::before, .lt-ie9 .pgmiImageLink:before,.PhotoGridMoreButton{background:rgba(76, 67, 67, 0.8)!important;}
  .timelineCaption{border-bottom-color:#000000!important;}
  .page,.Info .Story,.DesktopPPage,.Info .FeedbackBar,.top-bar-actions{background-color:#ffffff!important;border-color:#000!important;}
  .button-grey,.button-grey:visited{color:#fff!important;background:#ffffff!important;border:1px solid rgb(165,165,165);}.button-grey:hover{color:#ffffff!important;background:#ff0000!important;border:1px solid #ff0!important;}    
 .top-bar{border-bottom:8px solid #ffffff!important;}
 .top-bar{background:#000000!important;}
 .timelineCard{background:#ffffff!important;border-color:#ffffff!important;}
.timelineLikeList, .timelineBookmark{text-shadow:none!important;}  

    
}
}
