css:
@-moz-document url-prefix("http://korben.info"){
  .main {
   background-color: rgba(0, 0, 0, .5);
   color: #fff;
  } 
  
  .edito {
    background: rgba(0, 0, 0, 0.3);
  }
  .edito-footer {
    background: rgba(0,0,0,0.3);
  }
  
  .post-title a{
    color: #fff;
  }
  
  .post-title a:hover{
    color: #DEDEDE;
  }

  .main .post-footer {
    background: none;
  }
  
  .side {
    background: none;
    background-color: rgba(0, 0, 0, .3);
  }
  
  .side a {
    color: rgba(255,255,255,0.7)
  }
  
  .sep-sidebar{
    height: 0;
    background: none;
  }
  
  .side h2 {
    background: none;
    padding-bottom: 0;
  }
  
  input, textarea {
    background-color: rgba(179, 179, 179, 0.2);
    border: 1px solid rgb(102, 102, 102);
  }
  
  .search #searchsubmit {
    background: url("http://alicantepress.com/wp-content/themes/legatus-theme/images/icon-search.png") no-repeat scroll center center transparent;
  }
  
  .btn_comments, .side .univers-populaire ul.tags li a {
    background: rgba(0,0,0,0.5);
  }
  
  body, button, input, select, textarea{
    color: rgba(255,255,255,.6);
  }
  
  .wp-pagenavi a, .wp-pagenavi .current{
    /*background-image: none;
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  .wp-pagenavi .current{
    /*background: rgba(255,255,255,.1);
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  .univers {
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.3);
  }
  
  .univers a {
    color: rgba(255, 255, 255, .5);
  }
  
  a.btn-white, .post-edit-link {
  /*  background: rgba(255,255,255,.1);
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  blockquote {
   background-color: rgba(0, 0, 0, 0);
  }
  
}
