css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://alfalfalfa.com/") {

  div[class^="imobile_main_"],
  div[class^="main_adsense"],
  div[class^="fix_microad"],
  div[class^="ads_comment_"],
  div#matomeSiteHeaderArea,
  div#header_menu > div.b1 > :not(.blogtitle),
  div#header_menu > div.b2 > :not(form),
  div#header_ad,
  ul#hot_tweet,
  div.container_r,
  div.container_l > div#left,
  div#content > div.posted4,
  div#content > div.fullbody,
  div#content > div.keyword,
  div#content > div.header-rss,
  div.main > div#related-title,
  div.main > ul#mainmore,
  div#fixfooter,
  div#outfooter{
    display: none !important;
  }
  
  body,
  div.container_l,
  div#content{
    width:800px !important;
  }
  
  div#header_menu{
    margin: 0px 0px 5px 0px !important;
    padding: 0px !important;
    border: 2px solid #DBBB8C !important;
    height: 30px !important;
  }
  div#header_menu > div.b1 {margin: 5px 0px 10px 5px !important;}
  div#header_menu > div.b2 {margin: 13px 10px 0px 0px !important;}
  
  div.menu7{
     padding-top: 5px !important;
     height: 35px !important;
  }
  div.menu7 > a{font-size: 20px !important;}  
  
  div#content{
    float:left !important;
    margin: 0px auto !important;
  }
  
}


@-moz-document url-prefix("http://alfalfalfa.com/archives/") {

  div.postedS,
  div#categorytitlebody,
  div.tmk{
    display: none !important;
  }
  
}


@-moz-document url-prefix("http://alfalfalfa.com/articles/") {

  div.blogbody > :not(.article_list):not(.clearfix):not(#social_button_1):not(#main):not(#mainmore){
    display: none !important;
  }
  
}
