css:
@-moz-document url-prefix("http://blog.fefe.de"), url-prefix("https://blog.fefe.de") {
/* blog.fefe.de - Orange Gray PLUS
 * author: HoroTheWolf
 */

body {
   color: #989898;
   background-color: #0a0a0a;
   padding-left: 10px;
   padding-right: 10px;
   margin: 0 auto;
   max-width: 60em;
   position: relative;
   font-family: sans-serif;
   font-size: 130%;
}

h1, h2, h3, h4 {
   
}

h2 {
   font-size: 250%;
   margin: 0 0 0.2em 0;
   text-shadow: 1px 1px #666;
}

h3 {
	padding: 5px 3px 5px 10px;
	margin: 2.5em 15px 0.5em 15px;
	border-bottom-style: double;
	border-radius: 3px;
	background: #313131;
    border-bottom: 6px double #cc8400;
    color: #cc8400;
}


body > b, body > *[align="right"], body > *[style="text-align:right"] {
   font-weight: normal;
   font-size: 80%;
   font-style: italic;
   opacity: 0.5;
}

body > b, body > p[align="right"], body > p[style="text-align:right"] {
   right: 0;
}

body > p[align="right"], body > p[style="text-align:right"] {
   margin: 0px !important;
}

body > b {
  display: block;
  text-align: right;
  margin-top: 15px;
}

body > div[align="center"], body > div[style="text-align:center"] {
   font-weight: bold;
   margin-bottom: 2.75em;
}

p {
   margin: 1em 0;
   color: #fefefe;
}

p[u] {
  background-color: #7f5200;
  border-style: solid;
  border-radius: 8px;
  padding: 0px 10px 0px 10px;
  border-color: #4c3100;
  border-width: 2px;
}

a {
  color: #ffa500 !important;
  text-decoration: underline !important;
}

a:visited {
  color: #996300 !important;
}

h2 a:visited {
   color: #ffa500 !important;
}

a:hover, a:focus {
    text-decoration: underline !important;
    color: #ffd27f !important;
}

h2 a:hover, a:focus {
   text-decoration: underline !important;
}

body > ul {
   padding-left: 0;
}

body > ul > li {
   font-family: Georgia, "Times New Roman", Times, serif;
   font-size: 88%;
   line-height: 150%;
   color: #fefefe;
   padding: 5px 8px 8px 10px;
   margin-bottom: 0.75em;
   background-color: #1b1b1b;
   list-style-type: none;
   border-radius: 9px;
   -moz-border-radius: 9px;
   border-top: 2px solid #333;
   border-left: 2px solid #000;
   border-bottom: 2px solid #000;
   border-right: 2px solid #000;
}

body > ul > li > p:last-child {
   margin-bottom: 0;
}

body > ul > li > a:first-child {
   color: transparent !important;
   border-bottom: none;
   background: url(data:image/png;base64,) center center no-repeat;
}

li li {
   list-style-type: disc;
   margin-top: 0.33em;
   margin-bottom: 0.33em;
}

li blockquote {
   font-style: italic;
   color: #7f0000;
}
}
