css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("scenenotice.org") {

/* header logo - thanks to anagrammar @ https://userstyles.org/users/177777 */
#header{
width: 600px !important;
height:80px!important;
background:url(http://scenenotice.org/pics/header.png) no-repeat !important;
-webkit-filter:invert()hue-rotate(180deg);
filter:invert(1)hue-rotate(180deg);
}
  
#headerText {
color: #fff !important;
}
  
#menu {
margin: 0 !important;
height: auto !important;
width: auto !important;
float: none !important;
position: absolute !important;
top: 0 !important;
right: 0 !important;
}
  
/* log in area */
#log {
margin-top: 24px !important;
margin-bottom: 0 !important;
height: auto !important;
width: auto !important;
}

/* text color and font size, remove all background colors and images */
* {
color: #000 !important;
font-size: 13pt !important;
background: transparent none !important;
}

/* link color */
a:link { 
color: #0095DD !important;
}

a:hover {
border-bottom: none !important;
text-decoration: underline !important;
}

/* input field bg adjust*/
textarea,
input {
background: #fff !important;
}

/* gray backdrop */
body {
background-color: #f5f5f5 !important;
}

/* light gray */
#content {
background-color: #fafafa !important;
}

/* Remove needless spacer elements */
#lightleft,
#leftborder,
#rightborder,
#lightright {display: none !important;}

/* auto width instead of fixed width */
#hm,
#container,
#main,
#content,
#kwebnfo,
#searchFormContainer,
#paging {
width: auto !important;
float: none !important;
position: static !important;
}

/* front page notice */
#notices {
width: 40% !important;
min-width: 400px !important;
margin-bottom: 3em !important;
overflow:hidden !important;
}

/* add some whitespace */
#content {
padding: 2em !important;
margin: 2em !Important;
}

/* pagination */
#notice br {
display: none !important;
}

#notice a {
display: inline-block !important;
}

/* table listings */
#notice > table {
display: block !important;
overflow: hidden !important;
}

/* Break long title text @ details page */
#content > h1{
word-wrap: break-word !important;
}

/* Web NFO formatting */
#kwebnfo pre {
white-space: pre-wrap;
}

}
