css:
@-moz-document url("http://www.reddit.com/r/RWBY"), url("https://www.reddit.com/r/RWBY"), regexp("((https)||(http)):/(\\S+?)/r/RWBY(/.*)?$") {
  /*Main background*/
body {
    color:#000;
    background-color:#c6b2b8
}
/*All the second level content, sidebar, posts etc*/
.side h3:nth-of-type(1), .side, .linklisting .thing, .comments-page .content, .titlebox, .md ul, body .content, #header ul.tabmenu , .side .leavemoderator, .side .titlebox form.flairtoggle, .side blockquote, .sidecontentbox .content  a, .submit-page #newlink.submit.content .tabmenu.formtab a
{ background-color:#e5e5e5}
/*Post hover on main page*/
.listing-page .linklisting .thing:hover {background-color:f2f2f2;}

 /*First third etc comment background color*/
.comment,.comment .comment .comment, .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment .comment .comment,.res .comment .comment .comment,
.res .comment .comment .comment .comment .comment, .res .comment .comment .comment .comment .comment .comment .comment, .res .comment .comment .comment .comment .comment .comment .comment .comment .comment{
	background-color:#e5e5e5!important}

/* Second, fourth etc comments colour*/
.comment .comment, .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment,.comment .comment .comment .comment .comment .comment .comment .comment, .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment,
.res .comment .comment, .res .comment .comment .comment .comment, .res .comment .comment .comment .comment .comment .comment,.res .comment .comment .comment .comment .comment .comment .comment .comment, .res .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment {
	background-color:#dfd4d7!important }
}
