css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
  .sliderWidth {
    width: 80% !important;
    max-width: 80% !important;
  }
  .entryHolder, .entryholder {
	width: 100% !important;
  }
  
  .u100Entry {
  width: 98% !important;
    max-width: 98% !important;
    text-align: justify;
  }
  
  .entryBody img {
    text-align: center;
    margin: 0 auto 0 auto;
  }
  
  .slideEntryContent{
		width: 95% !important
	}
  .entryBody {
    width: 98% !important;
    max-width: 100% !important;
  }
  
  .slideBumper {
    display: none;
  }
  
  .headerInfo-title {
    color: #82BD1A;
    font-size: 20px;
    max-width: 95% !important;
  }
  
}
