css:
@-moz-document domain("qiita.com") {

/*
 * Hide
*/
.itemsShowCommentSignupOrLogin,
.career-ads-wrapper,
footer
{ display: none !important; }

 /*
 * Layout
*/
.col-sm-9 {
  width: auto;
}
.itemsShowBody_subColumn,
.itemsShowBody_subColumn>div {
  width: auto !important;
}
[class*="itemsShowBody"] {
  float: none !important;
}
.itemsShowBody {
  margin: 0 auto 100px;
}
.itemsShowAuthorInfo {
  padding: 20px;
  background-color: #f8f8f8;
}

 /*
 * Styling
*/
.markdownContent h1 {
  padding: 12px 12px 10px;
  margin: 64px -20px 16px;
  background: #e5f1dc;
  border: 0;
  border-left: 8px solid #79b74a;
}

.markdownContent h2 {
  margin: 64px -20px .7em;
  padding-left: 18px;
}
.markdownContent h3 {
  font-weight: bold;
  color: #666;
}

 /*
 * Font
*/
body, h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
  font-family: Arial, sans-serif !important;
}
.markdownContent .code-frame pre, code {
  font-family: monospace !important;
  font-size: 12px;
}
.itemsShowHeader h1 {
  font-size: 24px !important;
}

/*
 pre, code Colors
*/

.markdownContent .code-frame pre, code {
  background-color: #050505;
}
pre {
  color: #33f333;
  background-color: #050505;
  border: 3px solid #ccc;
}

code {
  color: #4aff4a;
  background: #200 !important ;
}

.markdownContent .highlight .k {
        color: #0ff;
}

.markdownContent .highlight .kd {
    color: #daf;
}

.markdownContent .highlight .kt {
  color: #0ff;
}

.markdownContent .highlight .m {
  color: #0ee;
}

.markdownContent .highlight .n {
  color: #afa;
}

.markdownContent .highlight .na {
  color: #aaf;
}
.markdownContent .highlight .nb {
  color: #eee;
}

.markdownContent .highlight .nb {
  color: #eee;
}
.markdownContent .highlight .nc {
  color: #8af;
}

.markdownContent .highlight .nf {
  color: #f77;
}

.markdownContent .highlight .no {
  color: #3cc;
}
.markdownContent .highlight .nt {
  color: #aef;
}
.markdownContent .highlight .nx {
  color: #8f8;
}

.markdownContent .highlight .p {
  color: #fff;
}

.markdownContent .highlight .s {
  color: #f88;
}

.markdownContent .highlight .s2 {
  color: #f88;
}
.markdownContent .highlight .ss {
  color: #f6b;
}


/* Table */

.markdownContent thead td, .markdownContent th {
      background-color: #fffff8;   
}

}
