css:
@-moz-document regexp("https://(gsw|gsw2)\\.view\\.usg\\.edu/d2l/login.*") {
.d2l-body {
	overflow-y : hidden;
}

div.d2l-background-global div {
	background-repeat : no-repeat;
	background-size : cover;
	/*
	background-image : url(https://stapps.gswcm.net/~simon.baev/bg.png);
    background-image : url(https://stapps.gswcm.net/~simon.baev/bg-ny.png);
	*/	
    background-image: none;
	background-color: black;
}

div.d2l-login-portal-image, div.d2l-clear, div#portalNavbar {
	display : none;
}
div.d2l-page-main {
	background : radial-gradient(rgba(0, 21, 36, 0.76) 20%, rgba(0, 0, 0, 0.31) 80%);
}
div.d2l-page-main {
	display : table;
	margin-top : 100px;
	border-radius : 20px;
	padding : 20px;
	width : 1000px;
}
div.d2l-htmlblock {		
	width : 600px;
	float : right;
	position : relative;
	margin-bottom : 0px;
	color : white;
}
div.d2l-htmlblock a, div.d2l-login-portal-login a {
	color : orange;
}
div.d2l-htmlblock a:visited, div.d2l-login-portal-login a:visited {
	color : khaki;
}

div.d2l-login-portal-login a.d2l-button {
	background : rgba(13, 148, 197, 0.34);
}

div.d2l-login-portal-login {	
	float : left;
	position : relative;
	margin-bottom : 0px;
	background : transparent;
	border-radius : 6px;
	padding-bottom : 10px;
}

label.d2l-field {
	text-shadow : none;
	color : white;
}
li.d2l-form-tracker input {
	background : transparent;
	color : white;
	border: 1px solid rgba(39, 209, 239, 0.32);
}
div.d2l-msg-container, div.d2l-messagearea-errors div.d2l-msg-container-inner, div.d2l-form-balloon-children, div.d2l-form-balloon-tip, div.d2l-form-balloon-border {    
    background-color : transparent;
}
div.d2l-messagearea-errors, div.d2l-form-balloon-border, div.d2l-form-balloon-children {
    border-color : red;	
}
div.d2l-msg-container {
	padding : 0px;
    color : white;
}
div.d2l-msg-container-inner, div.d2l-form-balloon-border {
    border : none;
}
div.d2l-form-balloon-tip {
    border-bottom-color : red;	
}
div.d2l-form-balloon-children {
    color : orange;
}
div.d2l-form-balloon, div.d2l-form-balloon * {
    display : none;
}
}
